import React, { useState, useEffect } from 'react';
import { Users, UserPlus, Shield, Eye, EyeOff, Settings, Check, X } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { Tabs } from '../../components/Tabs';

interface Step2Props {
  onComplete: () => void;
}

export const Step2AccountSetup: React.FC<Step2Props> = ({ onComplete }) => {
  const [understood, setUnderstood] = useState(false);

  useEffect(() => {
    if (understood) {
      onComplete();
    }
  }, [understood, onComplete]);

  const roleTypes = [
    {
      name: '프로젝트 관리자',
      badge: { text: '최고 권한', variant: 'danger' as const },
      icon: '👑',
      color: '#a855f7',
      backgroundColor: 'rgba(168, 85, 247, 0.1)',
      permissions: [
        '프로젝트의 전체 설정 및 관리 권한 보유',
        '구성원 초대/권한 관리, 프로젝트 환경 설정 가능',
        '최대 1명만 설정 가능 (루트 계정이 시스템 관리에서 설정)'
      ],
      description: '모든 권한을 가지고 있는 최고 관리자'
    },
    {
      name: '분석 관리자',
      badge: { text: '구축 관리', variant: 'primary' as const },
      icon: '🔧',
      color: '#3b82f6',
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      permissions: [
        '분석 관리 리소스의 구축 설계 및 관리',
        '분석 데이터 구축 설계 및 관리'
      ],
      description: '분석 관리 리소스의 구축 설계 및 관리'
    },
    {
      name: '분석가',
      badge: { text: '생성/편집', variant: 'success' as const },
      icon: '📊',
      color: '#10b981',
      backgroundColor: 'rgba(16, 185, 129, 0.1)',
      permissions: [
        '분석 리소스(리포트, 차트 등) 생성 및 편집 가능',
        '기존 데이터와 리소스 활용 중심, 구축 변경 권한 제한'
      ],
      description: '분석 리소스 생성 및 편집 중심의 역할'
    },
    {
      name: '일반 구성원',
      badge: { text: '최소 전용', variant: 'secondary' as const },
      icon: '👤',
      color: '#6b7280',
      backgroundColor: 'rgba(107, 114, 128, 0.1)',
      permissions: [
        '분석 리소스 조회 및 활용만 가능',
        '생성/수정/삭제 권한 없음'
      ],
      description: '분석 결과 조회 및 활용만 가능한 제한적 역할'
    }
  ];

  const dataPermissionSettings = [
    {
      name: '데이터 권한이란?',
      icon: '🤔',
      description: '데이터 권한은 구성원이 접근할 수 있는 데이터의 범위와 표시 방식을 세밀하게 제어하는 시스템입니다. 이벤트, 이벤트 속성, 유저 속성을 하나하나 체크해서 저장한 체크된 항목만 해당 구성원에게 표시됩니다.',
      example: '예시: 금액 관련 속성을 체크하지 않으면 일반 구성원에게는 금액 정보가 보이지 않습니다.'
    },
    {
      name: '데이터 표시 설정',
      icon: '👁️',
      description: '이벤트, 이벤트 속성, 유저 속성을 하나하나 지정하여 세밀한 권한 부여가 가능합니다.',
      examples: [
        {
          title: '이벤트 표시 설정',
          items: [
            { name: '구매완료', level: '체크됨', icon: '👁️', checked: true },
            { name: '결제실패', level: '체크안됨', icon: '🚫', checked: false },
            { name: '페이지뷰', level: '체크됨', icon: '👁️', checked: true }
          ]
        }
      ],
      note: '체크된 이벤트만 해당 구성원에게 표시됩니다.'
    },
    {
      name: '데이터 범위 설정',
      icon: '🔧',
      description: '이벤트 필터와 유저 필터를 사용하여 속성값으로 데이터를 필터링하고, 조건에 맞는 데이터만 접근할 수 있게 제한합니다.',
      examples: [
        {
          title: '이벤트 필터',
          subtitle: '상품 카테고리',
          description: '의류, 전자제품만 접근 가능',
          tags: ['의류', '전자제품']
        },
        {
          title: '유저 필터',
          subtitle: '지역',
          description: '서울, 경기 지역 사용자만',
          tags: ['서울', '경기']
        }
      ]
    },
    {
      name: '데이터 마스킹',
      icon: '🔒',
      description: '이벤트와 유저 속성에 개별적으로 마스킹을 적용하여 민감한 정보를 부분적으로 숨길 수 있습니다.',
      examples: [
        {
          title: '마스킹 적용 예시',
          items: [
            { type: '이메일 마스킹', original: 'user@example.com', masked: 'u***@e*******.com' },
            { type: '전화번호 마스킹', original: '010-1234-5678', masked: '010-****-5678' },
            { type: '금액 마스킹', original: '1,250,000원', masked: '1,***,***원' },
            { type: '신용카드 마스킹', original: '1234-5678-9012-3456', masked: '****-****-****-3456' }
          ]
        }
      ]
    },
    {
      name: '데이터 권한 생성 및 적용',
      icon: '⚙️',
      description: '위에서 설정한 데이터 표시, 범위, 마스킹 설정을 조합하여 데이터 권한을 생성하고 구성원에게 부여합니다.',
      process: [
        '체크박스로 이벤트, 이벤트 속성, 유저 속성 선택',
        '데이터 범위 필터 및 마스킹 설정',
        '데이터 권한 정책으로 저장',
        '구성원에게 데이터 권한 할당'
      ],
      note: '핵심: 체크된 항목만 해당 구성원에게 표시되고, 체크하지 않은 항목은 보이지 않습니다.'
    }
  ];

  const memberCreationTab = (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ 
          fontSize: '18px', 
          fontWeight: '600', 
          marginBottom: '16px',
          color: '#2c3e50',
          display: 'flex',
          alignItems: 'center',
          gap: '8px'
        }}>
          <UserPlus size={20} color="#3b82f6" />
          구성원 생성 단계별 가이드
        </h5>
        <p style={{ 
          fontSize: '14px', 
          color: '#34495e', 
          marginBottom: '20px',
          lineHeight: '1.5'
        }}>
          프로젝트 관리에서 새로운 구성원을 생성하고 역할을 할당하는 과정입니다.
        </p>

        <div style={{ marginBottom: '24px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '16px' }}>
            <div style={{
              width: '24px',
              height: '24px',
              background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '12px',
              fontWeight: '600',
              boxShadow: '0 2px 6px rgba(59, 130, 246, 0.3)'
            }}>
              1
            </div>
            <div>
              <h6 style={{
                fontSize: '16px',
                fontWeight: '600',
                margin: 0,
                color: '#1e293b'
              }}>
                프로젝트 관리 접근
              </h6>
              <p style={{
                fontSize: '14px',
                color: '#475569',
                margin: 0
              }}>
                오른쪽 상단의 톱니바퀴 아이콘을 클릭하여 프로젝트 관리로 이동합니다.
              </p>
            </div>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '16px' }}>
            <div style={{
              width: '24px',
              height: '24px',
              background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '12px',
              fontWeight: '600',
              boxShadow: '0 2px 6px rgba(59, 130, 246, 0.3)'
            }}>
              2
            </div>
            <div>
              <h6 style={{
                fontSize: '16px',
                fontWeight: '600',
                margin: 0,
                color: '#1e293b'
              }}>
                구성원 관리 메뉴
              </h6>
              <p style={{
                fontSize: '14px',
                color: '#475569',
                margin: 0
              }}>
                프로젝트 관리에서 구성원 관리 메뉴를 선택합니다.
              </p>
            </div>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '16px' }}>
            <div style={{
              width: '24px',
              height: '24px',
              background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '12px',
              fontWeight: '600',
              boxShadow: '0 2px 6px rgba(59, 130, 246, 0.3)'
            }}>
              3
            </div>
            <div>
              <h6 style={{
                fontSize: '16px',
                fontWeight: '600',
                margin: 0,
                color: '#1e293b'
              }}>
                새 구성원 추가
              </h6>
              <p style={{
                fontSize: '14px',
                color: '#475569',
                margin: 0
              }}>
                [+ 새 구성원] 버튼을 클릭하여 구성원 생성을 시작합니다.
              </p>
            </div>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '16px' }}>
            <div style={{
              width: '24px',
              height: '24px',
              background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '12px',
              fontWeight: '600',
              boxShadow: '0 2px 6px rgba(59, 130, 246, 0.3)'
            }}>
              4
            </div>
            <div>
              <h6 style={{
                fontSize: '16px',
                fontWeight: '600',
                margin: 0,
                color: '#1e293b'
              }}>
                계정 정보 입력
              </h6>
              <p style={{
                fontSize: '14px',
                color: '#475569',
                margin: 0
              }}>
                계정 ID(계정명)와 별칭을 입력한 후 [+ 추가] 버튼을 클릭합니다.
              </p>
            </div>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '16px' }}>
            <div style={{
              width: '24px',
              height: '24px',
              background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '12px',
              fontWeight: '600',
              boxShadow: '0 2px 6px rgba(59, 130, 246, 0.3)'
            }}>
              5
            </div>
            <div>
              <h6 style={{
                fontSize: '16px',
                fontWeight: '600',
                margin: 0,
                color: '#1e293b'
              }}>
                역할 지정
              </h6>
              <p style={{
                fontSize: '14px',
                color: '#475569',
                margin: 0
              }}>
                운영 역할, 분석 역할, 데이터 권한을 각각 선택하여 적절한 권한을 부여합니다.
              </p>
            </div>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
            <div style={{
              width: '24px',
              height: '24px',
              background: 'linear-gradient(135deg, #3b82f6 0%, #2563eb 100%)',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '12px',
              fontWeight: '600',
              boxShadow: '0 2px 6px rgba(59, 130, 246, 0.3)'
            }}>
              6
            </div>
            <div>
              <h6 style={{
                fontSize: '16px',
                fontWeight: '600',
                margin: 0,
                color: '#1e293b'
              }}>
                계정 생성 및 다운로드
              </h6>
              <p style={{
                fontSize: '14px',
                color: '#475569',
                margin: 0
              }}>
                [생성 및 다운로드] 버튼을 클릭하면 CSV 파일이 자동으로 다운로드됩니다.
              </p>
            </div>
          </div>
        </div>

        {/* CSV 파일 안내 */}
        <div style={{
          padding: '20px',
          background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)',
          borderRadius: '12px',
          marginBottom: '20px',
          border: '1px solid rgba(0, 0, 0, 0.05)',
          boxShadow: '0 2px 10px rgba(0, 0, 0, 0.05)'
        }}>
          <h6 style={{
            fontSize: '16px',
            fontWeight: '600',
            marginBottom: '12px',
            color: '#2c3e50',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            📋 CSV 파일 내용
          </h6>
          <ul style={{
            margin: 0,
            paddingLeft: '20px',
            fontSize: '14px',
            color: '#34495e'
          }}>
            <li style={{ marginBottom: '6px' }}>ID: 생성된 계정 ID</li>
            <li style={{ marginBottom: '6px' }}>별칭: 설정한 사용자 별칭</li>
            <li style={{ marginBottom: '6px' }}>초기 비밀번호: 임시로 생성된 비밀번호</li>
          </ul>
          <div style={{
            marginTop: '12px',
            padding: '12px',
            background: 'linear-gradient(135deg, #d97706 0%, #b45309 100%)',
            borderRadius: '8px',
            fontSize: '13px',
            color: 'white',
            textShadow: '0 1px 2px rgba(0, 0, 0, 0.2)',
            fontWeight: '500'
          }}>
            💡 다운로드한 CSV 파일의 정보를 해당 구성원에게 안전하게 전달하세요.
          </div>
        </div>

        {/* 여러 구성원 동시 생성 */}
        <div style={{
          padding: '20px',
          background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%)',
          borderRadius: '12px',
          marginBottom: '20px',
          boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)'
        }}>
          <h6 style={{
            fontSize: '16px',
            fontWeight: '600',
            marginBottom: '12px',
            color: '#303030',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            🔄 여러 구성원 동시 생성
          </h6>
          <p style={{
            fontSize: '14px',
            color: '#34495e',
            marginBottom: '12px',
            lineHeight: '1.5'
          }}>
            한 번에 여러 구성원을 생성하여 효율적으로 팀을 구성할 수 있습니다.
          </p>
          <div style={{
            padding: '12px',
            background: 'rgba(255, 255, 255, 0.9)',
            borderRadius: '8px',
            fontSize: '13px',
            color: '#2c3e50',
            boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
            fontWeight: '500'
          }}>
            여러 명의 구성원을 먼저 추가한 후, 마지막에 [생성 및 다운로드]를 클릭하면 모든 구성원의 정보가 포함된 CSV 파일을 한 번에 다운로드할 수 있습니다.
          </div>
          <ul style={{
            margin: '12px 0 0 0',
            paddingLeft: '20px',
            fontSize: '14px',
            color: '#34495e'
          }}>
            <li style={{ marginBottom: '4px' }}>구성원별로 개별 역할 설정 가능</li>
            <li style={{ marginBottom: '4px' }}>일괄 생성으로 시간 절약</li>
            <li style={{ marginBottom: '4px' }}>하나의 CSV 파일에 모든 구성원 정보 포함</li>
          </ul>
        </div>

        {/* 시스템 관리자 권한 */}
        <div style={{
          padding: '20px',
          background: 'linear-gradient(135deg, #334155 0%, #475569 100%)',
          borderRadius: '12px',
          boxShadow: '0 4px 15px rgba(0, 0, 0, 0.15)'
        }}>
          <h6 style={{
            fontSize: '16px',
            fontWeight: '600',
            marginBottom: '12px',
            color: 'rgba(255, 255, 255, 0.95)',
            textShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            🌐 시스템 관리자 권한
          </h6>
          <p style={{
            fontSize: '14px',
            color: 'rgba(255, 255, 255, 0.9)',
            textShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
            marginBottom: '12px',
            lineHeight: '1.5'
          }}>
            루트 계정을 가진 시스템 관리자는 시스템 전체의 구성원을 통합 관리할 수 있습니다.
          </p>
          <div style={{
            padding: '12px',
            background: 'rgba(255, 255, 255, 0.9)',
            borderRadius: '8px',
            fontSize: '13px',
            color: '#2c3e50',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
            fontWeight: '500',
            marginBottom: '12px'
          }}>
            시스템 관리에서 구성원을 생성하고 각 프로젝트에 배정할 수 있어, 여러 프로젝트에 걸친 구성원 관리가 가능합니다.
          </div>
          <ul style={{
            margin: 0,
            paddingLeft: '20px',
            fontSize: '14px',
            color: 'rgba(255, 255, 255, 0.9)',
            textShadow: '0 1px 2px rgba(0, 0, 0, 0.1)'
          }}>
            <li style={{ marginBottom: '4px' }}>전체 시스템 구성원 통합 관리</li>
            <li style={{ marginBottom: '4px' }}>프로젝트 간 구성원 이동 및 배정</li>
            <li style={{ marginBottom: '4px' }}>중앙화된 권한 관리</li>
          </ul>
        </div>
      </div>
    </div>
  );

  const roleCreationTab = (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ 
          fontSize: '16px', 
          fontWeight: '600', 
          marginBottom: '16px',
          color: 'var(--gray-900)'
        }}>
          역할 유형별 권한 비교
        </h5>
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(2, 1fr)', 
          gap: '24px'
        }}>
          {roleTypes.map((role, index) => (
            <div 
              key={index} 
              style={{
                background: '#ffffff',
