import React, { useState, useEffect } from 'react';
import { Play, Search, Menu, Sun, Moon, Settings, Bell, User, ChevronRight } from 'lucide-react';

export const HomePage: React.FC = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const [screenSize, setScreenSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    const handleResize = () => {
      setScreenSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => {
      clearInterval(timer);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navigateToQuickStart = () => {
    window.location.href = '/quickstart';
  };

  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
  };

  // 반응형 설정
  const isMobile = screenSize.width < 768;

  // 테마 색상
  const theme = {
    light: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      cardBg: 'rgba(255, 255, 255, 0.95)',
      cardBorder: 'rgba(255, 255, 255, 0.2)',
      text: '#1a202c',
      textSecondary: '#4a5568',
      headerBg: 'rgba(255, 255, 255, 0.1)',
    },
    dark: {
      background: 'linear-gradient(135deg, #0f0f0f 0%, #1a1a1a 100%)',
      cardBg: 'rgba(30, 30, 30, 0.95)',
      cardBorder: 'rgba(255, 255, 255, 0.1)',
      text: '#ffffff',
      textSecondary: '#a0aec0',
      headerBg: 'rgba(0, 0, 0, 0.3)',
    }
  };

  const currentTheme = isDarkTheme ? theme.dark : theme.light;
  const isDesktop = screenSize.width >= 1200;

  // 게임 카드들 데이터
  const gameCards = [
    {
      id: 0,
      title: "빠른 시작 가이드",
      subtitle: "데이터 분석의 기초를 배워보세요",
      image: "🚀",
      featured: true,
      category: "Quick Start",
      onClick: navigateToQuickStart
    },
    {
      id: 1,
      title: "ThinkingData란?",
      subtitle: "플랫폼 소개와 핵심 기능",
      image: "📊",
      featured: false,
      category: "Learning"
    },
    {
      id: 2,
      title: "사용자 가이드", 
      subtitle: "기본 사용법부터 고급 기능까지",
      image: "👥",
      featured: false,
      category: "Guide"
    },
    {
      id: 3,
      title: "개발자 문서",
      subtitle: "API 통합 및 SDK 사용법",
      image: "💻",
      featured: false,
      category: "Development"
    },
    {
      id: 4,
      title: "관리자 도구",
      subtitle: "고급 설정 및 관리 기능",
      image: "⚙️",
      featured: false,
      category: "Admin"
    },
    {
      id: 5,
      title: "고객 지원",
      subtitle: "기술 지원 및 문의",
      image: "💬",
      featured: false,
      category: "Support"
    }
  ];

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a1428 0%, #1a2332 50%, #2d3748 100%)',
      color: 'white',
      fontFamily: 'Segoe UI, system-ui, sans-serif',
      overflow: 'hidden'
    }}>
      {/* Xbox 스타일 상단 바 */}
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        height: '48px',
        background: 'rgba(0, 0, 0, 0.85)',
        backdropFilter: 'blur(10px)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0 20px',
        zIndex: 1000,
        borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
      }}>
        {/* 왼쪽: 사용자 정보 */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <div style={{
            width: '32px',
            height: '32px',
            borderRadius: '50%',
            background: 'linear-gradient(135deg, #0078d4, #106ebe)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            📊
          </div>
          <span style={{
            color: 'white',
            fontSize: '14px',
            fontWeight: '500'
          }}>
            ThinkingData Master
          </span>
        </div>

        {/* 가운데: 시간 */}
        <div style={{
          color: 'rgba(255, 255, 255, 0.8)',
          fontSize: '14px',
          fontWeight: '400'
        }}>
          {currentTime.toLocaleTimeString('ko-KR', { 
            hour: '2-digit', 
            minute: '2-digit' 
          })}
        </div>

        {/* 오른쪽: 아이콘들 */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <Search size={20} style={{ color: 'rgba(255, 255, 255, 0.7)', cursor: 'pointer' }} />
          <User size={20} style={{ color: 'rgba(255, 255, 255, 0.7)', cursor: 'pointer' }} />
        </div>
      </div>

      {/* 메인 컨테이너 */}
      <div style={{
        marginTop: '48px',
        padding: '20px',
        height: 'calc(100vh - 48px)',
        display: 'grid',
        gridTemplateColumns: isMobile ? '1fr' : '280px 1fr',
        gridTemplateRows: isMobile ? '1fr auto' : '1fr',
        gap: '20px'
      }}>

        {/* 왼쪽 사이드바 */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          borderRadius: '12px',
          padding: '16px',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          order: isMobile ? 2 : 1
        }}>
          <div style={{ marginBottom: '24px' }}>
            <h2 style={{
              color: 'white',
              fontSize: '16px',
              fontWeight: '600',
              margin: '0 0 16px 0',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <Home size={18} />
              내 게임 & 앱
            </h2>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
              {[
                { icon: '🚀', name: '빠른 시작 가이드', category: 'Quick Start', active: true, onClick: navigateToQuickStart },
                { icon: '📊', name: 'ThinkingData 소개', category: 'Learning' },
                { icon: '👥', name: '사용자 가이드', category: 'User Guide' },
                { icon: '💻', name: '개발자 문서', category: 'Developer' },
                { icon: '⚙️', name: '관리자 도구', category: 'Admin' }
              ].map((item, index) => (
                <div 
                  key={index}
                  onClick={item.onClick}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '12px',
                    padding: '12px',
                    borderRadius: '8px',
                    background: item.active ? 'rgba(0, 120, 212, 0.2)' : 'transparent',
                    border: item.active ? '1px solid rgba(0, 120, 212, 0.4)' : '1px solid transparent',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    if (!item.active) {
                      (e.currentTarget as HTMLElement).style.background = 'rgba(255, 255, 255, 0.05)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (!item.active) {
                      (e.currentTarget as HTMLElement).style.background = 'transparent';
                    }
                  }}
                >
                  <div style={{
                    width: '32px',
                    height: '32px',
                    borderRadius: '6px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '16px'
                  }}>
                    {item.icon}
                  </div>
                  <div style={{ flex: 1, minWidth: 0 }}>
                    <div style={{
                      color: 'white',
                      fontSize: '13px',
                      fontWeight: '500',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}>
                      {item.name}
                    </div>
                    <div style={{
                      color: 'rgba(255, 255, 255, 0.6)',
                      fontSize: '11px'
                    }}>
                      {item.category}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* 추천 및 통계 */}
          <div>
            <h3 style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '14px',
              fontWeight: '600',
              margin: '0 0 12px 0',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              최근 활동
            </h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
              {[
                { icon: '📈', text: '데이터 분석 프로젝트 생성', time: '2시간 전' },
                { icon: '🔗', text: 'API 연동 완료', time: '1일 전' },
                { icon: '👥', text: '팀원 3명 초대', time: '3일 전' }
              ].map((item, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  padding: '8px',
                  borderRadius: '6px',
                  background: 'rgba(255, 255, 255, 0.02)',
                  cursor: 'pointer'
                }}>
                  <span style={{ fontSize: '14px' }}>{item.icon}</span>
                  <div style={{ flex: 1, minWidth: 0 }}>
                    <div style={{
                      color: 'rgba(255, 255, 255, 0.9)',
                      fontSize: '12px',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}>
                      {item.text}
                    </div>
                    <div style={{
                      color: 'rgba(255, 255, 255, 0.5)',
                      fontSize: '10px'
                    }}>
                      {item.time}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* 메인 컨텐츠 영역 */}
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '20px',
          order: isMobile ? 1 : 2
        }}>

          {/* 메인 히어로 카드 (큰 카드) */}
          <div 
            onClick={navigateToQuickStart}
            style={{
              position: 'relative',
              height: isMobile ? '200px' : '300px',
              borderRadius: '16px',
              overflow: 'hidden',
              background: `
                linear-gradient(135deg, rgba(0, 120, 212, 0.8) 0%, rgba(16, 110, 190, 0.9) 100%),
                url('data:image/svg+xml,${encodeURIComponent(`
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 800">
                    <rect width="1200" height="800" fill="%23106ebe"/>
                    <circle cx="200" cy="200" r="100" fill="%23ffffff" opacity="0.1"/>
                    <circle cx="900" cy="150" r="80" fill="%23ffffff" opacity="0.08"/>
                    <circle cx="1100" cy="400" r="120" fill="%23ffffff" opacity="0.06"/>
                  </svg>
                `)}')
              `,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              cursor: 'pointer',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
              border: '2px solid rgba(255, 255, 255, 0.2)'
            }}
            onMouseEnter={(e) => {
              const element = e.currentTarget as HTMLElement;
              element.style.transform = 'scale(1.02)';
              element.style.boxShadow = '0 20px 40px rgba(0, 120, 212, 0.3)';
            }}
            onMouseLeave={(e) => {
              const element = e.currentTarget as HTMLElement;
              element.style.transform = 'scale(1)';
              element.style.boxShadow = 'none';
            }}
          >
            <div style={{
              position: 'absolute',
              inset: 0,
              background: 'linear-gradient(135deg, rgba(0, 0, 0, 0.3) 0%, rgba(0, 0, 0, 0.1) 100%)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              padding: isMobile ? '20px' : '40px'
            }}>
              <div>
                <div style={{
                  background: 'rgba(255, 255, 255, 0.2)',
                  padding: '6px 12px',
                  borderRadius: '20px',
                  fontSize: '11px',
                  fontWeight: '700',
                  color: 'white',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  marginBottom: '12px',
                  display: 'inline-block'
                }}>
                  ✨ Featured Quest
                </div>
                <h1 style={{
                  fontSize: isMobile ? '24px' : '36px',
                  fontWeight: '800',
                  color: 'white',
                  margin: '0 0 8px 0',
                  textShadow: '0 2px 12px rgba(0, 0, 0, 0.5)'
                }}>
                  빠른 시작 가이드
                </h1>
                <p style={{
                  fontSize: isMobile ? '14px' : '16px',
                  color: 'rgba(255, 255, 255, 0.9)',
                  margin: 0,
                  lineHeight: '1.5',
                  textShadow: '0 1px 8px rgba(0, 0, 0, 0.3)'
                }}>
                  4단계로 ThinkingData 마스터하기 🚀
                </p>
              </div>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '60px',
                height: '60px',
                background: 'rgba(255, 255, 255, 0.2)',
                borderRadius: '50%',
                backdropFilter: 'blur(10px)'
              }}>
                <Play size={24} style={{ color: 'white', marginLeft: '4px' }} />
              </div>
            </div>
          </div>

          {/* 게임 카드 그리드 */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: isMobile 
              ? 'repeat(2, 1fr)' 
              : isTablet 
                ? 'repeat(3, 1fr)' 
                : 'repeat(4, 1fr)',
            gap: '16px'
          }}>
            {gameCards.slice(1).map((card, index) => (
              <div
                key={card.id}
                onClick={card.onClick}
                style={{
                  position: 'relative',
                  aspectRatio: '3/4',
                  borderRadius: '12px',
                  overflow: 'hidden',
                  background: `
                    linear-gradient(135deg, rgba(255, 255, 255, 0.1) 0%, rgba(255, 255, 255, 0.05) 100%)
                  `,
                  backdropFilter: 'blur(10px)',
                  border: '1px solid rgba(255, 255, 255, 0.15)',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  flexDirection: 'column'
                }}
                onMouseEnter={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.transform = 'translateY(-8px)';
                  element.style.boxShadow = '0 16px 32px rgba(0, 0, 0, 0.3)';
                  element.style.borderColor = 'rgba(255, 255, 255, 0.3)';
                  setFocusedCard(card.id);
                }}
                onMouseLeave={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.transform = 'translateY(0)';
                  element.style.boxShadow = 'none';
                  element.style.borderColor = 'rgba(255, 255, 255, 0.15)';
                  setFocusedCard(0);
                }}
              >
                <div style={{
                  flex: 1,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '48px',
                  background: focusedCard === card.id 
                    ? 'rgba(255, 255, 255, 0.1)' 
                    : 'rgba(255, 255, 255, 0.05)',
                  transition: 'background 0.3s ease'
                }}>
                  {card.image}
                </div>
                <div style={{
                  padding: '16px',
                  background: 'rgba(0, 0, 0, 0.3)',
                  backdropFilter: 'blur(10px)'
                }}>
                  <div style={{
                    fontSize: '10px',
                    color: 'rgba(255, 255, 255, 0.7)',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px',
                    marginBottom: '4px'
                  }}>
                    {card.category}
                  </div>
                  <h3 style={{
                    fontSize: '14px',
                    fontWeight: '600',
                    color: 'white',
                    margin: '0 0 4px 0',
                    lineHeight: '1.3'
                  }}>
                    {card.title}
                  </h3>
                  <p style={{
                    fontSize: '11px',
                    color: 'rgba(255, 255, 255, 0.7)',
                    margin: 0,
                    lineHeight: '1.3',
                    overflow: 'hidden',
                    display: '-webkit-box',
                    WebkitLineClamp: 2,
                    WebkitBoxOrient: 'vertical'
                  }}>
                    {card.subtitle}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Xbox UI 글로벌 스타일 */}
      <style>{`
        * {
          scrollbar-width: thin;
          scrollbar-color: rgba(255, 255, 255, 0.3) transparent;
        }

        *::-webkit-scrollbar {
          width: 8px;
          height: 8px;
        }

        *::-webkit-scrollbar-track {
          background: transparent;
        }

        *::-webkit-scrollbar-thumb {
          background: rgba(255, 255, 255, 0.3);
          border-radius: 4px;
        }

        *::-webkit-scrollbar-thumb:hover {
          background: rgba(255, 255, 255, 0.5);
        }

        @keyframes slideIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @keyframes glow {
          0%, 100% { box-shadow: 0 0 20px rgba(0, 120, 212, 0.3); }
          50% { box-shadow: 0 0 30px rgba(0, 120, 212, 0.6); }
        }
      `}</style>
    </div>
  );
};
