import React from 'react';
import { Search, User, Star, Gamepad2 } from 'lucide-react';

interface HeaderProps {
  currentTime?: Date;
}

export const Header: React.FC<HeaderProps> = ({ 
  currentTime = new Date() 
}) => {
  // 게이밍 테마
  const theme = {
    headerBg: 'rgba(15, 15, 35, 0.8)',
    cardBorder: 'rgba(139, 92, 246, 0.3)',
    text: '#ffffff',
    textSecondary: '#a1a1aa',
    accent: '#8b5cf6',
    premium: 'linear-gradient(135deg, #f59e0b, #f97316)'
  };

  return (
    <div style={{
      position: 'sticky',
      top: 0,
      zIndex: 100,
      background: theme.headerBg,
      backdropFilter: 'blur(20px)',
      padding: '16px 24px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      borderBottom: `1px solid ${theme.cardBorder}`,
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.3)'
    }}>
      {/* 왼쪽: 로고와 제목 */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
        <div style={{
          width: '40px',
          height: '40px',
          background: theme.accent,
          borderRadius: '12px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          boxShadow: '0 4px 12px rgba(139, 92, 246, 0.4)'
        }}>
          <Gamepad2 size={24} style={{ color: 'white' }} />
        </div>
        <div>
          <h1 style={{
            fontSize: '24px',
            fontWeight: '800',
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            margin: 0,
            lineHeight: '1.2'
          }}>
            Thinking Engine
          </h1>
          <p style={{
            fontSize: '12px',
            color: theme.textSecondary,
            margin: 0,
            fontWeight: '500'
          }}>
            가이드 센터 • {currentTime.toLocaleTimeString('ko-KR', { 
              hour: '2-digit', 
              minute: '2-digit' 
            })}
          </p>
        </div>
      </div>

      {/* 오른쪽: 네비게이션과 액션 버튼들 */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
        {/* 검색 */}
        <div style={{
          display: 'flex',
          alignItems: 'center',
          background: 'rgba(139, 92, 246, 0.1)',
          border: `1px solid ${theme.cardBorder}`,
          borderRadius: '12px',
          padding: '8px 12px',
          gap: '8px',
          cursor: 'pointer',
          minWidth: '200px'
        }}>
          <Search size={16} style={{ color: theme.textSecondary }} />
          <span style={{ color: theme.textSecondary, fontSize: '14px' }}>가이드 검색...</span>
        </div>

        {/* 프리미엄 버튼 */}
        <div style={{
          background: theme.premium,
          borderRadius: '12px',
          padding: '8px 16px',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          gap: '6px',
          boxShadow: '0 4px 12px rgba(245, 158, 11, 0.3)'
        }}>
          <Star size={16} style={{ color: 'white' }} />
          <span style={{ color: 'white', fontSize: '14px', fontWeight: '600' }}>프리미엄</span>
        </div>

        {/* 사용자 아바타 */}
        <div style={{
          width: '36px',
          height: '36px',
          background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
          borderRadius: '50%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          cursor: 'pointer',
          border: `2px solid ${theme.cardBorder}`
        }}>
          <User size={20} style={{ color: 'white' }} />
        </div>
      </div>
    </div>
  );
};
