import React from 'react';
import { ChevronLeft, ChevronRight, Check } from 'lucide-react';
import { Button } from './Button';

interface Step {
  id: number;
  title: string;
  icon: React.ReactNode;
  completed: boolean;
}

interface StepNavigationProps {
  steps: Step[];
  currentStep: number;
  onStepChange: (step: number) => void;
  canGoNext: boolean;
  canGoPrev: boolean;
  onNext: () => void;
  onPrev: () => void;
}

export const StepNavigation: React.FC<StepNavigationProps> = ({
  steps,
  currentStep,
  onStepChange,
  canGoNext,
  canGoPrev,
  onNext,
  onPrev
}) => {
  const currentStepIndex = currentStep - 1;
  const completedSteps = steps.filter(step => step.completed).length;
  const progressPercentage = (completedSteps / steps.length) * 100;

  return (
    <div style={{ marginBottom: '32px' }}>
      {/* Progress Bar */}
      <div style={{ marginBottom: '24px' }}>
        <div style={{
          width: '100%',
          height: '8px',
          backgroundColor: 'var(--gray-200)',
          borderRadius: '4px',
          overflow: 'hidden'
        }}>
          <div style={{
            width: `${progressPercentage}%`,
            height: '100%',
            backgroundColor: 'var(--brand-primary)',
            transition: 'width 0.3s ease'
          }} />
        </div>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginTop: '8px',
          fontSize: '14px',
          color: 'var(--gray-600)'
        }}>
          <span>진행률</span>
          <span>{completedSteps}/{steps.length} 단계 완료</span>
        </div>
      </div>

      {/* Step Indicators */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: '32px',
        gap: '8px',
        overflowX: 'auto',
        padding: '8px 0'
      }}>
        {steps.map((step, index) => (
          <React.Fragment key={step.id}>
            <div
              onClick={() => step.completed && onStepChange(step.id)}
              style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                cursor: step.completed ? 'pointer' : 'default',
                minWidth: '120px',
                padding: '8px'
              }}
            >
              {/* Step Circle */}
              <div style={{
                width: '48px',
                height: '48px',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                marginBottom: '8px',
                backgroundColor: step.completed 
                  ? 'var(--brand-primary)' 
                  : currentStep === step.id 
                  ? 'var(--brand-primary)' 
                  : 'var(--gray-200)',
                color: step.completed || currentStep === step.id 
                  ? 'white' 
                  : 'var(--gray-500)',
                border: currentStep === step.id 
                  ? '2px solid var(--brand-primary)' 
                  : '2px solid transparent',
                transition: 'all 0.2s ease'
              }}>
                {step.completed ? <Check size={20} /> : step.icon}
              </div>

              {/* Step Title */}
              <span style={{
                fontSize: '12px',
                textAlign: 'center',
                fontWeight: currentStep === step.id ? '600' : '400',
                color: currentStep === step.id 
                  ? 'var(--brand-primary)' 
                  : step.completed 
                  ? 'var(--gray-700)' 
                  : 'var(--gray-500)',
                lineHeight: '1.2'
              }}>
                {step.title}
              </span>
            </div>

            {/* Connector Line */}
            {index < steps.length - 1 && (
              <div style={{
                flex: 1,
                height: '2px',
                backgroundColor: step.completed ? 'var(--brand-primary)' : 'var(--gray-200)',
                minWidth: '20px',
                transition: 'background-color 0.2s ease'
              }} />
            )}
          </React.Fragment>
        ))}
      </div>

      {/* Navigation Buttons */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        gap: '16px'
      }}>
        <Button
          variant="outline"
          onClick={onPrev}
          disabled={!canGoPrev}
          style={{ minWidth: '120px' }}
        >
          <ChevronLeft size={16} />
          이전 단계
        </Button>

        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '8px',
          fontSize: '14px',
          color: 'var(--gray-600)'
        }}>
          <span>{currentStep}</span>
          <span>/</span>
          <span>{steps.length}</span>
        </div>

        <Button
          variant="primary"
          onClick={onNext}
          disabled={!canGoNext}
          style={{ minWidth: '120px' }}
        >
          다음 단계
          <ChevronRight size={16} />
        </Button>
      </div>
    </div>
  );
};
