      {/* Í≤åÏù¥Î∞ç Ïä§ÌÉÄÏùº Completion Checkbox */}
      <div style={{ 
        padding: '24px', 
        background: theme.cardBg,
        borderRadius: '20px',
        border: `1px solid ${theme.cardBorder}`,
        boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
        backdropFilter: 'blur(20px)',
        position: 'relative',
        overflow: 'hidden'
      }}>
        {/* Î∞∞Í≤Ω Ïû•Ïãù */}
        <div style={{
          position: 'absolute',
          top: '-50%',
          left: '-20%',
          width: '120px',
          height: '120px',
          background: understood 
            ? 'linear-gradient(135deg, rgba(16, 185, 129, 0.2), rgba(5, 150, 105, 0.2))'
            : 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(16, 185, 129, 0.1))',
          borderRadius: '50%',
          filter: 'blur(30px)',
          transition: 'all 0.3s ease'
        }} />

        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          position: 'relative',
          zIndex: 1
        }}>
          <div style={{
            width: '48px',
            height: '48px',
            background: understood 
              ? 'linear-gradient(135deg, #10b981, #059669)'
              : 'linear-gradient(135deg, #3b82f6, #10b981)',
            borderRadius: '16px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: understood 
              ? '0 8px 20px rgba(16, 185, 129, 0.4)'
              : '0 8px 20px rgba(59, 130, 246, 0.4)',
            transition: 'all 0.3s ease'
          }}>
            {understood ? (
              <Crown size={24} color="white" />
            ) : (
              <Shield size={24} color="white" />
            )}
          </div>
          <div style={{ flex: 1 }}>
            <Checkbox
              checked={understood}
              onChange={setUnderstood}
              label={
                <span style={{ 
                  color: theme.text, 
                  fontSize: '16px', 
                  fontWeight: '600',
                  lineHeight: '1.5'
                }}>
                  {understood 
                    ? t('step2.checkboxCompleted')
                    : t('step2.checkboxReady')
                  }
                </span>
              }
            />
            {understood && (
              <div style={{
                marginTop: '12px',
                padding: '12px 16px',
                background: 'rgba(16, 185, 129, 0.1)',
                borderRadius: '12px',
                border: '1px solid rgba(16, 185, 129, 0.2)'
              }}>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  fontSize: '14px',
                  color: '#10b981',
                  fontWeight: '600'
                }}>
                  <Crown size={16} />
                  {t('step2.rewardEarned')}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

    </div>
  );
};
