          x: 200, y: 200,
          dialogue: ['ì•ˆë…•í•˜ì‹ ê°€, ì Šì€ ëª¨í—˜ê°€ì—¬!', 'ìš°ë¦¬ ë§ˆì„ì— ë„ì›€ì´ í•„ìš”í•˜ë‹¤ë„¤.'],
          quests: [
            {
              id: 'first_quest',
              title: 'ëŠ‘ëŒ€ í‡´ì¹˜',
              description: 'ë§ˆì„ ê·¼ì²˜ì˜ ëŠ‘ëŒ€ 5ë§ˆë¦¬ë¥¼ ì²˜ì¹˜í•˜ì„¸ìš”.',
              objectives: [{ type: 'kill', target: 'wolf', amount: 5, current: 0 }],
              rewards: [{ type: 'exp', amount: 100 }, { type: 'gold', amount: 50 }],
              level: 1,
              completed: false
            }
          ]
        },
        {
          id: 'weapon_shop',
          name: 'ë¬´ê¸° ìƒì¸',
          type: 'shop',
          x: 150, y: 180,
          dialogue: ['ìµœê³  í’ˆì§ˆì˜ ë¬´ê¸°ë¥¼ íŒë§¤í•©ë‹ˆë‹¤!'],
          shop: [
            {
              id: 'iron_sword',
              name: 'ì²  ê²€',
              type: 'weapon',
              rarity: 'common',
              stats: { attack: 15 },
              price: 100,
              level: 1
            },
            {
              id: 'steel_bow',
              name: 'ê°•ì²  í™œ',
              type: 'weapon',
              rarity: 'common',
              stats: { attack: 12, critChance: 10 },
              price: 120,
              level: 1
            }
          ]
        }
      ],
      monsters: []
    },
    {
      id: 'forest',
      name: 'ì–´ë‘ ì˜ ìˆ²',
      x: 600, y: 100, width: 500, height: 400,
      level: 2, type: 'combat',
      background: '#228B22',
      npcs: [],
      monsters: [
        {
          id: 'wolf_1',
          name: 'ëŠ‘ëŒ€',
          type: 'monster',
          x: 700, y: 200,
          level: 2,
          health: 60,
          maxHealth: 60
        },
        {
          id: 'wolf_2',
          name: 'ëŠ‘ëŒ€',
          type: 'monster',
          x: 800, y: 300,
          level: 2,
          health: 60,
          maxHealth: 60
        }
      ]
    },
    {
      id: 'dungeon_entrance',
      name: 'ë˜ì „ ìž…êµ¬',
      x: 400, y: 500, width: 200, height: 200,
      level: 5, type: 'dungeon',
      background: '#8B4513',
      npcs: [
        {
          id: 'dungeon_guard',
          name: 'ë˜ì „ ìˆ˜í˜¸ìž',
          type: 'guard',
          x: 500, y: 600,
          dialogue: ['ë˜ì „ì— ë“¤ì–´ê°€ë ¤ë©´ íŒŒí‹°ê°€ í•„ìš”í•©ë‹ˆë‹¤.', 'ë ˆë²¨ 5 ì´ìƒë§Œ ìž…ìž¥ ê°€ëŠ¥í•©ë‹ˆë‹¤.']
        }
      ],
      monsters: []
    },
    {
      id: 'pvp_arena',
      name: 'PvP ê²½ê¸°ìž¥',
      x: 1000, y: 500, width: 300, height: 300,
      level: 10, type: 'pvp',
      background: '#DC143C',
      npcs: [
        {
          id: 'arena_master',
          name: 'ê²½ê¸°ìž¥ ê´€ë¦¬ì¸',
          type: 'questGiver',
          x: 1150, y: 650,
          dialogue: ['ì—¬ê¸°ì„œ ë‹¤ë¥¸ í”Œë ˆì´ì–´ì™€ ëŒ€ê²°í•  ìˆ˜ ìžˆìŠµë‹ˆë‹¤!', 'PvP ëž­í‚¹ì„ ì˜¬ë ¤ë³´ì„¸ìš”!']
        }
      ],
      monsters: []
    }
  ];

  // Initialize game
  useEffect(() => {
    setGameState(prev => ({
      ...prev,
      worldMap: { ...prev.worldMap, zones: worldZones }
    }));

    // Simulate online players
    const simulateOnlinePlayers = () => {
      const playerNames = ['DragonSlayer', 'MagicMaster', 'ShadowArcher', 'HolyPriest', 'IronWarrior'];
      const classes: (keyof typeof characterClasses)[] = ['Warrior', 'Mage', 'Archer', 'Healer'];

      const players = playerNames.map((name, index) => ({
        id: `player_${index}`,
        name,
        class: classes[index % classes.length],
        level: Math.floor(Math.random() * 20) + 1,
        experience: Math.floor(Math.random() * 1000),
        health: 100,
        maxHealth: 100,
        mana: 50,
        maxMana: 50,
        x: Math.random() * 1500 + 200,
        y: Math.random() * 1000 + 200,
        gold: Math.floor(Math.random() * 1000),
        equipment: {},
        isOnline: Math.random() > 0.3,
        lastSeen: new Date().toISOString()
      }));

      setOnlinePlayers(players);
    };

    simulateOnlinePlayers();
    const interval = setInterval(simulateOnlinePlayers, 30000); // Update every 30 seconds

    return () => clearInterval(interval);
  }, []);

  // Create character
  const createCharacter = (name: string, selectedClass: keyof typeof characterClasses) => {
    const classData = characterClasses[selectedClass];
    const newCharacter: Player = {
      id: `${currentUser.user_id}_${Date.now()}`,
      name,
      class: selectedClass,
      level: 1,
      experience: 0,
      health: classData.baseStats.health,
      maxHealth: classData.baseStats.health,
      mana: classData.baseStats.mana,
      maxMana: classData.baseStats.mana,
      x: 250, // Start in village
      y: 250,
      gold: 100,
      equipment: {},
      isOnline: true,
      lastSeen: new Date().toISOString()
    };

    setGameState(prev => ({
      ...prev,
      selectedCharacter: newCharacter,
      currentScene: 'world',
      camera: { x: newCharacter.x - 300, y: newCharacter.y - 200 }
    }));

    setPlayerStats(prev => ({
      ...prev,
      character: newCharacter
    }));

    onAction('character_created', {
      player_id: currentUser.user_id,
      character_name: name,
      character_class: selectedClass,
      starting_stats: classData.baseStats
    });
  };

  // Movement
  const moveCharacter = useCallback((dx: number, dy: number) => {
    if (!gameState.selectedCharacter) return;

    const newX = Math.max(0, Math.min(gameState.worldMap.width, gameState.selectedCharacter.x + dx));
    const newY = Math.max(0, Math.min(gameState.worldMap.height, gameState.selectedCharacter.y + dy));

    setGameState(prev => ({
      ...prev,
      selectedCharacter: prev.selectedCharacter ? {
        ...prev.selectedCharacter,
        x: newX,
        y: newY
      } : null,
      camera: {
        x: newX - 300,
        y: newY - 200
      }
    }));

    // Check for zone entry
    const currentZone = worldZones.find(zone => 
      newX >= zone.x && newX <= zone.x + zone.width &&
      newY >= zone.y && newY <= zone.y + zone.height
    );

    if (currentZone) {
      onAction('zone_entered', {
        player_id: currentUser.user_id,
        zone_id: currentZone.id,
        zone_name: currentZone.name,
        zone_level: currentZone.level,
        player_level: gameState.selectedCharacter.level
      });
    }
  }, [gameState.selectedCharacter, gameState.worldMap, currentUser.user_id, onAction]);

  // Keyboard handling
  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'w', 'a', 's', 'd'].includes(e.key)) {
      e.preventDefault();
    }

    if (gameState.currentScene === 'world' && gameState.selectedCharacter) {
      const speed = 5;
      switch (e.key.toLowerCase()) {
        case 'arrowup':
        case 'w':
          moveCharacter(0, -speed);
          break;
        case 'arrowdown':
        case 's':
          moveCharacter(0, speed);
          break;
        case 'arrowleft':
        case 'a':
          moveCharacter(-speed, 0);
          break;
        case 'arrowright':
        case 'd':
          moveCharacter(speed, 0);
          break;
        case 'enter':
          // Open chat
          break;
        case 'i':
          setGameState(prev => ({ ...prev, currentScene: 'inventory' }));
          break;
        case 'escape':
          setGameState(prev => ({ ...prev, currentScene: 'world' }));
          break;
      }
    }
  }, [gameState.currentScene, gameState.selectedCharacter, moveCharacter]);

  // Chat system
  const sendChatMessage = (message: string, channel: ChatMessage['channel'] = 'global') => {
    if (!gameState.selectedCharacter) return;

    const chatMessage: ChatMessage = {
      id: `msg_${Date.now()}`,
      player: gameState.selectedCharacter.name,
      message,
      channel,
      timestamp: Date.now()
    };

    setGameState(prev => ({
      ...prev,
      chatMessages: [...prev.chatMessages.slice(-49), chatMessage] // Keep last 50 messages
    }));

    onAction('chat_message_sent', {
      player_id: currentUser.user_id,
      player_name: gameState.selectedCharacter.name,
      message,
      channel
    });
  };

  // Drawing functions
  const drawCharacterSelect = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    // Background
    ctx.fillStyle = '#2C1810';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Title
    ctx.fillStyle = '#FFD700';
    ctx.font = 'bold 32px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('ðŸŒŸ MMO RPG - ìºë¦­í„° ìƒì„±', canvas.width / 2, 80);

    // Class selection
    const classes = Object.entries(characterClasses);
    classes.forEach(([className, classData], index) => {
      const x = 50 + (index % 2) * 280;
      const y = 150 + Math.floor(index / 2) * 120;

      // Class card
      ctx.fillStyle = 'rgba(255, 255, 255, 0.1)';
      ctx.fillRect(x, y, 250, 100);
      ctx.strokeStyle = classData.color;
      ctx.lineWidth = 3;
      ctx.strokeRect(x, y, 250, 100);

      // Class info
      ctx.fillStyle = classData.color;
      ctx.font = 'bold 20px Arial';
      ctx.textAlign = 'left';
      ctx.fillText(`${classData.icon} ${className}`, x + 20, y + 30);

      ctx.fillStyle = '#FFFFFF';
      ctx.font = '12px Arial';
      ctx.fillText(classData.description, x + 20, y + 50);

      // Stats
      ctx.font = '10px Arial';
      ctx.fillText(`HP: ${classData.baseStats.health} MP: ${classData.baseStats.mana}`, x + 20, y + 65);
      ctx.fillText(`ATK: ${classData.baseStats.attack} DEF: ${classData.baseStats.defense}`, x + 20, y + 80);
    });

    // Instructions
    ctx.fillStyle = '#CCCCCC';
    ctx.font = '16px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('í´ë¦­í•´ì„œ í´ëž˜ìŠ¤ë¥¼ ì„ íƒí•˜ì„¸ìš”', canvas.width / 2, canvas.height - 30);
  };

  const drawWorld = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    if (!gameState.selectedCharacter) return;

    // Camera offset
    const offsetX = -gameState.camera.x;
    const offsetY = -gameState.camera.y;

    // Background
    ctx.fillStyle = '#87CEEB';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Draw zones
    gameState.worldMap.zones.forEach(zone => {
      const zoneX = zone.x + offsetX;
      const zoneY = zone.y + offsetY;

      // Zone background
      ctx.fillStyle = zone.background;
      ctx.fillRect(zoneX, zoneY, zone.width, zone.height);

      // Zone border
      ctx.strokeStyle = '#000000';
      ctx.lineWidth = 2;
      ctx.strokeRect(zoneX, zoneY, zone.width, zone.height);

      // Zone name
      ctx.fillStyle = '#000000';
      ctx.font = 'bold 16px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(zone.name, zoneX + zone.width / 2, zoneY + 25);
      ctx.fillText(`Lv.${zone.level}`, zoneX + zone.width / 2, zoneY + 45);

      // NPCs
      zone.npcs.forEach(npc => {
        const npcX = npc.x + offsetX;
        const npcY = npc.y + offsetY;

        // NPC icon based on type
        const npcIcons = {
          questGiver: 'â—',
          shop: 'ðŸ›’',
          guard: 'ðŸ›¡ï¸',
          monster: 'ðŸ‘¹'
        };

        ctx.fillStyle = '#FFD700';
        ctx.font = '20px Arial';
        ctx.textAlign = 'center';
        ctx.fillText(npcIcons[npc.type], npcX, npcY);

        // NPC name
        ctx.fillStyle = '#000000';
        ctx.font = '12px Arial';
        ctx.fillText(npc.name, npcX, npcY + 25);
      });

      // Monsters
      zone.monsters.forEach(monster => {
        const monsterX = monster.x + offsetX;
        const monsterY = monster.y + offsetY;

        ctx.fillStyle = '#FF0000';
        ctx.font = '16px Arial';
        ctx.textAlign = 'center';
        ctx.fillText('ðŸ‘¹', monsterX, monsterY);

        // Health bar
        if (monster.health && monster.maxHealth) {
          const healthPercent = monster.health / monster.maxHealth;
          ctx.fillStyle = '#FF0000';
          ctx.fillRect(monsterX - 15, monsterY - 25, 30, 4);
          ctx.fillStyle = '#00FF00';
          ctx.fillRect(monsterX - 15, monsterY - 25, 30 * healthPercent, 4);
        }
      });
    });

    // Draw online players
    onlinePlayers.forEach(player => {
      if (!player.isOnline || player.id === gameState.selectedCharacter?.id) return;

      const playerX = player.x + offsetX;
      const playerY = player.y + offsetY;

      // Player character
      const classData = characterClasses[player.class];
      ctx.fillStyle = classData.color;
      ctx.font = '20px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(classData.icon, playerX, playerY);

      // Player name
      ctx.fillStyle = '#FFFFFF';
      ctx.font = '12px Arial';
      ctx.fillText(player.name, playerX, playerY + 25);
      ctx.fillText(`Lv.${player.level}`, playerX, playerY + 38);
    });

    // Draw player character
    const playerX = gameState.selectedCharacter.x + offsetX;
    const playerY = gameState.selectedCharacter.y + offsetY;
    const playerClass = characterClasses[gameState.selectedCharacter.class];

    // Player glow effect
    ctx.shadowColor = playerClass.color;
    ctx.shadowBlur = 10;
    ctx.fillStyle = playerClass.color;
    ctx.font = '24px Arial';
    ctx.textAlign = 'center';
    ctx.fillText(playerClass.icon, playerX, playerY);
    ctx.shadowBlur = 0;

    // Player name and level
    ctx.fillStyle = '#FFFF00';
    ctx.font = 'bold 14px Arial';
    ctx.fillText(gameState.selectedCharacter.name, playerX, playerY + 30);
    ctx.fillText(`Lv.${gameState.selectedCharacter.level}`, playerX, playerY + 45);

    // Health and mana bars
    const barWidth = 40;
    // Health bar
    ctx.fillStyle = '#FF0000';
    ctx.fillRect(playerX - barWidth/2, playerY - 35, barWidth, 4);
    ctx.fillStyle = '#00FF00';
    const healthPercent = gameState.selectedCharacter.health / gameState.selectedCharacter.maxHealth;
    ctx.fillRect(playerX - barWidth/2, playerY - 35, barWidth * healthPercent, 4);

    // Mana bar
    ctx.fillStyle = '#000080';
    ctx.fillRect(playerX - barWidth/2, playerY - 28, barWidth, 4);
    ctx.fillStyle = '#0080FF';
    const manaPercent = gameState.selectedCharacter.mana / gameState.selectedCharacter.maxMana;
    ctx.fillRect(playerX - barWidth/2, playerY - 28, barWidth * manaPercent, 4);

    // UI Panel
    drawUI(ctx, canvas);
  };

  const drawUI = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    if (!gameState.selectedCharacter) return;

    // Bottom UI panel
    ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    ctx.fillRect(0, canvas.height - 120, canvas.width, 120);

    // Player stats
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'left';
    ctx.fillText(`${gameState.selectedCharacter.name} - Lv.${gameState.selectedCharacter.level}`, 20, canvas.height - 95);
    ctx.fillText(`HP: ${gameState.selectedCharacter.health}/${gameState.selectedCharacter.maxHealth}`, 20, canvas.height - 75);
    ctx.fillText(`MP: ${gameState.selectedCharacter.mana}/${gameState.selectedCharacter.maxMana}`, 20, canvas.height - 55);
    ctx.fillText(`Gold: ${gameState.selectedCharacter.gold}`, 20, canvas.height - 35);
    ctx.fillText(`EXP: ${gameState.selectedCharacter.experience}/${gameState.selectedCharacter.level * 100}`, 20, canvas.height - 15);

    // Online players count
    const onlineCount = onlinePlayers.filter(p => p.isOnline).length;
    ctx.fillStyle = '#00FF00';
    ctx.font = '12px Arial';
    ctx.textAlign = 'right';
    ctx.fillText(`ðŸŒ Online: ${onlineCount} players`, canvas.width - 20, canvas.height - 95);

    // Chat messages
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '12px Arial';
    ctx.textAlign = 'left';
    gameState.chatMessages.slice(-5).forEach((msg, index) => {
      const channelColors = {
        global: '#FFFFFF',
        party: '#00FF00',
        guild: '#FFD700',
        whisper: '#FF69B4'
      };

      ctx.fillStyle = channelColors[msg.channel];
      const y = canvas.height - 95 + index * 15;
      ctx.fillText(`[${msg.channel.toUpperCase()}] ${msg.player}: ${msg.message}`, 200, y);
    });

    // Controls
    ctx.fillStyle = '#CCCCCC';
    ctx.font = '10px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('WASD: Move | I: Inventory | Enter: Chat | ESC: Menu', canvas.width / 2, canvas.height - 5);
  };

  // Click handling
  const handleCanvasClick = useCallback((e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    if (gameState.currentScene === 'characterSelect') {
      // Handle class selection
      const classes = Object.keys(characterClasses);
      classes.forEach((className, index) => {
        const cardX = 50 + (index % 2) * 280;
        const cardY = 150 + Math.floor(index / 2) * 120;

        if (x >= cardX && x <= cardX + 250 && y >= cardY && y <= cardY + 100) {
          const characterName = `Hero_${Math.floor(Math.random() * 1000)}`;
          createCharacter(characterName, className as keyof typeof characterClasses);
        }
      });
    }
  }, [gameState.currentScene]);

  const draw = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    switch (gameState.currentScene) {
      case 'characterSelect':
        drawCharacterSelect(ctx, canvas);
        break;
      case 'world':
        drawWorld(ctx, canvas);
        break;
    }
  }, [gameState]);

  // Game loop
  useEffect(() => {
    const gameLoop = () => {
      draw();
      setFrameCount(prev => prev + 1);
      gameLoopRef.current = requestAnimationFrame(gameLoop);
    };

    gameLoopRef.current = requestAnimationFrame(gameLoop);

    return () => {
      if (gameLoopRef.current) {
        cancelAnimationFrame(gameLoopRef.current);
      }
    };
  }, [draw]);

  // Event listeners
  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleKeyDown]);

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center',
      background: '#0a0a0a',
      borderRadius: '12px',
      padding: '16px',
      border: '2px solid #2d3748',
      maxHeight: '500px',
      overflowY: 'auto',
      overflowX: 'auto'
    }}>
      <canvas
        ref={canvasRef}
        width={600}
        height={400}
        style={{
          border: '2px solid #4a5568',
          borderRadius: '8px',
          cursor: 'pointer',
          maxWidth: '100%',
          height: 'auto'
        }}
        onClick={handleCanvasClick}
        tabIndex={0}
      />

      <div style={{
        marginTop: '12px',
        color: '#ffffff',
        fontSize: '14px',
        textAlign: 'center',
        maxWidth: '600px',
      }}>
        <p>ðŸŒŸ <strong>MMO RPG Adventure</strong></p>
        <p>
          {gameState.currentScene === 'characterSelect' && 'í´ëž˜ìŠ¤ë¥¼ ì„ íƒí•´ì„œ ëª¨í—˜ì„ ì‹œìž‘í•˜ì„¸ìš”!'}
          {gameState.currentScene === 'world' && 'WASDë¡œ ì´ë™í•˜ë©° ë‹¤ë¥¸ í”Œë ˆì´ì–´ë“¤ê³¼ í•¨ê»˜ ëª¨í—˜í•˜ì„¸ìš”!'}
        </p>
        {gameState.selectedCharacter && (
          <p>
            {gameState.selectedCharacter.name} | Level {gameState.selectedCharacter.level} | 
            {characterClasses[gameState.selectedCharacter.class].icon} {gameState.selectedCharacter.class}
          </p>
        )}
      </div>
    </div>
  );
};
