import React, { useState, useEffect } from 'react';
import { Database, Smartphone, Gamepad2, Wrench, Globe, Star, Trophy } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { Tabs } from '../../components/Tabs';
import { Input } from '../../components/Input';
import { CodeBlock } from '../../components/CodeBlock';
import { CodeTabs } from '../../components/CodeTabs';
import { useLanguage } from '../../contexts/LanguageContext';
import { useTheme } from '../../contexts/ThemeContext';

interface Step3Props {
  onComplete: () => void;
  appData: {
    appId: string;
    dataUrl: string;
    selectedVersion: 'saas' | 'private' | null;
  };
  onAppDataChange: (data: any) => void;
}

export const Step3DataIntegration: React.FC<Step3Props> = ({
  onComplete,
  appData,
  onAppDataChange
}) => {
  const { t } = useLanguage();
  const { colors } = useTheme();
  const [understood, setUnderstood] = useState(false);
  const [hasRequiredData, setHasRequiredData] = useState(false);

  // 테마 색상 가져오기
  const theme = colors;

  useEffect(() => {
    const hasData = appData.appId.trim() !== '' && appData.dataUrl.trim() !== '';
    setHasRequiredData(hasData);

    if (understood && hasData) {
      onComplete();
    }
  }, [understood, appData, onComplete]);

  const platformTabs = [
    {
      id: 'javascript',
      label: 'JavaScript',
      icon: <Globe size={16} />,
      content: getJavaScriptCode(appData, t)
    },
    {
      id: 'ios',
      label: 'iOS',
      icon: <Smartphone size={16} />,
      content: getIOSCode(appData, t)
    },
    {
      id: 'android',
      label: 'Android',
      icon: <Smartphone size={16} />,
      content: getAndroidCode(appData, t)
    },
    {
      id: 'unity',
      label: 'Unity',
      icon: <Gamepad2 size={16} />,
      content: getUnityCode(appData, t)
    },
    {
      id: 'unreal',
      label: 'Unreal',
      icon: <Gamepad2 size={16} />,
      content: getUnrealCode(appData, t)
    },
    {
      id: 'api',
      label: 'RESTful API',
      icon: <Wrench size={16} />,
      content: getAPICode(appData, t)
    }
  ];

  const tabs = platformTabs.map(platform => ({
    id: platform.id,
    label: (
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        {platform.icon}
        {platform.label}
      </div>
    ),
    content: platform.content
  }));

  return (
    <div style={{ color: theme.text }}>
      {/* 게이밍 스타일 Step Title */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          marginBottom: '20px',
          padding: '20px',
          background: 'rgba(6, 182, 212, 0.1)',
          borderRadius: '16px',
          border: `1px solid ${theme.cardBorder}`,
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* 배경 장식 */}
          <div style={{
            position: 'absolute',
            top: '-50%',
            right: '-20%',
            width: '100px',
            height: '100px',
            background: 'linear-gradient(135deg, rgba(6, 182, 212, 0.1), rgba(8, 145, 178, 0.1))',
            borderRadius: '50%',
            filter: 'blur(30px)'
          }} />

          <div style={{
            width: '60px',
            height: '60px',
            background: 'linear-gradient(135deg, #06b6d4, #0891b2)',
            borderRadius: '18px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 8px 20px rgba(6, 182, 212, 0.4)',
            position: 'relative',
            zIndex: 1
          }}>
            <Database size={28} color="white" />
          </div>
          <div style={{ position: 'relative', zIndex: 1, flex: 1 }}>
            <h3 style={{
              fontSize: '28px',
              fontWeight: '800',
              background: 'linear-gradient(135deg, #06b6d4, #0891b2)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: 0,
              marginBottom: '8px'
            }}>
              {t('step3.mission')} 🔗
            </h3>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px'
            }}>
              <span style={{
                fontSize: '14px',
                color: theme.textSecondary,
                padding: '4px 12px',
                background: 'rgba(6, 182, 212, 0.2)',
                borderRadius: '12px',
                fontWeight: '600'
              }}>
                {t('step3.levelLabel')}
              </span>
              <span style={{
                fontSize: '14px',
                color: '#f59e0b',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}>
                <Star size={16} />
                {t('step3.rewardXP')}
              </span>
            </div>
          </div>
          {/* 단계 버튼 */}
          <div style={{
            position: 'absolute',
            top: '20px',
            right: '20px',
            zIndex: 2
          }}>
            <div style={{
              padding: '8px 16px',
              background: 'rgba(255, 255, 255, 0.9)',
              borderRadius: '20px',
              fontSize: '14px',
              fontWeight: '600',
              color: '#06b6d4',
              boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              ▷ 3/4 단계
            </div>
          </div>
        </div>
        <p style={{
          fontSize: '18px',
          color: theme.textSecondary,
          lineHeight: '1.6',
          margin: 0,
          padding: '0 20px'
        }}>
          <span dangerouslySetInnerHTML={{ __html: t('step3.goal') }} />
        </p>
      </div>

      {/* 게이밍 스타일 App Configuration */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          padding: '28px',
          background: theme.cardBg,
          border: `1px solid ${theme.cardBorder}`,
          borderRadius: '20px',
          boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
          backdropFilter: 'blur(20px)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* 배경 장식 */}
          <div style={{
            position: 'absolute',
            top: '-20%',
            right: '-10%',
            width: '150px',
            height: '150px',
            background: 'linear-gradient(135deg, rgba(6, 182, 212, 0.1), rgba(8, 145, 178, 0.1))',
            borderRadius: '50%',
            filter: 'blur(40px)'
          }} />

          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '16px',
            marginBottom: '24px',
            position: 'relative',
            zIndex: 1
          }}>
            <div style={{
              width: '48px',
              height: '48px',
              background: 'linear-gradient(135deg, #06b6d4, #0891b2)',
              borderRadius: '16px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              boxShadow: '0 8px 20px rgba(6, 182, 212, 0.4)'
            }}>
              <Wrench size={24} color="white" />
            </div>
            <h4 style={{
              fontSize: '24px',
              fontWeight: '700',
              margin: 0,
              background: 'linear-gradient(135deg, #06b6d4, #0891b2)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}>
              {t('step3.configInputTitle')}
            </h4>
          </div>

          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
            gap: '20px',
            marginBottom: '20px',
            position: 'relative',
            zIndex: 1
          }}>
            <Input
              label="App ID"
              value={appData.appId}
              onChange={(value) => onAppDataChange({ ...appData, appId: value })}
              placeholder="your-app-id"
              required
            />
            <Input
              label="Data URL"
              value={appData.dataUrl}
              onChange={(value) => onAppDataChange({ ...appData, dataUrl: value })}
              placeholder="https://your-server-url/sync_js"
              required
              type="url"
            />
          </div>

          <div style={{
            padding: '20px',
            background: 'rgba(6, 182, 212, 0.1)',
            borderRadius: '16px',
            border: '1px solid rgba(6, 182, 212, 0.2)',
            position: 'relative',
            zIndex: 1
          }}>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              marginBottom: '12px'
            }}>
              <span style={{
                fontSize: '14px',
                fontWeight: '700',
                color: theme.accent,
                padding: '6px 10px',
                background: 'rgba(6, 182, 212, 0.2)',
                borderRadius: '8px'
              }}>
                {t('step3.configInfoTitle')}
              </span>
            </div>
            <p style={{ 
              margin: 0, 
              color: theme.textSecondary, 
              fontSize: '14px', 
              lineHeight: '1.6' 
            }}>
              {t('step3.configInfoText')}
            </p>
          </div>
        </div>
      </div>

      {/* 게이밍 스타일 Platform SDK Guides */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          marginBottom: '24px'
        }}>
          <div style={{
            width: '48px',
            height: '48px',
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            borderRadius: '16px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 8px 20px rgba(139, 92, 246, 0.4)'
          }}>
            <Globe size={24} color="white" />
          </div>
          <h4 style={{
            fontSize: '24px',
            fontWeight: '700',
            margin: 0,
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            {t('step3.platformGuideTitle')}
          </h4>
        </div>

        <div style={{
          background: theme.cardBg,
          border: `1px solid ${theme.cardBorder}`,
          borderRadius: '20px',
          overflow: 'hidden',
          boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
          backdropFilter: 'blur(20px)'
        }}>
          <Tabs tabs={tabs} defaultTab="javascript" />
        </div>
      </div>

      {/* 게이밍 스타일 Additional Resources */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          marginBottom: '24px'
        }}>
          <div style={{
            width: '48px',
            height: '48px',
            background: 'linear-gradient(135deg, #f59e0b, #d97706)',
            borderRadius: '16px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 8px 20px rgba(245, 158, 11, 0.4)',
            fontSize: '20px'
          }}>
            📚
          </div>
          <h4 style={{
            fontSize: '24px',
            fontWeight: '700',
            margin: 0,
            background: 'linear-gradient(135deg, #f59e0b, #d97706)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            {t('step3.additionalResourcesTitle')}
          </h4>
        </div>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '24px',
          marginBottom: '32px'
        }}>
          {/* 게이밍 스타일 핵심 문서 */}
          <div style={{
            background: theme.cardBg,
            borderRadius: '20px',
            padding: '28px',
            border: `1px solid ${theme.cardBorder}`,
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
            backdropFilter: 'blur(20px)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            {/* 상단 그라디언트 바 */}
            <div style={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '4px',
              background: 'linear-gradient(90deg, #3b82f6, #1d4ed8)',
              borderRadius: '20px 20px 0 0'
            }} />

            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '16px',
              marginBottom: '20px'
            }}>
              <div style={{
                width: '56px',
                height: '56px',
                background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
                borderRadius: '16px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '24px',
                boxShadow: '0 6px 20px rgba(59, 130, 246, 0.25)',
                flexShrink: 0
              }}>
                📖
              </div>
              <div>
                <h5 style={{ 
                  fontSize: '18px', 
                  fontWeight: '700', 
                  margin: 0,
                  color: theme.text,
                  letterSpacing: '-0.025em'
                }}>
                  {t('step3.coreDocsTitle')}
                </h5>
                <p style={{ 
                  fontSize: '14px', 
                  color: theme.textSecondary, 
                  margin: 0,
                  marginTop: '4px',
                  fontWeight: '500'
                }}>
                  {t('step3.coreDocsSubtitle')}
                </p>
              </div>
            </div>

            <div style={{
              background: 'rgba(59, 130, 246, 0.1)',
              borderRadius: '12px',
              padding: '20px',
              border: '1px solid rgba(59, 130, 246, 0.2)'
            }}>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
                <a 
                  href="https://docs-v2.thinkingdata.kr/?version=latest&lan=ko-KR&code=installation_menu-YfSvwiWwYiX1DtkCMoTcnbOsnsb&anchorId=" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  style={{ 
                    color: '#374151', 
                    textDecoration: 'none', 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '10px',
                    fontSize: '14px',
                    fontWeight: '600',
                    padding: '8px 12px',
                    borderRadius: '8px',
                    background: '#ffffff',
                    border: '1px solid rgba(59, 130, 246, 0.2)',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = 'rgba(59, 130, 246, 0.05)';
                    element.style.transform = 'translateX(4px)';
                  }}
                  onMouseLeave={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = '#ffffff';
                    element.style.transform = 'translateX(0)';
                  }}
                >
                  <span style={{ fontSize: '16px' }}>📋</span>
                  {t('step3.fullSdkGuide')}
                </a>
                <a 
                  href="https://docs-v2.thinkingdata.kr/?version=latest&lan=ko-KR&code=client_sdk_faq&anchorId=" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  style={{ 
                    color: '#374151', 
                    textDecoration: 'none', 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '10px',
                    fontSize: '14px',
                    fontWeight: '600',
                    padding: '8px 12px',
                    borderRadius: '8px',
                    background: '#ffffff',
                    border: '1px solid rgba(59, 130, 246, 0.2)',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = 'rgba(59, 130, 246, 0.05)';
                    element.style.transform = 'translateX(4px)';
                  }}
                  onMouseLeave={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = '#ffffff';
                    element.style.transform = 'translateX(0)';
                  }}
                >
                  <span style={{ fontSize: '16px' }}>❓</span>
                  {t('step3.clientSdkFaq')}
                </a>
              </div>
