import React, { useRef, useEffect, useState, useCallback } from 'react';

interface GameProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface Tower {
  id: string;
  x: number;
  y: number;
  type: 'archer' | 'cannon' | 'magic' | 'ice';
  level: number;
  damage: number;
  range: number;
  attackSpeed: number;
  lastAttack: number;
  cost: number;
  kills: number;
}

interface Enemy {
  id: string;
  x: number;
  y: number;
  health: number;
  maxHealth: number;
  speed: number;
  type: 'goblin' | 'orc' | 'troll' | 'dragon';
  reward: number;
  pathIndex: number;
  alive: boolean;
  effects: string[];
}

interface Projectile {
  id: string;
  x: number;
  y: number;
  targetX: number;
  targetY: number;
  speed: number;
  damage: number;
  type: string;
  towerId: string;
}

interface GameState {
  wave: number;
  lives: number;
  gold: number;
  score: number;
  waveProgress: number;
  gameStatus: 'playing' | 'paused' | 'gameOver' | 'victory';
  selectedTowerType: Tower['type'] | null;
}

export const TowerDefenseGame: React.FC<GameProps> = ({
  onAction,
  playerStats,
  setPlayerStats,
  currentUser
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const gameLoopRef = useRef<number>();

  // Game path (enemy route)
  const gamePath = [
    { x: 0, y: 200 },
    { x: 150, y: 200 },
    { x: 150, y: 100 },
    { x: 300, y: 100 },
    { x: 300, y: 300 },
    { x: 450, y: 300 },
    { x: 450, y: 150 },
    { x: 600, y: 150 },
  ];

  // Game state
  const [gameState, setGameState] = useState<GameState>({
    wave: 1,
    lives: 20,
    gold: 500,
    score: 0,
    waveProgress: 0,
    gameStatus: 'playing',
    selectedTowerType: null,
  });

  const [towers, setTowers] = useState<Tower[]>([]);
  const [enemies, setEnemies] = useState<Enemy[]>([]);
  const [projectiles, setProjectiles] = useState<Projectile[]>([]);
  const [frameCount, setFrameCount] = useState(0);
  const [nextEnemySpawn, setNextEnemySpawn] = useState(0);

  // Tower types configuration
  const towerTypes = {
    archer: { cost: 100, damage: 25, range: 120, speed: 30, color: '#8B4513' },
    cannon: { cost: 200, damage: 60, range: 100, speed: 60, color: '#696969' },
    magic: { cost: 300, damage: 40, range: 140, speed: 40, color: '#9400D3' },
    ice: { cost: 250, damage: 20, range: 110, speed: 45, color: '#00BFFF' }
  };

  // Enemy types configuration
  const enemyTypes = {
    goblin: { health: 80, speed: 2, reward: 15, color: '#228B22' },
    orc: { health: 150, speed: 1.5, reward: 25, color: '#8B4513' },
    troll: { health: 300, speed: 1, reward: 40, color: '#2F4F4F' },
    dragon: { health: 500, speed: 0.8, reward: 80, color: '#DC143C' }
  };

  // Prevent default keyboard actions
  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    // Prevent default for game keys to avoid page scrolling
    if (['1', '2', '3', '4', ' ', 'Enter', 'Escape'].includes(e.key)) {
      e.preventDefault();
      e.stopPropagation();

      // Handle tower selection
      switch(e.key) {
        case '1':
          setGameState(prev => ({ ...prev, selectedTowerType: 'archer' }));
          break;
        case '2':
          setGameState(prev => ({ ...prev, selectedTowerType: 'cannon' }));
          break;
        case '3':
          setGameState(prev => ({ ...prev, selectedTowerType: 'magic' }));
          break;
        case '4':
          setGameState(prev => ({ ...prev, selectedTowerType: 'ice' }));
          break;
        case ' ':
          startNextWave();
          break;
        case 'Escape':
          setGameState(prev => ({ ...prev, selectedTowerType: null }));
          break;
      }
    }
  }, []);

  // Canvas click handler
  const handleCanvasClick = useCallback((e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas || !gameState.selectedTowerType) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Check if click is valid tower placement
    if (canPlaceTower(x, y)) {
      placeTower(x, y, gameState.selectedTowerType);
    }
  }, [gameState.selectedTowerType]);

  const canPlaceTower = (x: number, y: number): boolean => {
    // Check if too close to path
    for (const point of gamePath) {
      const distance = Math.sqrt((x - point.x) ** 2 + (y - point.y) ** 2);
      if (distance < 40) return false;
    }

    // Check if too close to existing towers
    for (const tower of towers) {
      const distance = Math.sqrt((x - tower.x) ** 2 + (y - tower.y) ** 2);
      if (distance < 50) return false;
    }

    return true;
  };

  const placeTower = (x: number, y: number, type: Tower['type']) => {
    const towerConfig = towerTypes[type];

    if (gameState.gold < towerConfig.cost) {
      onAction('tower_placement_failed', {
        reason: 'insufficient_gold',
        required_gold: towerConfig.cost,
        current_gold: gameState.gold,
        tower_type: type,
      });
      return;
    }

    const newTower: Tower = {
      id: `tower_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      x,
      y,
      type,
      level: 1,
      damage: towerConfig.damage,
      range: towerConfig.range,
      attackSpeed: towerConfig.speed,
      lastAttack: 0,
      cost: towerConfig.cost,
      kills: 0,
    };

    setTowers(prev => [...prev, newTower]);
    setGameState(prev => ({ 
      ...prev, 
      gold: prev.gold - towerConfig.cost,
      selectedTowerType: null 
    }));

    setPlayerStats((prev: any) => ({
      ...prev,
      gold: gameState.gold - towerConfig.cost,
    }));

    onAction('tower_placed', {
      tower_type: type,
      tower_id: newTower.id,
      position_x: x,
      position_y: y,
      cost: towerConfig.cost,
      remaining_gold: gameState.gold - towerConfig.cost,
      wave: gameState.wave,
    });
  };

  const startNextWave = () => {
    if (gameState.waveProgress > 0) return; // Wave already in progress

    setGameState(prev => ({ ...prev, waveProgress: 1 }));
    setNextEnemySpawn(frameCount + 60); // Start spawning in 1 second

    onAction('wave_started', {
      wave_number: gameState.wave,
      current_gold: gameState.gold,
      current_lives: gameState.lives,
      towers_count: towers.length,
    });
  };

  const spawnEnemy = () => {
    const waveTypes = ['goblin', 'orc', 'troll', 'dragon'] as Enemy['type'][];
    const typeIndex = Math.min(Math.floor(gameState.wave / 2), waveTypes.length - 1);
    const enemyType = waveTypes[Math.floor(Math.random() * (typeIndex + 1))];
    const enemyConfig = enemyTypes[enemyType];

    const newEnemy: Enemy = {
      id: `enemy_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      x: gamePath[0].x,
      y: gamePath[0].y,
      health: enemyConfig.health + (gameState.wave - 1) * 20,
      maxHealth: enemyConfig.health + (gameState.wave - 1) * 20,
      speed: enemyConfig.speed,
      type: enemyType,
      reward: enemyConfig.reward + gameState.wave * 2,
      pathIndex: 0,
      alive: true,
      effects: [],
    };

    setEnemies(prev => [...prev, newEnemy]);

    onAction('enemy_spawned', {
      enemy_type: enemyType,
      enemy_id: newEnemy.id,
      wave: gameState.wave,
      enemy_health: newEnemy.health,
      enemy_reward: newEnemy.reward,
    });
  };

  const updateEnemies = useCallback(() => {
    setEnemies(prev => prev.map(enemy => {
      if (!enemy.alive) return enemy;

      // Move along path
      const currentTarget = gamePath[enemy.pathIndex + 1];
      if (currentTarget) {
        const dx = currentTarget.x - enemy.x;
        const dy = currentTarget.y - enemy.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 5) {
          enemy.pathIndex++;
          if (enemy.pathIndex >= gamePath.length - 1) {
            // Enemy reached the end
            setGameState(prevState => ({ 
              ...prevState, 
              lives: prevState.lives - 1 
            }));

            onAction('enemy_reached_end', {
              enemy_type: enemy.type,
              enemy_id: enemy.id,
              lives_lost: 1,
              remaining_lives: gameState.lives - 1,
              wave: gameState.wave,
            });

            return { ...enemy, alive: false };
          }
        } else {
          const moveX = (dx / distance) * enemy.speed;
          const moveY = (dy / distance) * enemy.speed;
          enemy.x += moveX;
          enemy.y += moveY;
        }
      }

      return enemy;
    }).filter(enemy => enemy.alive || enemy.pathIndex < gamePath.length - 1));
  }, [gameState.lives, gameState.wave, onAction]);

  const updateTowers = useCallback(() => {
    towers.forEach(tower => {
      if (frameCount - tower.lastAttack < tower.attackSpeed) return;

      // Find target
      const enemiesInRange = enemies.filter(enemy => {
        if (!enemy.alive) return false;
        const distance = Math.sqrt((enemy.x - tower.x) ** 2 + (enemy.y - tower.y) ** 2);
        return distance <= tower.range;
      });

      if (enemiesInRange.length > 0) {
        // Target the enemy furthest along the path
        const target = enemiesInRange.reduce((best, current) => 
          current.pathIndex > best.pathIndex ? current : best
        );

        // Create projectile
        const newProjectile: Projectile = {
          id: `proj_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
          x: tower.x,
          y: tower.y,
          targetX: target.x,
          targetY: target.y,
          speed: 8,
          damage: tower.damage,
          type: tower.type,
          towerId: tower.id,
        };

        setProjectiles(prev => [...prev, newProjectile]);
        tower.lastAttack = frameCount;

        onAction('tower_attack', {
          tower_type: tower.type,
          tower_id: tower.id,
          target_enemy_id: target.id,
          target_enemy_type: target.type,
          damage: tower.damage,
          wave: gameState.wave,
        });
      }
    });
  }, [towers, enemies, frameCount, gameState.wave, onAction]);

  const updateProjectiles = useCallback(() => {
    setProjectiles(prev => prev.filter(proj => {
      const dx = proj.targetX - proj.x;
      const dy = proj.targetY - proj.y;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance < 5) {
        // Hit target - find enemy and damage
        setEnemies(prevEnemies => prevEnemies.map(enemy => {
          const enemyDistance = Math.sqrt((enemy.x - proj.targetX) ** 2 + (enemy.y - proj.targetY) ** 2);
          if (enemyDistance < 30 && enemy.alive) {
            const newHealth = Math.max(0, enemy.health - proj.damage);
            const killed = newHealth <= 0;

            if (killed) {
              // Enemy killed
              setGameState(prevState => ({ 
                ...prevState, 
                gold: prevState.gold + enemy.reward,
                score: prevState.score + enemy.reward * 10
              }));

              setPlayerStats((prev: any) => ({
                ...prev,
                gold: gameState.gold + enemy.reward,
                experience: prev.experience + enemy.reward,
              }));

              // Update tower kill count
              setTowers(prevTowers => prevTowers.map(tower => 
                tower.id === proj.towerId 
                  ? { ...tower, kills: tower.kills + 1 }
                  : tower
              ));

              onAction('enemy_defeated', {
                enemy_type: enemy.type,
                enemy_id: enemy.id,
                tower_type: proj.type,
                tower_id: proj.towerId,
                damage_dealt: proj.damage,
                gold_gained: enemy.reward,
                exp_gained: enemy.reward,
                wave: gameState.wave,
              });

              return { ...enemy, health: 0, alive: false };
            } else {
              onAction('enemy_damaged', {
                enemy_type: enemy.type,
                enemy_id: enemy.id,
                tower_type: proj.type,
                damage_dealt: proj.damage,
                enemy_health_remaining: newHealth,
                wave: gameState.wave,
              });

              return { ...enemy, health: newHealth };
            }
          }
          return enemy;
        }));

        return false; // Remove projectile
      }

      // Move projectile
      const moveX = (dx / distance) * proj.speed;
      const moveY = (dy / distance) * proj.speed;
      proj.x += moveX;
      proj.y += moveY;

      return true; // Keep projectile
    }));
  }, [gameState.gold, gameState.wave, onAction, setPlayerStats]);

  // Wave management
  useEffect(() => {
    if (gameState.waveProgress > 0 && frameCount >= nextEnemySpawn) {
      const enemiesThisWave = 5 + gameState.wave * 2;
      const spawnedCount = enemies.filter(e => e.alive).length;

      if (spawnedCount < enemiesThisWave) {
        spawnEnemy();
        setNextEnemySpawn(frameCount + 45 - Math.min(gameState.wave, 30)); // Faster spawning as waves progress
      } else if (enemies.filter(e => e.alive).length === 0) {
        // Wave complete
        setGameState(prev => ({ 
          ...prev, 
          wave: prev.wave + 1, 
          waveProgress: 0,
          gold: prev.gold + 50 + prev.wave * 10 // Wave completion bonus
        }));

        onAction('wave_completed', {
          wave_number: gameState.wave,
          bonus_gold: 50 + gameState.wave * 10,
          total_gold: gameState.gold + 50 + gameState.wave * 10,
          score: gameState.score,
          towers_count: towers.length,
        });
      }
    }
  }, [frameCount, nextEnemySpawn, gameState.waveProgress, gameState.wave, enemies, gameState.gold, gameState.score, towers.length, onAction]);

  // Game over check
  useEffect(() => {
    if (gameState.lives <= 0) {
      setGameState(prev => ({ ...prev, gameStatus: 'gameOver' }));

      onAction('game_over', {
        final_wave: gameState.wave,
        final_score: gameState.score,
        towers_built: towers.length,
        total_kills: towers.reduce((sum, tower) => sum + tower.kills, 0),
      });
    }
  }, [gameState.lives, gameState.wave, gameState.score, towers, onAction]);

  // Drawing functions
  const drawPath = (ctx: CanvasRenderingContext2D) => {
    ctx.strokeStyle = '#8B4513';
    ctx.lineWidth = 30;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';

    ctx.beginPath();
    ctx.moveTo(gamePath[0].x, gamePath[0].y);
    gamePath.slice(1).forEach(point => {
      ctx.lineTo(point.x, point.y);
    });
    ctx.stroke();

    // Path decorations
    ctx.strokeStyle = '#DEB887';
    ctx.lineWidth = 20;
    ctx.beginPath();
    ctx.moveTo(gamePath[0].x, gamePath[0].y);
    gamePath.slice(1).forEach(point => {
      ctx.lineTo(point.x, point.y);
    });
    ctx.stroke();
  };

  const drawTower = (ctx: CanvasRenderingContext2D, tower: Tower) => {
    const config = towerTypes[tower.type];

    // Tower base
    ctx.fillStyle = '#696969';
    ctx.beginPath();
    ctx.arc(tower.x, tower.y, 20, 0, 2 * Math.PI);
    ctx.fill();

    // Tower top
    ctx.fillStyle = config.color;
    ctx.beginPath();
    ctx.arc(tower.x, tower.y, 15, 0, 2 * Math.PI);
    ctx.fill();

    // Tower details based on type
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '12px Arial';
    ctx.textAlign = 'center';

    const symbols = { archer: 'üèπ', cannon: 'üí£', magic: '‚ú®', ice: '‚ùÑÔ∏è' };
    ctx.fillText(symbols[tower.type], tower.x, tower.y + 4);

    // Range indicator for selected tower type
    if (gameState.selectedTowerType === tower.type) {
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.3)';
      ctx.lineWidth = 2;
      ctx.setLineDash([5, 5]);
      ctx.beginPath();
      ctx.arc(tower.x, tower.y, tower.range, 0, 2 * Math.PI);
      ctx.stroke();
      ctx.setLineDash([]);
    }

    // Kill count
    if (tower.kills > 0) {
      ctx.fillStyle = '#FFD700';
      ctx.font = '10px Arial';
      ctx.fillText(tower.kills.toString(), tower.x, tower.y - 25);
    }
  };

  const drawEnemy = (ctx: CanvasRenderingContext2D, enemy: Enemy) => {
    if (!enemy.alive) return;

    const config = enemyTypes[enemy.type];
    const size = { goblin: 12, orc: 16, troll: 20, dragon: 24 }[enemy.type];

    // Enemy body
    ctx.fillStyle = config.color;
    ctx.beginPath();
    ctx.arc(enemy.x, enemy.y, size, 0, 2 * Math.PI);
    ctx.fill();

    // Enemy details
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'center';

    const symbols = { goblin: 'üëπ', orc: 'üó°Ô∏è', troll: 'üèîÔ∏è', dragon: 'üêâ' };
    ctx.fillText(symbols[enemy.type], enemy.x, enemy.y + 4);

    // Health bar
    const barWidth = size * 2;
    const barHeight = 4;
    const healthPercent = enemy.health / enemy.maxHealth;

    ctx.fillStyle = '#FF0000';
    ctx.fillRect(enemy.x - barWidth/2, enemy.y - size - 10, barWidth, barHeight);
    ctx.fillStyle = '#00FF00';
    ctx.fillRect(enemy.x - barWidth/2, enemy.y - size - 10, barWidth * healthPercent, barHeight);
  };

  const drawProjectile = (ctx: CanvasRenderingContext2D, projectile: Projectile) => {
    const colors = { archer: '#8B4513', cannon: '#000000', magic: '#9400D3', ice: '#00BFFF' };

    ctx.fillStyle = colors[projectile.type as keyof typeof colors];
    ctx.beginPath();
    ctx.arc(projectile.x, projectile.y, 4, 0, 2 * Math.PI);
    ctx.fill();

    // Trail effect
    ctx.fillStyle = colors[projectile.type as keyof typeof colors] + '80';
    ctx.beginPath();
    ctx.arc(projectile.x - 2, projectile.y - 2, 2, 0, 2 * Math.PI);
    ctx.fill();
  };

  const drawUI = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    // Game stats panel
    ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    ctx.fillRect(0, 0, canvas.width, 60);

    ctx.fillStyle = '#FFFFFF';
    ctx.font = '16px Arial';
    ctx.textAlign = 'left';
    ctx.fillText(`Wave: ${gameState.wave}`, 10, 25);
    ctx.fillText(`Lives: ${gameState.lives}`, 10, 45);
    ctx.fillText(`Gold: ${gameState.gold}`, 120, 25);
    ctx.fillText(`Score: ${gameState.score}`, 120, 45);

    // Tower selection panel
    ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    ctx.fillRect(0, canvas.height - 80, canvas.width, 80);

    const towerButtons = [
      { type: 'archer', x: 50, key: '1' },
      { type: 'cannon', x: 150, key: '2' },
      { type: 'magic', x: 250, key: '3' },
      { type: 'ice', x: 350, key: '4' }
    ];

    towerButtons.forEach(button => {
      const config = towerTypes[button.type as keyof typeof towerTypes];
      const isSelected = gameState.selectedTowerType === button.type;
      const canAfford = gameState.gold >= config.cost;

      // Button background
      ctx.fillStyle = isSelected ? 'rgba(255, 255, 0, 0.5)' : 
                     canAfford ? 'rgba(255, 255, 255, 0.2)' : 'rgba(255, 0, 0, 0.2)';
      ctx.fillRect(button.x - 25, canvas.height - 70, 50, 60);

      // Tower icon
      ctx.fillStyle = config.color;
      ctx.beginPath();
      ctx.arc(button.x, canvas.height - 55, 12, 0, 2 * Math.PI);
      ctx.fill();

      // Tower info
      ctx.fillStyle = canAfford ? '#FFFFFF' : '#FF6666';
      ctx.font = '12px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(`${button.key}: $${config.cost}`, button.x, canvas.height - 20);
      ctx.fillText(button.type.toUpperCase(), button.x, canvas.height - 8);
    });

    // Instructions
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'right';
    ctx.fillText('SPACE: Next Wave', canvas.width - 10, canvas.height - 45);
    ctx.fillText('ESC: Cancel', canvas.width - 10, canvas.height - 25);
    ctx.fillText('1-4: Select Tower', canvas.width - 10, canvas.height - 5);

    // Wave progress
    if (gameState.waveProgress > 0) {
      ctx.fillStyle = 'rgba(255, 255, 0, 0.8)';
      ctx.font = '20px Arial';
      ctx.textAlign = 'center';
      ctx.fillText('WAVE IN PROGRESS', canvas.width / 2, 35);
    }

    // Game over screen
    if (gameState.gameStatus === 'gameOver') {
      ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = '#FF0000';
      ctx.font = '32px Arial';
      ctx.textAlign = 'center';
      ctx.fillText('GAME OVER', canvas.width / 2, canvas.height / 2 - 50);

      ctx.fillStyle = '#FFFFFF';
      ctx.font = '18px Arial';
      ctx.fillText(`Final Wave: ${gameState.wave}`, canvas.width / 2, canvas.height / 2);
      ctx.fillText(`Final Score: ${gameState.score}`, canvas.width / 2, canvas.height / 2 + 30);
    }
  };

  const draw = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas with background
    ctx.fillStyle = '#1a4c2e';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Draw game elements
    drawPath(ctx);

    towers.forEach(tower => drawTower(ctx, tower));
    enemies.forEach(enemy => drawEnemy(ctx, enemy));
    projectiles.forEach(proj => drawProjectile(ctx, proj));

    drawUI(ctx, canvas);

    // Show placement preview
    if (gameState.selectedTowerType && canvas) {
      const rect = canvas.getBoundingClientRect();
      // This would need mouse position tracking for preview
    }
  }, [towers, enemies, projectiles, gameState]);

  // Game loop
  useEffect(() => {
    const gameLoop = () => {
      updateEnemies();
      updateTowers();
      updateProjectiles();
      draw();
      setFrameCount(prev => prev + 1);
      gameLoopRef.current = requestAnimationFrame(gameLoop);
    };

    gameLoopRef.current = requestAnimationFrame(gameLoop);

    return () => {
      if (gameLoopRef.current) {
        cancelAnimationFrame(gameLoopRef.current);
      }
    };
  }, [updateEnemies, updateTowers, updateProjectiles, draw]);

  // Event listeners
  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleKeyDown]);

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center',
      background: '#0f2419',
      borderRadius: '12px',
      padding: '16px',
      border: '2px solid #2d5016',
    }}>
      <canvas
        ref={canvasRef}
        width={600}
        height={400}
        style={{
          border: '2px solid #4a6741',
          borderRadius: '8px',
          background: '#1a4c2e',
          cursor: gameState.selectedTowerType ? 'crosshair' : 'default',
        }}
        onClick={handleCanvasClick}
        tabIndex={0}
      />

      <div style={{
