import React, { useRef, useEffect, useState, useCallback } from 'react';

interface GameProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface PlayerData {
  id: string;
  name: string;
  level: number;
  experience: number;
  gold: number;
  gems: number;
  energy: number;
  maxEnergy: number;
  unlockedStages: number;
  inventory: InventoryItem[];
  completedStages: CompletedStage[];
  achievements: string[];
  totalPlayTime: number;
  lastLogin: string;
}

interface InventoryItem {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'consumable' | 'material';
  rarity: 'common' | 'rare' | 'epic' | 'legendary';
  quantity: number;
  stats: any;
}

interface CompletedStage {
  stageId: number;
  bestScore: number;
  completionTime: number;
  starsEarned: number;
  completedAt: string;
}

interface StageData {
  id: number;
  name: string;
  difficulty: 'Easy' | 'Normal' | 'Hard' | 'Expert' | 'Hell';
  energyCost: number;
  recommendedLevel: number;
  rewards: StageReward[];
  isUnlocked: boolean;
  path: { x: number; y: number }[];
  enemies: EnemyWave[];
  background: string;
}

interface StageReward {
  type: 'gold' | 'exp' | 'item' | 'gem';
  amount: number;
  item?: InventoryItem;
}

interface EnemyWave {
  enemies: Enemy[];
  spawnDelay: number;
}

interface Enemy {
  id: string;
  x: number;
  y: number;
  health: number;
  maxHealth: number;
  speed: number;
  type: string;
  pathIndex: number;
  alive: boolean;
  armor: number;
  reward: StageReward[];
}

interface Tower {
  id: string;
  x: number;
  y: number;
  type: string;
  level: number;
  damage: number;
  range: number;
  attackSpeed: number;
  lastAttack: number;
  cost: number;
  kills: number;
}

interface GameState {
  currentScene: 'lobby' | 'dungeon' | 'stage' | 'shop' | 'inventory' | 'battle' | 'stageComplete' | 'stageFailed';
  selectedStage: number | null;
  battleState: BattleState;
  stageResults: StageResults | null;
  cameraX: number;
  cameraY: number;
  selectedTower: Tower | null;
  selectedTowerType: string | null;
}

interface BattleState {
  wave: number;
  lives: number;
  score: number;
  isWaveActive: boolean;
  towers: Tower[];
  enemies: Enemy[];
  waveProgress: number;
  battleTime: number;
}

interface StageResults {
  victory: boolean;
  score: number;
  starsEarned: number;
  timeBonus: number;
  rewards: StageReward[];
  newUnlocks: string[];
}

export const CompleteGameSystem: React.FC<GameProps> = ({
  onAction,
  playerStats,
  setPlayerStats,
  currentUser
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const gameLoopRef = useRef<number>();
  const [frameCount, setFrameCount] = useState(0);

  // Player persistent data
  const [playerData, setPlayerData] = useState<PlayerData>({
    id: currentUser.user_id,
    name: "Hero",
    level: 1,
    experience: 0,
    gold: 1000,
    gems: 50,
    energy: 100,
    maxEnergy: 100,
    unlockedStages: 1,
    inventory: [
      { id: 'sword1', name: 'Iron Sword', type: 'weapon', rarity: 'common', quantity: 1, stats: { attack: 10 } },
      { id: 'potion1', name: 'Health Potion', type: 'consumable', rarity: 'common', quantity: 5, stats: { heal: 50 } }
    ],
    completedStages: [],
    achievements: [],
    totalPlayTime: 0,
    lastLogin: new Date().toISOString()
  });

  // Game state
  const [gameState, setGameState] = useState<GameState>({
    currentScene: 'lobby',
    selectedStage: null,
    battleState: {
      wave: 1,
      lives: 20,
      score: 0,
      isWaveActive: false,
      towers: [],
      enemies: [],
      waveProgress: 0,
      battleTime: 0
    },
    stageResults: null,
    cameraX: 0,
    cameraY: 0,
    selectedTower: null,
    selectedTowerType: null
  });

  // Stage definitions
  const stageDefinitions: StageData[] = [
    {
      id: 1,
      name: "Green Fields",
      difficulty: 'Easy',
      energyCost: 10,
      recommendedLevel: 1,
      rewards: [
        { type: 'gold', amount: 100 },
        { type: 'exp', amount: 50 }
      ],
      isUnlocked: true,
      path: [
        { x: 0, y: 200 }, { x: 150, y: 200 }, { x: 150, y: 100 },
        { x: 300, y: 100 }, { x: 300, y: 300 }, { x: 600, y: 300 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 60 }
      ],
      background: '#2d5016'
    },
    {
      id: 2,
      name: "Dark Forest",
      difficulty: 'Normal',
      energyCost: 15,
      recommendedLevel: 3,
      rewards: [
        { type: 'gold', amount: 200 },
        { type: 'exp', amount: 80 },
        { type: 'gem', amount: 5 }
      ],
      isUnlocked: false,
      path: [
        { x: 0, y: 150 }, { x: 100, y: 150 }, { x: 100, y: 50 },
        { x: 250, y: 50 }, { x: 250, y: 250 }, { x: 400, y: 250 },
        { x: 400, y: 100 }, { x: 600, y: 100 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 50 }
      ],
      background: '#1a3d1a'
    },
    {
      id: 3,
      name: "Ancient Ruins",
      difficulty: 'Hard',
      energyCost: 20,
      recommendedLevel: 5,
      rewards: [
        { type: 'gold', amount: 350 },
        { type: 'exp', amount: 120 },
        { type: 'gem', amount: 10 }
      ],
      isUnlocked: false,
      path: [
        { x: 0, y: 200 }, { x: 80, y: 200 }, { x: 80, y: 80 },
        { x: 200, y: 80 }, { x: 200, y: 320 }, { x: 320, y: 320 },
        { x: 320, y: 120 }, { x: 500, y: 120 }, { x: 500, y: 280 },
        { x: 600, y: 280 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 40 }
      ],
      background: '#4a4a2a'
    },
    {
      id: 4,
      name: "Dragon's Lair",
      difficulty: 'Expert',
      energyCost: 25,
      recommendedLevel: 8,
      rewards: [
        { type: 'gold', amount: 500 },
        { type: 'exp', amount: 200 },
        { type: 'gem', amount: 20 }
      ],
      isUnlocked: false,
      path: [
        { x: 0, y: 200 }, { x: 150, y: 200 }, { x: 150, y: 100 },
        { x: 450, y: 100 }, { x: 450, y: 300 }, { x: 600, y: 300 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 35 }
      ],
      background: '#5d1a1a'
    },
    {
      id: 5,
      name: "Demon Realm",
      difficulty: 'Hell',
      energyCost: 30,
      recommendedLevel: 12,
      rewards: [
        { type: 'gold', amount: 1000 },
        { type: 'exp', amount: 400 },
        { type: 'gem', amount: 50 }
      ],
      isUnlocked: false,
      path: [
        { x: 0, y: 200 }, { x: 100, y: 200 }, { x: 200, y: 200 },
        { x: 300, y: 200 }, { x: 400, y: 200 }, { x: 500, y: 200 },
        { x: 600, y: 200 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 30 }
      ],
      background: '#2d0d0d'
    }
  ];

  // Tower types
  const towerTypes = {
    'archer': { cost: 80, damage: 25, range: 120, speed: 30, color: '#8B4513', icon: 'ðŸ¹' },
    'cannon': { cost: 150, damage: 60, range: 100, speed: 60, color: '#696969', icon: 'ðŸ’£' },
    'magic': { cost: 220, damage: 40, range: 140, speed: 40, color: '#9400D3', icon: 'âœ¨' },
    'ice': { cost: 180, damage: 20, range: 110, speed: 35, color: '#00BFFF', icon: 'â„ï¸' },
    'lightning': { cost: 300, damage: 80, range: 150, speed: 50, color: '#FFD700', icon: 'âš¡' }
  };

  // Enemy types
  const enemyTypes = {
    'goblin': { health: 80, speed: 2, reward: 15, color: '#228B22', icon: 'ðŸ‘¹', armor: 0 },
    'orc': { health: 150, speed: 1.5, reward: 25, color: '#8B4513', icon: 'ðŸ—¡ï¸', armor: 2 },
    'troll': { health: 300, speed: 1, reward: 40, color: '#2F4F4F', icon: 'ðŸ”ï¸', armor: 5 },
    'dragon': { health: 500, speed: 0.8, reward: 80, color: '#DC143C', icon: 'ðŸ‰', armor: 8 },
    'demon': { health: 800, speed: 0.6, reward: 150, color: '#8B0000', icon: 'ðŸ˜ˆ', armor: 12 }
  };

  // Initialize session
  useEffect(() => {
    onAction('session_started', {
      player_id: playerData.id,
      player_level: playerData.level,
      total_gold: playerData.gold,
      unlocked_stages: playerData.unlockedStages,
      login_streak: 1
    });
  }, []);

  // Energy regeneration
  useEffect(() => {
    const energyRegen = setInterval(() => {
      setPlayerData(prev => ({
        ...prev,
        energy: Math.min(prev.maxEnergy, prev.energy + 1)
      }));
    }, 60000); // 1 energy per minute

    return () => clearInterval(energyRegen);
  }, []);

  // Level up check
  useEffect(() => {
    const expNeeded = playerData.level * 100;
    if (playerData.experience >= expNeeded) {
      const newLevel = playerData.level + 1;
      setPlayerData(prev => ({
        ...prev,
        level: newLevel,
        experience: prev.experience - expNeeded,
        maxEnergy: prev.maxEnergy + 10
      }));

      onAction('player_level_up', {
        player_id: playerData.id,
        old_level: playerData.level,
        new_level: newLevel,
        new_max_energy: playerData.maxEnergy + 10,
        total_playtime: playerData.totalPlayTime
      });
    }
  }, [playerData.experience, playerData.level, playerData.maxEnergy, playerData.totalPlayTime, playerData.id, onAction]);

  // Keyboard handling
  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    // Prevent page scrolling
    if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'w', 'a', 's', 'd', ' ', 'Enter', 'Escape'].includes(e.key)) {
      e.preventDefault();
      e.stopPropagation();
    }

    switch (e.key.toLowerCase()) {
      case 'escape':
        if (gameState.currentScene === 'battle') {
          pauseBattle();
        } else if (gameState.currentScene !== 'lobby') {
          setGameState(prev => ({ ...prev, currentScene: 'lobby' }));
        }
        break;
      case ' ':
        if (gameState.currentScene === 'battle') {
          startWave();
        }
        break;
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
        if (gameState.currentScene === 'battle') {
          const towerIndex = parseInt(e.key) - 1;
          const towerTypeKeys = Object.keys(towerTypes);
          if (towerIndex < towerTypeKeys.length) {
            setGameState(prev => ({
              ...prev,
              selectedTowerType: towerTypeKeys[towerIndex],
              selectedTower: null
            }));
          }
        }
        break;
    }
  }, [gameState.currentScene]);

  // Mouse/Click handling
  const handleCanvasClick = useCallback((e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    switch (gameState.currentScene) {
      case 'lobby':
        handleLobbyClick(x, y);
        break;
      case 'dungeon':
        handleDungeonClick(x, y);
        break;
      case 'battle':
        handleBattleClick(x, y);
        break;
      case 'stageComplete':
      case 'stageFailed':
        handleResultsClick(x, y);
        break;
    }
  }, [gameState.currentScene]);

  const handleLobbyClick = (x: number, y: number) => {
    // Dungeon button
    if (x >= 250 && x <= 350 && y >= 200 && y <= 250) {
      setGameState(prev => ({ ...prev, currentScene: 'dungeon' }));

      onAction('menu_navigation', {
        player_id: playerData.id,
        from_scene: 'lobby',
        to_scene: 'dungeon',
        player_level: playerData.level
      });
    }

    // Shop button
    if (x >= 100 && x <= 200 && y >= 300 && y <= 350) {
      setGameState(prev => ({ ...prev, currentScene: 'shop' }));

      onAction('menu_navigation', {
        player_id: playerData.id,
        from_scene: 'lobby',
        to_scene: 'shop',
        player_level: playerData.level
      });
    }

    // Inventory button
    if (x >= 400 && x <= 500 && y >= 300 && y <= 350) {
      setGameState(prev => ({ ...prev, currentScene: 'inventory' }));

      onAction('menu_navigation', {
        player_id: playerData.id,
        from_scene: 'lobby',
        to_scene: 'inventory',
        player_level: playerData.level
      });
    }
  };

  const handleDungeonClick = (x: number, y: number) => {
    // Stage selection
    stageDefinitions.forEach((stage, index) => {
      const stageX = 50 + (index % 3) * 200;
      const stageY = 100 + Math.floor(index / 3) * 150;

      if (x >= stageX && x <= stageX + 150 && y >= stageY && y <= stageY + 100) {
        if (stage.id <= playerData.unlockedStages && playerData.energy >= stage.energyCost) {
          enterStage(stage.id);
        }
      }
    });

    // Back button
    if (x >= 50 && x <= 150 && y >= 350 && y <= 380) {
      setGameState(prev => ({ ...prev, currentScene: 'lobby' }));
    }
  };

  const enterStage = (stageId: number) => {
    const stage = stageDefinitions.find(s => s.id === stageId);
    if (!stage) return;

    // Consume energy
    setPlayerData(prev => ({ ...prev, energy: prev.energy - stage.energyCost }));

    // Initialize battle
    setGameState(prev => ({
      ...prev,
      currentScene: 'battle',
      selectedStage: stageId,
      battleState: {
        wave: 1,
        lives: 20,
        score: 0,
        isWaveActive: false,
        towers: [],
        enemies: [],
        waveProgress: 0,
        battleTime: 0
      }
    }));

    onAction('stage_entered', {
      player_id: playerData.id,
      stage_id: stageId,
      stage_name: stage.name,
      energy_cost: stage.energyCost,
      remaining_energy: playerData.energy - stage.energyCost,
      player_level: playerData.level,
      recommended_level: stage.recommendedLevel
    });
  };

  const handleBattleClick = (x: number, y: number) => {
    // UI area (bottom 80px)
    if (y > 320) return;

    if (gameState.selectedTowerType) {
      // Place tower
      if (canPlaceTower(x, y)) {
        placeTower(x, y, gameState.selectedTowerType);
      }
    } else {
      // Select existing tower
      const clickedTower = gameState.battleState.towers.find(tower => {
        const distance = Math.sqrt((x - tower.x) ** 2 + (y - tower.y) ** 2);
        return distance < 25;
      });

      if (clickedTower) {
        setGameState(prev => ({
          ...prev,
          selectedTower: clickedTower,
          selectedTowerType: null
        }));
      }
    }
  };

  const canPlaceTower = (x: number, y: number): boolean => {
    if (!gameState.selectedStage) return false;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return false;

    // Check distance from path
    for (const point of stage.path) {
      const distance = Math.sqrt((x - point.x) ** 2 + (y - point.y) ** 2);
      if (distance < 40) return false;
    }

    // Check distance from other towers
    for (const tower of gameState.battleState.towers) {
      const distance = Math.sqrt((x - tower.x) ** 2 + (y - tower.y) ** 2);
      if (distance < 50) return false;
    }

    return x > 25 && x < 575 && y > 25 && y < 295;
  };

  const placeTower = (x: number, y: number, type: string) => {
    const towerConfig = towerTypes[type as keyof typeof towerTypes];
    if (playerData.gold < towerConfig.cost) return;

    const newTower: Tower = {
      id: `tower_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      x, y, type,
      level: 1,
      damage: towerConfig.damage,
      range: towerConfig.range,
      attackSpeed: towerConfig.speed,
      lastAttack: 0,
      cost: towerConfig.cost,
      kills: 0
    };

    setPlayerData(prev => ({ ...prev, gold: prev.gold - towerConfig.cost }));
    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        towers: [...prev.battleState.towers, newTower]
      },
      selectedTowerType: null
    }));

    onAction('tower_placed', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      tower_type: type,
      tower_cost: towerConfig.cost,
      position_x: x,
      position_y: y,
      remaining_gold: playerData.gold - towerConfig.cost,
      wave_number: gameState.battleState.wave
    });
  };

  const startWave = () => {
    if (gameState.battleState.isWaveActive) return;

    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        isWaveActive: true,
        waveProgress: 0
      }
    }));

    onAction('wave_started', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      wave_number: gameState.battleState.wave,
      towers_count: gameState.battleState.towers.length,
      current_gold: playerData.gold
    });

    // Spawn enemies
    spawnEnemyWave();
  };

  const spawnEnemyWave = () => {
    if (!gameState.selectedStage) return;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return;

    const enemyCount = 8 + gameState.battleState.wave * 2;
    const enemyTypeKeys = Object.keys(enemyTypes);

    for (let i = 0; i < enemyCount; i++) {
      setTimeout(() => {
        const enemyType = enemyTypeKeys[Math.min(gameState.battleState.wave - 1, enemyTypeKeys.length - 1)];
        const enemyConfig = enemyTypes[enemyType as keyof typeof enemyTypes];

        const newEnemy: Enemy = {
          id: `enemy_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
          x: stage.path[0].x,
          y: stage.path[0].y,
          health: enemyConfig.health * gameState.selectedStage!,
          maxHealth: enemyConfig.health * gameState.selectedStage!,
          speed: enemyConfig.speed,
          type: enemyType,
          pathIndex: 0,
          alive: true,
          armor: enemyConfig.armor,
          reward: [
            { type: 'gold', amount: enemyConfig.reward },
            { type: 'exp', amount: enemyConfig.reward / 2 }
          ]
        };

        setGameState(prev => ({
          ...prev,
          battleState: {
            ...prev.battleState,
            enemies: [...prev.battleState.enemies, newEnemy]
          }
        }));
      }, i * 800);
    }
  };

  const pauseBattle = () => {
    onAction('battle_paused', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      wave_number: gameState.battleState.wave,
      battle_time: gameState.battleState.battleTime,
      current_score: gameState.battleState.score
    });
  };

  const completeStage = (victory: boolean) => {
    if (!gameState.selectedStage) return;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return;

    const starsEarned = victory ? calculateStars() : 0;
    const rewards = victory ? stage.rewards : [];

    // Apply rewards
    if (victory) {
      let goldGained = 0;
      let expGained = 0;
      let gemsGained = 0;

      rewards.forEach(reward => {
        switch (reward.type) {
          case 'gold':
            goldGained += reward.amount;
            break;
          case 'exp':
            expGained += reward.amount;
            break;
          case 'gem':
            gemsGained += reward.amount;
            break;
        }
      });

      setPlayerData(prev => ({
        ...prev,
        gold: prev.gold + goldGained,
        experience: prev.experience + expGained,
        gems: prev.gems + gemsGained,
        unlockedStages: Math.max(prev.unlockedStages, gameState.selectedStage! + 1)
      }));

      // Record completion
      const completion: CompletedStage = {
        stageId: gameState.selectedStage,
        bestScore: gameState.battleState.score,
        completionTime: gameState.battleState.battleTime,
        starsEarned,
        completedAt: new Date().toISOString()
      };

      setPlayerData(prev => {
        const existingIndex = prev.completedStages.findIndex(c => c.stageId === gameState.selectedStage);
        const newCompletedStages = [...prev.completedStages];

        if (existingIndex >= 0) {
          newCompletedStages[existingIndex] = completion;
        } else {
          newCompletedStages.push(completion);
        }

        return { ...prev, completedStages: newCompletedStages };
      });
    }

    const results: StageResults = {
      victory,
      score: gameState.battleState.score,
      starsEarned,
      timeBonus: victory ? Math.max(0, 300 - gameState.battleState.battleTime) : 0,
      rewards,
      newUnlocks: victory && gameState.selectedStage === playerData.unlockedStages ? 
        [`Stage ${gameState.selectedStage + 1} Unlocked!`] : []
    };

    setGameState(prev => ({
      ...prev,
      currentScene: victory ? 'stageComplete' : 'stageFailed',
      stageResults: results
    }));

    onAction(victory ? 'stage_victory' : 'stage_defeat', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      stage_name: stage.name,
      final_score: gameState.battleState.score,
      stars_earned: starsEarned,
      battle_time: gameState.battleState.battleTime,
      towers_built: gameState.battleState.towers.length,
      waves_completed: gameState.battleState.wave,
      gold_gained: victory ? rewards.find(r => r.type === 'gold')?.amount || 0 : 0,
      exp_gained: victory ? rewards.find(r => r.type === 'exp')?.amount || 0 : 0,
      new_unlocks: results.newUnlocks
    });
  };

  const calculateStars = (): number => {
    const baseScore = gameState.battleState.score;
    const timeBonus = Math.max(0, 300 - gameState.battleState.battleTime);
    const livesBonus = gameState.battleState.lives * 10;

    const totalScore = baseScore + timeBonus + livesBonus;

    if (totalScore >= 1000) return 3;
    if (totalScore >= 500) return 2;
    return 1;
  };

  const handleResultsClick = (x: number, y: number) => {
    // Continue button
    if (x >= 200 && x <= 400 && y >= 300 && y <= 350) {
      setGameState(prev => ({
        ...prev,
        currentScene: 'lobby',
        stageResults: null,
        selectedStage: null
      }));
    }
  };

  // Game loop updates
  const updateBattle = useCallback(() => {
    if (gameState.currentScene !== 'battle' || !gameState.battleState.isWaveActive) return;

    // Update battle time
    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        battleTime: prev.battleState.battleTime + 1
      }
    }));

    // Update enemies
    updateEnemies();

    // Update towers
    updateTowers();

    // Check win/lose conditions
    checkBattleEnd();
  }, [gameState.currentScene, gameState.battleState.isWaveActive]);

  const updateEnemies = () => {
    if (!gameState.selectedStage) return;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return;

    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        enemies: prev.battleState.enemies.map(enemy => {
          if (!enemy.alive) return enemy;

          const currentTarget = stage.path[enemy.pathIndex + 1];
          if (currentTarget) {
            const dx = currentTarget.x - enemy.x;
            const dy = currentTarget.y - enemy.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            if (distance < 5) {
              enemy.pathIndex++;
              if (enemy.pathIndex >= stage.path.length - 1) {
                // Enemy reached end
                setGameState(prev2 => ({
                  ...prev2,
                  battleState: {
                    ...prev2.battleState,
                    lives: prev2.battleState.lives - 1
                  }
                }));

                onAction('life_lost', {
                  player_id: playerData.id,
                  stage_id: gameState.selectedStage,
                  enemy_type: enemy.type,
                  remaining_lives: gameState.battleState.lives - 1,
                  wave_number: gameState.battleState.wave
                });

                return { ...enemy, alive: false };
              }
            } else {
              const moveX = (dx / distance) * enemy.speed;
              const moveY = (dy / distance) * enemy.speed;
              enemy.x += moveX;
              enemy.y += moveY;
            }
          }

          return enemy;
        }).filter(enemy => enemy.alive || enemy.pathIndex < stage.path.length - 1)
      }
    }));
  };

  const updateTowers = () => {
    gameState.battleState.towers.forEach(tower => {
      if (frameCount - tower.lastAttack < tower.attackSpeed) return;

      const enemiesInRange = gameState.battleState.enemies.filter(enemy => {
        if (!enemy.alive) return false;
        const distance = Math.sqrt((enemy.x - tower.x) ** 2 + (enemy.y - tower.y) ** 2);
        return distance <= tower.range;
      });

      if (enemiesInRange.length > 0) {
        const target = enemiesInRange.reduce((best, current) => 
          current.pathIndex > best.pathIndex ? current : best
        );

        const actualDamage = Math.max(1, tower.damage - target.armor);

        setGameState(prev => ({
          ...prev,
          battleState: {
            ...prev.battleState,
            enemies: prev.battleState.enemies.map(enemy => {
              if (enemy.id === target.id) {
                const newHealth = Math.max(0, enemy.health - actualDamage);
                const killed = newHealth <= 0;

                if (killed) {
                  // Apply rewards
                  const goldGain = enemy.reward.find(r => r.type === 'gold')?.amount || 0;
                  const expGain = enemy.reward.find(r => r.type === 'exp')?.amount || 0;

                  setPlayerData(prev2 => ({
                    ...prev2,
                    gold: prev2.gold + goldGain,
                    experience: prev2.experience + expGain
                  }));

                  setGameState(prev2 => ({
                    ...prev2,
                    battleState: {
                      ...prev2.battleState,
                      score: prev2.battleState.score + goldGain * 10,
                      towers: prev2.battleState.towers.map(t => 
                        t.id === tower.id ? { ...t, kills: t.kills + 1 } : t
                      )
                    }
                  }));

                  if (enemy.type === 'demon') {
                    onAction('boss_defeated', {
                      player_id: playerData.id,
                      stage_id: gameState.selectedStage,
                      boss_type: enemy.type,
                      tower_type: tower.type,
                      gold_gained: goldGain,
                      exp_gained: expGain
                    });
                  }

                  return { ...enemy, health: 0, alive: false };
                }

                return { ...enemy, health: newHealth };
              }
              return enemy;
            })
          }
        }));

        tower.lastAttack = frameCount;
      }
    });
  };

  const checkBattleEnd = () => {
