    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    switch (gameState.currentScene) {
      case 'lobby':
        drawLobby(ctx, canvas);
        break;
      case 'dungeon':
        drawDungeon(ctx, canvas);
        break;
      case 'battle':
        drawBattle(ctx, canvas);
        break;
      case 'stageComplete':
      case 'stageFailed':
        drawBattle(ctx, canvas);
        drawResults(ctx, canvas);
        break;
      case 'shop':
