        // Add shop overlay
        ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.fillStyle = '#FFD700';
        ctx.font = 'bold 24px Arial';
        ctx.textAlign = 'center';
        ctx.fillText('ðŸ›’ SHOP (Coming Soon)', canvas.width / 2, canvas.height / 2);
        break;
      case 'inventory':
        drawLobby(ctx, canvas);
        // Add inventory overlay
        ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.fillStyle = '#FFD700';
        ctx.font = 'bold 24px Arial';
        ctx.textAlign = 'center';
        ctx.fillText('ðŸŽ’ INVENTORY (Coming Soon)', canvas.width / 2, canvas.height / 2);
        break;
    }
  }, [gameState, playerData]);

  // Game loop
  useEffect(() => {
    const gameLoop = () => {
