import React, { useState, useEffect } from 'react';
import { BarChart, TrendingUp, Users, PartyPopper, X, Trophy, Star } from 'lucide-react';
import { useLanguage } from '../../contexts/LanguageContext';
import { useTheme } from '../../contexts/ThemeContext';

interface Step4Props {
  onComplete: () => void;
  appData: {
    appId: string;
    dataUrl: string;
    selectedVersion: 'saas' | 'private' | null;
  };
}

export const Step4AnalyticsStart: React.FC<Step4Props> = ({
  onComplete,
  appData
}) => {
  const { t } = useLanguage();
  const { colors } = useTheme();
  const [understood, setUnderstood] = useState(false);
  const [celebrationShown, setCelebrationShown] = useState(false);
  const [hasCompleted, setHasCompleted] = useState(false);

  // í…Œë§ˆ ìƒ‰ìƒ ê°€ì ¸ì˜¤ê¸°
  const theme = colors;

  useEffect(() => {
    if (understood && !hasCompleted) {
      onComplete();
      setCelebrationShown(true);
      setHasCompleted(true);

      // íŒì—…ì´ í‘œì‹œë  ë•Œ í™”ë©´ ì¤‘ì•™ìœ¼ë¡œ ë¶€ë“œëŸ½ê²Œ ìŠ¤í¬ë¡¤
      setTimeout(() => {
        const windowHeight = window.innerHeight;
        const documentHeight = document.documentElement.scrollHeight;
        const scrollPosition = Math.max(0, (documentHeight - windowHeight) / 2);

        window.scrollTo({
          top: scrollPosition,
          left: 0,
          behavior: 'smooth'
        });
      }, 100);
