import React, { useState, useEffect } from 'react';
import { Users, UserPlus, Shield, Eye, EyeOff } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { Tabs } from '../../components/Tabs';

interface Step2Props {
  onComplete: () => void;
}

export const Step2AccountSetup: React.FC<Step2Props> = ({ onComplete }) => {
  const [understood, setUnderstood] = useState(false);

  useEffect(() => {
    if (understood) {
      onComplete();
    }
  }, [understood, onComplete]);

  const roleTypes = [
    {
      name: '프로젝트 관리자',
      badge: { text: '최고 권한', variant: 'danger' as const },
      permissions: [
        '모든 프로젝트 설정 관리',
        '구성원 및 역할 관리',
        '데이터 연동 설정',
        '보고서 생성 및 공유',
        '시스템 설정'
      ],
      description: '프로젝트의 모든 권한을 가지며 전체적인 관리를 담당합니다.'
    },
    {
      name: '분석 관리자',
      badge: { text: '관리 권한', variant: 'warning' as const },
      permissions: [
        '대시보드 생성 및 편집',
        '보고서 관리',
        '데이터 분석',
        '일반 구성원 관리',
        '분석 도구 설정'
      ],
      description: '분석과 관련된 모든 업무를 관리할 수 있습니다.'
    },
    {
      name: '분석가',
      badge: { text: '분석 권한', variant: 'info' as const },
      permissions: [
        '대시보드 보기 및 생성',
        '데이터 분석',
        '개인 보고서 생성',
        '차트 및 위젯 생성',
        '데이터 내보내기'
      ],
      description: '데이터 분석 업무를 수행할 수 있습니다.'
    },
    {
      name: '일반 구성원',
      badge: { text: '기본 권한', variant: 'secondary' as const },
      permissions: [
        '대시보드 보기',
        '기본 보고서 보기',
        '개인 설정 관리'
      ],
      description: '기본적인 데이터 조회만 가능합니다.'
    }
  ];

  const dataPermissionSettings = [
    {
      name: '표시 설정',
      icon: <Eye size={20} color="var(--info)" />,
      description: '구성원이 볼 수 있는 데이터 범위를 설정합니다.',
      options: [
        '전체 데이터 접근',
        '부서별 데이터만',
        '개인 데이터만',
        '지정된 기간만'
      ]
    },
    {
      name: '범위 설정',
      icon: <Shield size={20} color="var(--warning)" />,
      description: '접근 가능한 기능과 메뉴를 제한합니다.',
      options: [
        '모든 기능 접근',
        '분석 기능만',
        '보고서 조회만',
        '대시보드만'
      ]
    },
    {
      name: '마스킹',
      icon: <EyeOff size={20} color="var(--danger)" />,
      description: '민감한 데이터를 숨기거나 마스킹합니다.',
      options: [
        '마스킹 없음',
        '개인정보 마스킹',
        '결제정보 마스킹',
        '전체 마스킹'
      ]
    }
  ];

  const memberCreationTab = (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ 
          fontSize: '16px', 
          fontWeight: '600', 
          marginBottom: '12px',
          color: 'var(--gray-900)'
        }}>
          구성원 초대 방법
        </h5>
        <div style={{ 
          display: 'flex', 
          flexDirection: 'column', 
          gap: '12px' 
        }}>
          {[
            '이메일을 통한 개별 초대',
            '대량 초대 (CSV 파일 업로드)',
            '초대 링크 생성 및 공유',
            'SSO를 통한 자동 가입'
          ].map((method, index) => (
            <div key={index} style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '12px',
              padding: '12px',
              backgroundColor: 'var(--gray-50)',
              borderRadius: 'var(--border-radius)',
              border: '1px solid var(--gray-200)'
            }}>
              <div style={{
                width: '24px',
                height: '24px',
                borderRadius: '50%',
                backgroundColor: 'var(--brand-primary)',
                color: 'white',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: '600'
              }}>
                {index + 1}
              </div>
              <span style={{ fontSize: '14px', color: 'var(--gray-700)' }}>
                {method}
              </span>
            </div>
          ))}
        </div>
      </div>

      <InfoBox type="info" title="구성원 초대 팁">
        <p style={{ margin: 0 }}>
          초대된 구성원은 이메일을 통해 가입 링크를 받고, 계정 생성 후 바로 프로젝트에 참여할 수 있습니다.
        </p>
      </InfoBox>
    </div>
  );

  const roleCreationTab = (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ 
          fontSize: '16px', 
          fontWeight: '600', 
          marginBottom: '16px',
          color: 'var(--gray-900)'
        }}>
          역할 유형별 권한 비교
        </h5>
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
          gap: '16px' 
        }}>
          {roleTypes.map((role, index) => (
            <Card key={index}>
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'space-between',
                marginBottom: '12px'
              }}>
                <h6 style={{ 
                  fontSize: '16px', 
                  fontWeight: '600', 
                  margin: 0,
                  color: 'var(--gray-900)'
                }}>
                  {role.name}
                </h6>
                <Badge variant={role.badge.variant} size="sm">
                  {role.badge.text}
                </Badge>
              </div>

              <p style={{ 
                fontSize: '12px', 
                color: 'var(--gray-600)', 
                marginBottom: '16px',
                lineHeight: '1.4'
              }}>
                {role.description}
              </p>

              <ul style={{ 
                margin: 0, 
                paddingLeft: '16px', 
                fontSize: '13px' 
              }}>
                {role.permissions.map((permission, permIndex) => (
                  <li key={permIndex} style={{ 
                    marginBottom: '4px', 
                    color: 'var(--gray-700)',
                    lineHeight: '1.3'
                  }}>
                    {permission}
                  </li>
                ))}
              </ul>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );

  const dataPermissionTab = (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ 
          fontSize: '16px', 
          fontWeight: '600', 
          marginBottom: '16px',
          color: 'var(--gray-900)'
        }}>
          데이터 권한 관리
        </h5>

        <div style={{ 
          display: 'flex', 
          flexDirection: 'column', 
          gap: '16px' 
        }}>
          {dataPermissionSettings.map((setting, index) => (
            <Card key={index}>
              <div style={{ 
                display: 'flex', 
                alignItems: 'flex-start', 
                gap: '16px' 
              }}>
                <div style={{ flexShrink: 0, marginTop: '4px' }}>
                  {setting.icon}
                </div>
                <div style={{ flex: 1 }}>
                  <h6 style={{ 
                    fontSize: '16px', 
                    fontWeight: '600', 
                    margin: '0 0 8px 0',
                    color: 'var(--gray-900)'
                  }}>
                    {setting.name}
                  </h6>
                  <p style={{ 
                    fontSize: '14px', 
                    color: 'var(--gray-600)', 
                    marginBottom: '12px',
                    lineHeight: '1.5'
                  }}>
                    {setting.description}
                  </p>
                  <div style={{ 
                    display: 'flex', 
                    flexWrap: 'wrap', 
                    gap: '8px' 
                  }}>
                    {setting.options.map((option, optionIndex) => (
                      <Badge key={optionIndex} variant="secondary" size="sm">
                        {option}
                      </Badge>
                    ))}
                  </div>
                </div>
              </div>
            </Card>
          ))}
        </div>
      </div>

      <InfoBox type="warning" title="데이터 권한 설정 주의사항">
        <p style={{ margin: 0 }}>
          데이터 마스킹은 한 번 설정하면 변경하기 어려우니 신중하게 결정해주세요. 
          개인정보 보호 규정을 준수하여 설정하는 것이 중요합니다.
        </p>
      </InfoBox>
    </div>
  );

  const tabs = [
    {
      id: 'members',
      label: '구성원 생성',
      content: memberCreationTab
    },
    {
      id: 'roles',
      label: '역할 생성',
      content: roleCreationTab
    },
    {
      id: 'permissions',
      label: '데이터 권한',
      content: dataPermissionTab
    }
  ];

  return (
    <div>
      {/* Step Title */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '12px',
          marginBottom: '16px' 
        }}>
          <Users size={24} color="var(--brand-primary)" />
          <h3 style={{ 
            fontSize: '24px', 
            fontWeight: '600', 
            color: 'var(--gray-900)',
            margin: 0
          }}>
            2단계: 계정 및 권한 설정
          </h3>
        </div>
        <p style={{ 
          fontSize: '16px', 
          color: 'var(--gray-600)',
          lineHeight: '1.6',
          margin: 0
        }}>
          프로젝트에 참여할 구성원을 추가하고 적절한 권한을 부여하세요. 
          역할별로 다른 권한을 설정하여 보안을 강화할 수 있습니다.
        </p>
      </div>

      {/* Tabs Content */}
      <div style={{ marginBottom: '32px' }}>
        <Tabs tabs={tabs} defaultTab="members" />
      </div>

      {/* Completion Checkbox */}
      <div style={{ 
        padding: '20px', 
        backgroundColor: 'var(--gray-50)', 
        borderRadius: 'var(--border-radius)',
        border: '1px solid var(--gray-200)'
      }}>
        <Checkbox
          checked={understood}
          onChange={setUnderstood}
          label="계정 및 권한 설정에 대해 이해했으며, 다음 단계로 진행할 준비가 되었습니다."
        />
      </div>
    </div>
  );
};
