import React, { useState, useEffect } from 'react';
import { Play, Star, Trophy, Zap, Users, Code, Settings, ChevronRight, Mail, Phone, Video, Shield, BookOpen, HelpCircle, Target, Sparkles, Crown, Sword, Search, User, Home, Gamepad2, BarChart3 } from 'lucide-react';
import { Button } from '../components/Button';

export const HomePage: React.FC = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [focusedCard, setFocusedCard] = useState(0);
  const [screenSize, setScreenSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    const handleResize = () => {
      setScreenSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => {
      clearInterval(timer);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navigateToQuickStart = () => {
    window.location.href = '/quickstart';
  };

  // Î∞òÏùëÌòï ÏÑ§Ï†ï
  const isMobile = screenSize.width < 768;
  const isTablet = screenSize.width >= 768 && screenSize.width < 1200;
  const isDesktop = screenSize.width >= 1200;

  // Í≤åÏûÑ Ïπ¥ÎìúÎì§ Îç∞Ïù¥ÌÑ∞
  const gameCards = [
    {
      id: 0,
      title: "Îπ†Î•∏ ÏãúÏûë Í∞ÄÏù¥Îìú",
      subtitle: "Îç∞Ïù¥ÌÑ∞ Î∂ÑÏÑùÏùò Í∏∞Ï¥àÎ•º Î∞∞ÏõåÎ≥¥ÏÑ∏Ïöî",
      image: "üöÄ",
      featured: true,
      category: "Quick Start",
      onClick: navigateToQuickStart
    },
    {
      id: 1,
      title: "ThinkingDataÎûÄ?",
      subtitle: "ÌîåÎû´Ìèº ÏÜåÍ∞úÏôÄ ÌïµÏã¨ Í∏∞Îä•",
      image: "üìä",
      featured: false,
      category: "Learning"
    },
    {
      id: 2,
      title: "ÏÇ¨Ïö©Ïûê Í∞ÄÏù¥Îìú", 
      subtitle: "Í∏∞Î≥∏ ÏÇ¨Ïö©Î≤ïÎ∂ÄÌÑ∞ Í≥†Í∏â Í∏∞Îä•ÍπåÏßÄ",
      image: "üë•",
      featured: false,
      category: "Guide"
    },
    {
      id: 3,
      title: "Í∞úÎ∞úÏûê Î¨∏ÏÑú",
      subtitle: "API ÌÜµÌï© Î∞è SDK ÏÇ¨Ïö©Î≤ï",
      image: "üíª",
      featured: false,
      category: "Development"
    },
    {
      id: 4,
      title: "Í¥ÄÎ¶¨Ïûê ÎèÑÍµ¨",
      subtitle: "Í≥†Í∏â ÏÑ§Ï†ï Î∞è Í¥ÄÎ¶¨ Í∏∞Îä•",
      image: "‚öôÔ∏è",
      featured: false,
      category: "Admin"
    },
    {
      id: 5,
      title: "Í≥†Í∞ù ÏßÄÏõê",
      subtitle: "Í∏∞Ïà† ÏßÄÏõê Î∞è Î¨∏Ïùò",
      image: "üí¨",
      featured: false,
      category: "Support"
    }
  ];

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a1428 0%, #1a2332 50%, #2d3748 100%)',
      color: 'white',
      fontFamily: 'Segoe UI, system-ui, sans-serif',
      overflow: 'hidden'
    }}>
      {/* Xbox Ïä§ÌÉÄÏùº ÏÉÅÎã® Î∞î */}
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        height: '48px',
        background: 'rgba(0, 0, 0, 0.85)',
        backdropFilter: 'blur(10px)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0 20px',
        zIndex: 1000,
        borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
      }}>
        {/* ÏôºÏ™Ω: ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <div style={{
            width: '32px',
            height: '32px',
            borderRadius: '50%',
            background: 'linear-gradient(135deg, #0078d4, #106ebe)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            üìä
          </div>
          <span style={{
            color: 'white',
            fontSize: '14px',
            fontWeight: '500'
          }}>
            ThinkingData Master
          </span>
        </div>

        {/* Í∞ÄÏö¥Îç∞: ÏãúÍ∞Ñ */}
        <div style={{
          color: 'rgba(255, 255, 255, 0.8)',
          fontSize: '14px',
          fontWeight: '400'
        }}>
          {currentTime.toLocaleTimeString('ko-KR', { 
            hour: '2-digit', 
            minute: '2-digit' 
          })}
        </div>

        {/* Ïò§Î•∏Ï™Ω: ÏïÑÏù¥ÏΩòÎì§ */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <Search size={20} style={{ color: 'rgba(255, 255, 255, 0.7)', cursor: 'pointer' }} />
          <User size={20} style={{ color: 'rgba(255, 255, 255, 0.7)', cursor: 'pointer' }} />
        </div>
      </div>

      {/* Î©îÏù∏ Ïª®ÌÖåÏù¥ÎÑà */}
      <div style={{
        marginTop: '48px',
        padding: '20px',
        height: 'calc(100vh - 48px)',
        display: 'grid',
        gridTemplateColumns: isMobile ? '1fr' : '280px 1fr',
        gridTemplateRows: isMobile ? '1fr auto' : '1fr',
        gap: '20px'
      }}>

        {/* ÏôºÏ™Ω ÏÇ¨Ïù¥ÎìúÎ∞î */}
        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          borderRadius: '12px',
          padding: '16px',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          order: isMobile ? 2 : 1
        }}>
          <div style={{ marginBottom: '24px' }}>
            <h2 style={{
              color: 'white',
              fontSize: '16px',
              fontWeight: '600',
              margin: '0 0 16px 0',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <Home size={18} />
              ÎÇ¥ Í≤åÏûÑ & Ïï±
            </h2>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
              {[
                { icon: 'üöÄ', name: 'Îπ†Î•∏ ÏãúÏûë Í∞ÄÏù¥Îìú', category: 'Quick Start', active: true, onClick: navigateToQuickStart },
                { icon: 'üìä', name: 'ThinkingData ÏÜåÍ∞ú', category: 'Learning' },
                { icon: 'üë•', name: 'ÏÇ¨Ïö©Ïûê Í∞ÄÏù¥Îìú', category: 'User Guide' },
                { icon: 'üíª', name: 'Í∞úÎ∞úÏûê Î¨∏ÏÑú', category: 'Developer' },
                { icon: '‚öôÔ∏è', name: 'Í¥ÄÎ¶¨Ïûê ÎèÑÍµ¨', category: 'Admin' }
              ].map((item, index) => (
                <div 
                  key={index}
                  onClick={item.onClick}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '12px',
                    padding: '12px',
                    borderRadius: '8px',
                    background: item.active ? 'rgba(0, 120, 212, 0.2)' : 'transparent',
                    border: item.active ? '1px solid rgba(0, 120, 212, 0.4)' : '1px solid transparent',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    if (!item.active) {
                      (e.currentTarget as HTMLElement).style.background = 'rgba(255, 255, 255, 0.05)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (!item.active) {
                      (e.currentTarget as HTMLElement).style.background = 'transparent';
                    }
                  }}
                >
                  <div style={{
                    width: '32px',
                    height: '32px',
                    borderRadius: '6px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '16px'
                  }}>
                    {item.icon}
                  </div>
                  <div style={{ flex: 1, minWidth: 0 }}>
                    <div style={{
                      color: 'white',
                      fontSize: '13px',
                      fontWeight: '500',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}>
                      {item.name}
                    </div>
                    <div style={{
                      color: 'rgba(255, 255, 255, 0.6)',
                      fontSize: '11px'
                    }}>
                      {item.category}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Ï∂îÏ≤ú Î∞è ÌÜµÍ≥Ñ */}
          <div>
            <h3 style={{
              color: 'rgba(255, 255, 255, 0.8)',
              fontSize: '14px',
              fontWeight: '600',
              margin: '0 0 12px 0',
              textTransform: 'uppercase',
