import React, { useState, useEffect, useRef } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  clearedFloors: Set<number>; // Track which floors have been cleared
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
  isComingSoon?: boolean; // ì—…ë°ì´íŠ¸ ì˜ˆì • ë˜ì „ì¸ì§€
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  baseDamage: number;
  damageMultiplier: number; // ë ˆë²¨ë‹¹ ì¦ê°€ìœ¨
  statMultiplier: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
  }; // ìŠ¤íƒ¯ ê¸°ë°˜ ë°ë¯¸ì§€ ë°°ìœ¨
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: "active" | "passive";
  category: "combat" | "magic" | "support" | "passive"; // ìŠ¤í‚¬ ë¶„ë¥˜
  description: string;
  requiredLevel?: number;
  isUnlocked?: boolean;
  isLearned?: boolean; // ë°°ìš´ ìŠ¤í‚¬ì¸ì§€ (0ë ˆë²¨ì—ì„œ 1ë ˆë²¨ë¡œ ì—…ê·¸ë ˆì´ë“œí–ˆëŠ”ì§€)
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type:
    | "weapon"
    | "armor"
    | "helmet"
    | "boots"
    | "ring"
    | "amulet"
    | "consumable";
  rarity: "common" | "rare" | "epic" | "legendary" | "mythic";
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // ë¬¼ì•½ìš© ì†ì„±ë“¤
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // ë²„í”„ ì§€ì†ì‹œê°„ (ë°€ë¦¬ì´ˆ)
    buffType?: "attack" | "defense" | "speed"; // ë²„í”„ íƒ€ì…
    buffAmount?: number; // ë²„í”„ ìˆ˜ì¹˜
  };
  quantity?: number; // ì†Œë¹„ ì•„ì´í…œ ìˆ˜ëŸ‰
}

interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser,
}) => {
  const { colors } = useTheme();
  const { t } = useLanguage();

  // ìŠ¤í‚¬ ë°ë¯¸ì§€ ê³„ì‚° í•¨ìˆ˜
  const calculateSkillDamage = (skill: Skill, character: Character): number => {
    if (skill.level === 0 || !skill.isLearned) return 0;

    let damage = skill.baseDamage;

    // ë ˆë²¨ì— ë”°ë¥¸ ë°ë¯¸ì§€ ì¦ê°€
    damage += skill.baseDamage * skill.damageMultiplier * (skill.level - 1);

    // ìŠ¤íƒ¯ì— ë”°ë¥¸ ë°ë¯¸ì§€ ì¦ê°€
    if (skill.statMultiplier.attack) {
      damage += character.attack * skill.statMultiplier.attack * skill.level;
    }
    if (skill.statMultiplier.defense) {
      damage += character.defense * skill.statMultiplier.defense * skill.level;
    }
    if (skill.statMultiplier.hp) {
      damage += character.maxHp * skill.statMultiplier.hp * skill.level;
    }
    if (skill.statMultiplier.mp) {
      damage += character.maxMp * skill.statMultiplier.mp * skill.level;
    }

    return Math.floor(damage);
  };

  // CSS ì• ë‹ˆë©”ì´ì…˜ ì¶”ê°€
  React.useEffect(() => {
    const style = document.createElement("style");
    style.textContent = `
      @keyframes progress {
        0% {
          width: 0%;
        }
        100% {
          width: 100%;
        }
      }
      @keyframes pulse {
        0%, 100% {
          opacity: 1;
        }
        50% {
          opacity: 0.7;
        }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);
  const [character, setCharacter] = useState<Character>({
    id: "1",
    name: "ìš©ì‚¬",
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: "ì „ì‚¬",
    skills: [
      // ê¸°ë³¸ ìŠ¤í‚¬ë“¤ì„ ë¯¸ë¦¬ ì •ì˜í•˜ì—¬ ì´ˆê¸°í™” ë¬¸ì œ ë°©ì§€
      {
        id: "basic-attack",
        name: "ê¸°ë³¸ ê³µê²©",
        level: 1,
        maxLevel: 10,
        baseDamage: 15,
        damageMultiplier: 0.2,
        statMultiplier: { attack: 1.0 },
        manaCost: 0,
        cooldown: 1000,
        currentCooldown: 0,
        type: "active",
        category: "combat",
        description: "ê¸°ë³¸ì ì¸ ë¬¼ë¦¬ ê³µê²©ì…ë‹ˆë‹¤.",
        requiredLevel: 1,
        isUnlocked: true,
        isLearned: true,
      },
      {
        id: "power-strike",
        name: "ê°•ë ¥í•œ ë² ê¸°",
        level: 0,
        maxLevel: 10,
        baseDamage: 25,
        damageMultiplier: 0.3,
        statMultiplier: { attack: 1.5 },
        manaCost: 15,
