  const generateItemName = (rarity: string, itemType: string): string => {
    const prefixes = {
      common: ["ë‚¡ì€", "ì¼ë°˜ì ì¸", "ê¸°ë³¸"],
      rare: ["ë‚ ì¹´ë¡œìš´", "íŠ¼íŠ¼í•œ", "ë§ˆë²•ì˜"],
      epic: ["ì „ì„¤ì ì¸", "ê³ ëŒ€ì˜", "ì‹ ì„±í•œ"],
