    };

    const itemNames = {
      weapon: ["ê²€", "ë„ë¼", "ì°½", "í™œ", "ì§€íŒ¡ì´"],
      armor: ["ê°‘ì˜·", "ë¡œë¸Œ", "ì‚¬ìŠ¬ê°‘ì˜·", "ê°€ì£½ê°‘ì˜·"],
      helmet: ["íˆ¬êµ¬", "ëª¨ì", "ê´€", "í—¬ë¦„"],
