      ring: ["ë°˜ì§€", "ë§", "ë°´ë“œ"],
      amulet: ["ëª©ê±¸ì´", "ë¶€ì ", "íœë˜íŠ¸"],
    };

    const prefix =
      prefixes[rarity as keyof typeof prefixes][Math.floor(Math.random() * 3)];
    const itemTypeNames = itemNames[itemType as keyof typeof itemNames] || [
      "ì•„ì´í…œ",
