                          (invItem) => invItem.id !== selectedShopItem.id
                        )
                      );
                      addToCombatLog(
                        `${selectedShopItem.name}ì„ ${sellPrice} ê³¨ë“œì— íŒë§¤í–ˆìŠµë‹ˆë‹¤!`
                      );
                      setSelectedShopItem(null);
                    }}
                    style={{
                      background:
                        "linear-gradient(135deg, #f59e0b 0%, #d97706 100%)",
                      color: "white",
                      border: "none",
                      borderRadius: "6px",
                      padding: "12px",
                      fontSize: "14px",
                      cursor: "pointer",
                      fontWeight: "bold",
                    }}
                  >
                    íŒë§¤í•˜ê¸° (
                    {Math.floor(selectedShopItem.price * 0.7).toLocaleString()}
                    G)
                  </button>
                )}

                {shopCategory === "craft" && (
                  <button
                    onClick={() => {
                      const craftPrice = Math.floor(
                        selectedShopItem.price * 1.5
                      );
                      if (character.gold >= craftPrice) {
                        setCharacter((prev) => ({
                          ...prev,
                          gold: prev.gold - craftPrice,
                        }));
                        setInventory((prev) => [
                          ...prev,
                          {
                            ...selectedShopItem,
                            id: `${selectedShopItem.id}_${Date.now()}`,
                          },
                        ]);
                        addToCombatLog(
                          `${selectedShopItem.name}ì„ ì œì‘í–ˆìŠµë‹ˆë‹¤!`
                        );
                        setSelectedShopItem(null);
                      }
                    }}
                    disabled={
                      character.gold < Math.floor(selectedShopItem.price * 1.5)
                    }
                    style={{
                      background:
                        character.gold >=
                        Math.floor(selectedShopItem.price * 1.5)
                          ? "linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)"
                          : "#6b7280",
                      color: "white",
                      border: "none",
                      borderRadius: "6px",
                      padding: "12px",
                      fontSize: "14px",
                      cursor:
                        character.gold >=
                        Math.floor(selectedShopItem.price * 1.5)
                          ? "pointer"
                          : "not-allowed",
                      fontWeight: "bold",
                    }}
                  >
                    ì œì‘í•˜ê¸° (
                    {Math.floor(selectedShopItem.price * 1.5).toLocaleString()}
                    G)
                  </button>
                )}
              </div>
            </div>
          ) : (
            <div
              style={{
                background: "rgba(255, 255, 255, 0.05)",
                borderRadius: "10px",
                padding: "40px",
                textAlign: "center",
                color: "#6b7280",
              }}
            >
              <p>ìƒí’ˆì„ í´ë¦­í•˜ì—¬ ì •ë³´ë¥¼ í™•ì¸í•˜ì„¸ìš”</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const renderTabs = () => (
