        { id: "character", name: t("rpg.tabs.character"), icon: "ğŸ‘¤" },
        { id: "inventory", name: t("rpg.tabs.inventory"), icon: "ğŸ’" },
        { id: "skills", name: t("rpg.tabs.skills"), icon: "âœ¨" },
        { id: "dungeon", name: t("rpg.tabs.dungeon"), icon: "ğŸ°" },
