  badges: string[];
  joinDate: string;
}

interface Notification {
  id: string;
  title: string;
  message: string;
  type: 'success' | 'info' | 'warning' | 'error';
  timestamp: number;
  read: boolean;
}

interface Restaurant {
  id: string;
  name: string;
  category: string;
  image: string;
  rating: number;
  deliveryTime: string;
  deliveryFee: number;
  minOrder: number;
  isOpen: boolean;
  tags: string[];
  distance: string;
  discount?: string;
}

interface CartItem {
  id: string;
  restaurantId: string;
  name: string;
  price: number;
  quantity: number;
  options: string[];
}

interface Order {
  id: string;
  restaurantName: string;
  items: CartItem[];
  totalPrice: number;
  status: 'preparing' | 'cooking' | 'delivering' | 'delivered' | 'cancelled';
  orderTime: string;
  deliveryTime?: string;
  address: string;
}

