import React, { useState, useEffect, useRef } from 'react';

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

interface User {
  id: string;
  name: string;
  avatar: string;
  level: number;
  points: number;
  streak: number;
  badges: string[];
  joinDate: string;
}

interface Notification {
  id: string;
  title: string;
  message: string;
  type: 'success' | 'info' | 'warning' | 'error';
  timestamp: number;
  read: boolean;
}

interface Restaurant {
  id: string;
  name: string;
  category: string;
  image: string;
  rating: number;
  deliveryTime: string;
  deliveryFee: number;
  minOrder: number;
  isOpen: boolean;
  tags: string[];
  distance: string;
  discount?: string;
}

interface CartItem {
  id: string;
  restaurantId: string;
  name: string;
  price: number;
  quantity: number;
  options: string[];
}

interface Order {
  id: string;
  restaurantName: string;
  items: CartItem[];
  totalPrice: number;
  status: 'preparing' | 'cooking' | 'delivering' | 'delivered' | 'cancelled';
  orderTime: string;
  deliveryTime?: string;
  address: string;
}

interface AppState {
  currentTab: 'home' | 'search' | 'favorites' | 'orders' | 'my';
  user: User;
  notifications: Notification[];
  restaurants: Restaurant[];
  favorites: string[];
  cart: CartItem[];
  orders: Order[];
  currentLocation: string;
  isLoading: boolean;
  showNotification: Notification | null;
  pullRefresh: { isRefreshing: boolean; pullDistance: number };
  searchQuery: string;
  selectedCategory: string;
}

export const MobileApp: React.FC<AppProps> = ({
  onAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const [appState, setAppState] = useState<AppState>({
    currentTab: 'home',
    user: {
      id: currentUser.user_id,
      name: 'ë°°ë‹¬ê³ ìˆ˜',
      avatar: 'ðŸ”',
      level: 5,
      points: 2450,
      streak: 7,
      badges: ['ðŸ”¥', 'â­', 'ðŸŽ¯', 'ðŸ’Ž'],
      joinDate: '2024-01-15'
    },
    notifications: [
      {
        id: '1',
        title: 'ë°°ë‹¬ ì™„ë£Œ!',
        message: 'ì¹˜í‚¨ì´ ì„±ê³µì ìœ¼ë¡œ ë°°ë‹¬ë˜ì—ˆìŠµë‹ˆë‹¤.',
        type: 'success',
        timestamp: Date.now() - 1800000,
        read: false
      },
      {
        id: '2',
        title: 'í• ì¸ ì¿ í° ë„ì°©!',
        message: '30% í• ì¸ ì¿ í°ì´ ë°œê¸‰ë˜ì—ˆìŠµë‹ˆë‹¤.',
        type: 'info',
        timestamp: Date.now() - 3600000,
        read: false
      }
    ],
    restaurants: [
      {
        id: 'rest1',
        name: 'ë§›ìžˆëŠ” ì¹˜í‚¨ì§‘',
        category: 'ì¹˜í‚¨',
        image: 'ðŸ—',
        rating: 4.8,
        deliveryTime: '25-35ë¶„',
        deliveryFee: 2500,
        minOrder: 15000,
        isOpen: true,
        tags: ['ì¸ê¸°', 'ë¹ ë¥¸ë°°ë‹¬'],
        distance: '0.8km',
        discount: '30% í• ì¸'
      },
      {
        id: 'rest2',
        name: 'í”¼ìžì™•êµ­',
        category: 'í”¼ìž',
