import React, { useState, useEffect } from 'react';
import { Cloud, Server, Building, Zap, Star, Trophy } from 'lucide-react';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';

interface Step1Props {
  onComplete: () => void;
  onVersionSelect: (version: 'saas' | 'private') => void;
  selectedVersion: 'saas' | 'private' | null;
}

export const Step1ProjectCreation: React.FC<Step1Props> = ({
  onComplete,
  onVersionSelect,
  selectedVersion
}) => {
  const [understood, setUnderstood] = useState(false);

  useEffect(() => {
    if (understood && selectedVersion) {
      onComplete();
    }
  }, [understood, selectedVersion, onComplete]);

  // 게이밍 테마 색상
  const theme = {
    background: 'radial-gradient(ellipse at top, #1e1b4b 0%, #0f0f23 50%, #000000 100%)',
    cardBg: 'linear-gradient(145deg, rgba(30, 30, 60, 0.9), rgba(15, 15, 35, 0.9))',
    cardBorder: 'rgba(139, 92, 246, 0.3)',
    text: '#ffffff',
    textSecondary: '#a1a1aa',
    accent: '#8b5cf6',
    accentSecondary: '#3b82f6'
  };

  return (
    <div style={{ color: theme.text }}>
      {/* 게이밍 스타일 Step Title */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          marginBottom: '20px',
          padding: '20px',
          background: 'rgba(139, 92, 246, 0.1)',
          borderRadius: '16px',
          border: `1px solid ${theme.cardBorder}`,
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* 배경 장식 */}
          <div style={{
            position: 'absolute',
            top: '-50%',
            right: '-20%',
            width: '100px',
            height: '100px',
            background: 'linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1))',
            borderRadius: '50%',
            filter: 'blur(30px)'
          }} />

          <div style={{
            width: '60px',
            height: '60px',
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            borderRadius: '18px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 8px 20px rgba(139, 92, 246, 0.4)',
            position: 'relative',
            zIndex: 1
          }}>
            <Zap size={28} color="white" />
          </div>
          <div style={{ position: 'relative', zIndex: 1 }}>
            <h3 style={{
              fontSize: '28px',
              fontWeight: '800',
              background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: 0,
              marginBottom: '8px'
            }}>
              미션 1: 프로젝트 생성 🚀
            </h3>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px'
            }}>
              <span style={{
                fontSize: '14px',
                color: theme.textSecondary,
                padding: '4px 12px',
                background: 'rgba(139, 92, 246, 0.2)',
                borderRadius: '12px',
                fontWeight: '600'
              }}>
                레벨: 초급자
              </span>
              <span style={{
                fontSize: '14px',
                color: '#f59e0b',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}>
                <Star size={16} />
                보상: +100 XP
              </span>
            </div>
          </div>
        </div>
        <p style={{
          fontSize: '18px',
          color: theme.textSecondary,
          lineHeight: '1.6',
          margin: 0,
          padding: '0 20px'
        }}>
          🎯 <strong>목표:</strong> 사용할 버전을 선택하고 프로젝트 생성 과정을 마스터하세요!
        </p>
      </div>

      {/* 게이밍 스타일 Version Selection */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '12px',
          marginBottom: '24px'
        }}>
          <Trophy size={24} style={{ color: '#f59e0b' }} />
          <h4 style={{
            fontSize: '24px',
            fontWeight: '700',
            color: theme.text,
            margin: 0
          }}>
            ⚔️ 버전 선택 챌린지
          </h4>
        </div>
        <p style={{
          fontSize: '16px',
          color: theme.textSecondary,
          marginBottom: '24px',
          paddingLeft: '36px'
        }}>
          당신의 모험에 맞는 버전을 선택하세요. 각 버전은 고유한 특성과 장점을 가지고 있습니다!
        </p>

        {/* 게이밍 스타일 Version Buttons */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '20px',
          marginBottom: '24px'
        }}>
          <div
            onClick={() => onVersionSelect('saas')}
            style={{
              padding: '24px',
              border: selectedVersion === 'saas' 
                ? '2px solid #8b5cf6' 
                : `1px solid ${theme.cardBorder}`,
              borderRadius: '20px',
              cursor: 'pointer',
              background: selectedVersion === 'saas' 
                ? 'linear-gradient(135deg, #8b5cf6, #3b82f6)' 
                : theme.cardBg,
              backdropFilter: 'blur(20px)',
              color: selectedVersion === 'saas' ? 'white' : theme.text,
              boxShadow: selectedVersion === 'saas' 
                ? '0 8px 30px rgba(139, 92, 246, 0.4)' 
                : '0 8px 24px rgba(0, 0, 0, 0.2)',
              transition: 'all 0.3s ease',
              transform: selectedVersion === 'saas' ? 'translateY(-8px) scale(1.02)' : 'none',
              position: 'relative',
              overflow: 'hidden'
            }}
            onMouseEnter={(e) => {
              if (selectedVersion !== 'saas') {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(-4px)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 12px 30px rgba(139, 92, 246, 0.2)';
              }
            }}
            onMouseLeave={(e) => {
              if (selectedVersion !== 'saas') {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(0)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 8px 24px rgba(0, 0, 0, 0.2)';
              }
            }}
          >
            {selectedVersion === 'saas' && (
              <div style={{
                position: 'absolute',
                top: '16px',
                right: '16px',
                background: 'linear-gradient(135deg, #f59e0b, #d97706)',
                color: 'white',
                padding: '4px 8px',
                borderRadius: '8px',
                fontSize: '12px',
                fontWeight: '600',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}>
                <Star size={12} />
                선택됨
              </div>
            )}

            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '16px',
              marginBottom: '16px'
            }}>
              <div style={{
                width: '64px',
                height: '64px',
                background: selectedVersion === 'saas' 
                  ? 'rgba(255, 255, 255, 0.2)' 
                  : 'linear-gradient(135deg, #06b6d4, #0891b2)',
                borderRadius: '18px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 8px 20px rgba(0, 0, 0, 0.2)'
              }}>
                <Cloud size={32} color="white" />
              </div>
              <div>
                <h5 style={{
                  fontSize: '20px',
                  fontWeight: '700',
                  margin: '0 0 4px 0',
                  color: selectedVersion === 'saas' ? 'white' : theme.text
                }}>
                  ☁️ SaaS 버전
                </h5>
                <p style={{
                  fontSize: '14px',
                  color: selectedVersion === 'saas' ? 'rgba(255,255,255,0.8)' : theme.textSecondary,
                  margin: 0
                }}>
                  클라우드 마스터 모드
                </p>
              </div>
            </div>
            <div style={{
              display: 'flex',
              gap: '8px',
              flexWrap: 'wrap'
            }}>
              {['빠른 설정', '자동 업데이트', '간편한 관리'].map((feature, index) => (
                <span key={index} style={{
                  fontSize: '12px',
                  padding: '4px 8px',
                  background: selectedVersion === 'saas' 
                    ? 'rgba(255, 255, 255, 0.2)' 
                    : 'rgba(139, 92, 246, 0.1)',
                  borderRadius: '8px',
                  color: selectedVersion === 'saas' ? 'white' : theme.textSecondary
                }}>
                  {feature}
                </span>
              ))}
            </div>
          </div>

          <div
            onClick={() => onVersionSelect('private')}
            style={{
              padding: '24px',
              border: selectedVersion === 'private' 
                ? '2px solid #10b981' 
                : `1px solid ${theme.cardBorder}`,
              borderRadius: '20px',
              cursor: 'pointer',
              background: selectedVersion === 'private' 
                ? 'linear-gradient(135deg, #10b981, #059669)' 
                : theme.cardBg,
              backdropFilter: 'blur(20px)',
              color: selectedVersion === 'private' ? 'white' : theme.text,
              boxShadow: selectedVersion === 'private' 
                ? '0 8px 30px rgba(16, 185, 129, 0.4)' 
                : '0 8px 24px rgba(0, 0, 0, 0.2)',
              transition: 'all 0.3s ease',
              transform: selectedVersion === 'private' ? 'translateY(-8px) scale(1.02)' : 'none',
              position: 'relative',
              overflow: 'hidden'
            }}
            onMouseEnter={(e) => {
              if (selectedVersion !== 'private') {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(-4px)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 12px 30px rgba(16, 185, 129, 0.2)';
              }
            }}
            onMouseLeave={(e) => {
              if (selectedVersion !== 'private') {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(0)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 8px 24px rgba(0, 0, 0, 0.2)';
              }
            }}
          >
            {selectedVersion === 'private' && (
              <div style={{
                position: 'absolute',
                top: '16px',
                right: '16px',
                background: 'linear-gradient(135deg, #f59e0b, #d97706)',
                color: 'white',
                padding: '4px 8px',
                borderRadius: '8px',
                fontSize: '12px',
                fontWeight: '600',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}>
                <Star size={12} />
                선택됨
              </div>
            )}

            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '16px',
              marginBottom: '16px'
            }}>
              <div style={{
                width: '64px',
                height: '64px',
                background: selectedVersion === 'private' 
                  ? 'rgba(255, 255, 255, 0.2)' 
                  : 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
                borderRadius: '18px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 8px 20px rgba(0, 0, 0, 0.2)'
              }}>
                <Server size={32} color="white" />
              </div>
              <div>
                <h5 style={{
                  fontSize: '20px',
                  fontWeight: '700',
                  margin: '0 0 4px 0',
                  color: selectedVersion === 'private' ? 'white' : theme.text
                }}>
                  🏰 프라이빗 버전
                </h5>
                <p style={{
                  fontSize: '14px',
                  color: selectedVersion === 'private' ? 'rgba(255,255,255,0.8)' : theme.textSecondary,
                  margin: 0
                }}>
                  엔터프라이즈 모드
                </p>
              </div>
            </div>
            <div style={{
              display: 'flex',
              gap: '8px',
              flexWrap: 'wrap'
            }}>
              {['완전한 제어', '커스텀 가능', '최고 보안'].map((feature, index) => (
                <span key={index} style={{
                  fontSize: '12px',
                  padding: '4px 8px',
                  background: selectedVersion === 'private' 
                    ? 'rgba(255, 255, 255, 0.2)' 
                    : 'rgba(16, 185, 129, 0.1)',
                  borderRadius: '8px',
                  color: selectedVersion === 'private' ? 'white' : theme.textSecondary
                }}>
                  {feature}
                </span>
              ))}
            </div>
          </div>
        </div>

        {/* Dynamic Content Based on Selection */}
        {selectedVersion && (
          <div style={{
            padding: '24px',
            background: theme.cardBg,
            border: `1px solid ${theme.cardBorder}`,
            borderRadius: '20px',
            marginBottom: '24px',
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
            backdropFilter: 'blur(20px)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            {/* 배경 장식 */}
            <div style={{
              position: 'absolute',
              bottom: '-30%',
              left: '-10%',
              width: '150px',
              height: '150px',
              background: selectedVersion === 'saas' 
                ? 'linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1))'
                : 'linear-gradient(135deg, rgba(16, 185, 129, 0.1), rgba(5, 150, 105, 0.1))',
              borderRadius: '50%',
              filter: 'blur(40px)'
            }} />

            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '16px',
              marginBottom: '20px',
              position: 'relative',
              zIndex: 1
            }}>
              <div style={{
                width: '40px',
                height: '40px',
                background: selectedVersion === 'saas' 
                  ? 'linear-gradient(135deg, #8b5cf6, #3b82f6)'
                  : 'linear-gradient(135deg, #10b981, #059669)',
                borderRadius: '12px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)'
              }}>
                {selectedVersion === 'saas' ? (
                  <Cloud size={20} color="white" />
                ) : (
                  <Server size={20} color="white" />
                )}
              </div>
              <h5 style={{
                fontSize: '20px',
                fontWeight: '700',
                margin: 0,
                color: theme.text
              }}>
                {selectedVersion === 'saas' ? '☁️ SaaS 버전 상세 정보' : '🏰 프라이빗 버전 상세 정보'}
              </h5>
            </div>

            <div style={{ marginBottom: '24px', position: 'relative', zIndex: 1 }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
                marginBottom: '12px'
              }}>
                <span style={{
                  fontSize: '14px',
                  fontWeight: '700',
                  color: '#10b981',
                  padding: '4px 8px',
                  background: 'rgba(16, 185, 129, 0.1)',
                  borderRadius: '8px'
                }}>
                  ✅ 장점
                </span>
              </div>
              <ul style={{
                margin: 0,
                paddingLeft: '20px',
                fontSize: '14px',
                color: theme.textSecondary,
                lineHeight: '1.6'
              }}>
                {selectedVersion === 'saas' ? (
                  <>
                    <li style={{ marginBottom: '8px' }}>💰 <strong>저렴한 초기 비용:</strong> 별도의 서버 구축 없이 월/연간 구독료로 즉시 사용 시작</li>
                    <li style={{ marginBottom: '8px' }}>🛠️ <strong>간편한 유지보수:</strong> 소프트웨어 업데이트, 서버 관리, 보안을 벤더가 담당</li>
                    <li style={{ marginBottom: '8px' }}>⚡ <strong>신속한 도입:</strong> 계정 전달 후 바로 사용 가능한 빠른 도입 속도</li>
                    <li style={{ marginBottom: '8px' }}>🔄 <strong>자동 업데이트:</strong> 새로운 기능과 보안 패치가 자동으로 적용</li>
                  </>
                ) : (
                  <>
                    <li style={{ marginBottom: '8px' }}>🔐 <strong>완벽한 데이터 주권:</strong> 모든 데이터를 기업 내부에서 완전히 통제하고 소유</li>
                    <li style={{ marginBottom: '8px' }}>🎨 <strong>무제한 커스터마이징:</strong> 비즈니스 특화된 독자적인 분석 지표와 기능 구현</li>
                    <li style={{ marginBottom: '8px' }}>🏛️ <strong>기술적 독립성:</strong> 특정 벤더 종속 없이 장기적으로 안정적인 데이터 자산 구축</li>
                    <li style={{ marginBottom: '8px' }}>🚄 <strong>고성능 환경:</strong> 내부 인프라 활용으로 대용량 데이터 안정 처리</li>
                  </>
                )}
              </ul>
            </div>

            <div style={{ position: 'relative', zIndex: 1 }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
