import React from 'react';

interface BadgeProps {
  children: React.ReactNode;
  variant?: 'primary' | 'secondary' | 'success' | 'warning' | 'danger' | 'info';
  size?: 'sm' | 'md';
  className?: string;
}

export const Badge: React.FC<BadgeProps> = ({
  children,
  variant = 'primary',
  size = 'sm',
  className = ''
}) => {
  const getVariantStyles = () => {
    switch (variant) {
      case 'primary':
        return {
          backgroundColor: 'var(--brand-primary)',
          color: 'white'
        };
      case 'secondary':
        return {
          backgroundColor: 'var(--gray-200)',
          color: 'var(--gray-800)'
        };
      case 'success':
        return {
          backgroundColor: 'var(--success)',
          color: 'white'
        };
      case 'warning':
        return {
          backgroundColor: 'var(--warning)',
          color: 'var(--gray-900)'
        };
      case 'danger':
        return {
          backgroundColor: 'var(--danger)',
          color: 'white'
        };
      case 'info':
        return {
          backgroundColor: 'var(--info)',
          color: 'white'
        };
      default:
        return {};
    }
  };

  const getSizeStyles = () => {
    switch (size) {
      case 'md':
        return {
          padding: '6px 12px',
          fontSize: '14px'
        };
      default:
        return {
          padding: '4px 8px',
          fontSize: '12px'
        };
    }
  };

  return (
    <span
      className={className}
      style={{
        ...getVariantStyles(),
        ...getSizeStyles(),
        borderRadius: '4px',
        fontWeight: '500',
        display: 'inline-flex',
        alignItems: 'center',
        gap: '4px'
      }}
    >
      {children}
    </span>
  );
};
