import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  clearedFloors: Set<number>; // Track which floors have been cleared
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
  isComingSoon?: boolean; // ì—…ë°ì´íŠ¸ ì˜ˆì • ë˜ì „ì¸ì§€
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  baseDamage: number;
  damageMultiplier: number; // ë ˆë²¨ë‹¹ ì¦ê°€ìœ¨
  statMultiplier: { attack?: number; defense?: number; hp?: number; mp?: number }; // ìŠ¤íƒ¯ ê¸°ë°˜ ë°ë¯¸ì§€ ë°°ìœ¨
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  category: 'combat' | 'magic' | 'support' | 'passive'; // ìŠ¤í‚¬ ë¶„ë¥˜
  description: string;
  requiredLevel?: number;
  isUnlocked?: boolean;
  isLearned?: boolean; // ë°°ìš´ ìŠ¤í‚¬ì¸ì§€ (0ë ˆë²¨ì—ì„œ 1ë ˆë²¨ë¡œ ì—…ê·¸ë ˆì´ë“œí–ˆëŠ”ì§€)
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet' | 'consumable';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // ë¬¼ì•½ìš© ì†ì„±ë“¤
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // ë²„í”„ ì§€ì†ì‹œê°„ (ë°€ë¦¬ì´ˆ)
    buffType?: 'attack' | 'defense' | 'speed'; // ë²„í”„ íƒ€ì…
    buffAmount?: number; // ë²„í”„ ìˆ˜ì¹˜
  };
  quantity?: number; // ì†Œë¹„ ì•„ì´í…œ ìˆ˜ëŸ‰
}



interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
