      combat: { appeared: 'ì´ ë‚˜íƒ€ë‚¬ë‹¤!' }
    };
    const monsters = [
      { name: gameTexts.monsters.slime, baseHp: 40, baseAttack: 8, baseDefense: 2, exp: 15, gold: 10 },
      { name: gameTexts.monsters.goblin, baseHp: 60, baseAttack: 12, baseDefense: 4, exp: 25, gold: 18 },
