  // ë˜ì „ ëª¬ìŠ¤í„° ìŠ¤í°
  const spawnDungeonMonster = (dungeon: Dungeon, forceSpawn = false) => {
    // ì´ë¯¸ ëª¬ìŠ¤í„°ê°€ ìˆìœ¼ë©´ ìƒˆë¡œ ìƒì„±í•˜ì§€ ì•ŠìŒ (ê°•ì œ ìƒì„±ì´ ì•„ë‹Œ ê²½ìš°)
    if (currentMonster && !forceSpawn) return;

    // ê°•ì œ ìƒì„±ì¸ ê²½ìš° ê¸°ì¡´ ëª¬ìŠ¤í„°ë¥¼ ë¨¼ì € ì œê±°
