      setCurrentMonster(null);
    }
    // ë˜ì „ë³„ ê³ ìœ  ëª¬ìŠ¤í„° íƒ€ì… ì •ì˜
    const getDungeonMonsters = (dungeonId: string) => {
      switch (dungeonId) {
