import React from 'react';

interface CardProps {
  children: React.ReactNode;
  className?: string;
  onClick?: () => void;
  selected?: boolean;
  style?: React.CSSProperties;
}

export const Card: React.FC<CardProps> = ({ 
  children, 
  className = '', 
  onClick, 
  selected = false,
  style = {}
}) => {
  return (
    <div 
      className={`card ${selected ? 'selected animate-bounce-in' : 'animate-fade-in hover-lift'} ${className}`}
      onClick={onClick}
      style={{
        background: selected 
          ? 'linear-gradient(135deg, var(--primary-50) 0%, var(--primary-100) 100%)'
          : 'var(--gray-50)',
        border: selected ? '2px solid var(--primary-500)' : '1px solid var(--gray-200)',
        borderRadius: 'var(--radius-xl)',
        padding: 'var(--space-6)',
        boxShadow: selected ? 'var(--shadow-lg)' : 'var(--shadow-sm)',
        cursor: onClick ? 'pointer' : 'default',
        transition: 'var(--transition-fast)',
        backdropFilter: 'blur(8px)',
        transform: selected ? 'translateY(-2px)' : 'none',
        ...style
      }}
    >
      {children}
    </div>
  );
};
