import React, { useState, useEffect } from 'react';
import { Cloud, Server, Building, Zap, Star, Trophy } from 'lucide-react';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { useLanguage } from '../../contexts/LanguageContext';

interface Step1Props {
  onComplete: () => void;
  onVersionSelect: (version: 'saas' | 'private') => void;
  selectedVersion: 'saas' | 'private' | null;
}

export const Step1ProjectCreation: React.FC<Step1Props> = ({
  onComplete,
  onVersionSelect,
  selectedVersion
}) => {
  const { t } = useLanguage();
  const [understood, setUnderstood] = useState(false);

  useEffect(() => {
    if (understood && selectedVersion) {
      onComplete();
    }
  }, [understood, selectedVersion, onComplete]);

  // Í≤åÏù¥Î∞ç ÌÖåÎßà ÏÉâÏÉÅ
  const theme = {
    background: 'radial-gradient(ellipse at top, #1e1b4b 0%, #0f0f23 50%, #000000 100%)',
    cardBg: 'linear-gradient(145deg, rgba(30, 30, 60, 0.9), rgba(15, 15, 35, 0.9))',
    cardBorder: 'rgba(139, 92, 246, 0.3)',
    text: '#ffffff',
    textSecondary: '#a1a1aa',
    accent: '#8b5cf6',
    accentSecondary: '#3b82f6'
  };

  return (
    <div style={{ color: theme.text }}>
      {/* Í≤åÏù¥Î∞ç Ïä§ÌÉÄÏùº Step Title */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          marginBottom: '20px',
          padding: '20px',
          background: 'rgba(139, 92, 246, 0.1)',
          borderRadius: '16px',
          border: `1px solid ${theme.cardBorder}`,
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* Î∞∞Í≤Ω Ïû•Ïãù */}
          <div style={{
            position: 'absolute',
            top: '-50%',
            right: '-20%',
            width: '100px',
            height: '100px',
            background: 'linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1))',
            borderRadius: '50%',
            filter: 'blur(30px)'
          }} />

          <div style={{
            width: '60px',
            height: '60px',
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            borderRadius: '18px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 8px 20px rgba(139, 92, 246, 0.4)',
            position: 'relative',
            zIndex: 1
          }}>
            <Zap size={28} color="white" />
          </div>
          <div style={{ position: 'relative', zIndex: 1 }}>
            <h3 style={{
              fontSize: '28px',
              fontWeight: '800',
              background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: 0,
              marginBottom: '8px'
            }}>
{t('step1.mission')} üöÄ
            </h3>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px'
            }}>
              <span style={{
                fontSize: '14px',
                color: theme.textSecondary,
                padding: '4px 12px',
                background: 'rgba(139, 92, 246, 0.2)',
                borderRadius: '12px',
                fontWeight: '600'
              }}>
{t('step1.questType')}: {t('quickstart.currentLevel').includes('Îã®Í≥Ñ') ? 'Ï¥àÍ∏âÏûê' : 'Beginner'}
              </span>
              <span style={{
                fontSize: '14px',
                color: '#f59e0b',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}>
                <Star size={16} />
{t('step1.reward')}
              </span>
            </div>
          </div>
        </div>
        <p style={{
          fontSize: '18px',
          color: theme.textSecondary,
          lineHeight: '1.6',
          margin: 0,
          padding: '0 20px'
        }}>
          {t('step1.warning')}
        </p>
      </div>

      {/* Í≤åÏù¥Î∞ç Ïä§ÌÉÄÏùº Version Selection */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '12px',
          marginBottom: '24px'
        }}>
          <Trophy size={24} style={{ color: '#f59e0b' }} />
          <h4 style={{
            fontSize: '24px',
            fontWeight: '700',
            color: theme.text,
            margin: 0
          }}>
            ‚öîÔ∏è Î≤ÑÏ†Ñ ÏÑ†ÌÉù Ï±åÎ¶∞ÏßÄ
          </h4>
        </div>
        <p style={{
          fontSize: '16px',
          color: theme.textSecondary,
          marginBottom: '24px',
          paddingLeft: '36px'
        }}>
          ÎãπÏã†Ïùò Î™®ÌóòÏóê ÎßûÎäî Î≤ÑÏ†ÑÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî. Í∞Å Î≤ÑÏ†ÑÏùÄ Í≥†Ïú†Ìïú ÌäπÏÑ±Í≥º Ïû•Ï†êÏùÑ Í∞ÄÏßÄÍ≥† ÏûàÏäµÎãàÎã§!
        </p>

        {/* Í≤åÏù¥Î∞ç Ïä§ÌÉÄÏùº Version Buttons */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '20px',
          marginBottom: '24px'
        }}>
          <div
            onClick={() => onVersionSelect('saas')}
            style={{
              padding: '24px',
              border: selectedVersion === 'saas' 
                ? '2px solid #8b5cf6' 
                : `1px solid ${theme.cardBorder}`,
              borderRadius: '20px',
              cursor: 'pointer',
              background: selectedVersion === 'saas' 
                ? 'linear-gradient(135deg, #8b5cf6, #3b82f6)' 
                : theme.cardBg,
              backdropFilter: 'blur(20px)',
              color: selectedVersion === 'saas' ? 'white' : theme.text,
              boxShadow: selectedVersion === 'saas' 
                ? '0 8px 30px rgba(139, 92, 246, 0.4)' 
                : '0 8px 24px rgba(0, 0, 0, 0.2)',
              transition: 'all 0.3s ease',
              transform: selectedVersion === 'saas' ? 'translateY(-8px) scale(1.02)' : 'none',
              position: 'relative',
              overflow: 'hidden'
            }}
            onMouseEnter={(e) => {
              if (selectedVersion !== 'saas') {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(-4px)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 12px 30px rgba(139, 92, 246, 0.2)';
              }
            }}
            onMouseLeave={(e) => {
              if (selectedVersion !== 'saas') {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(0)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 8px 24px rgba(0, 0, 0, 0.2)';
              }
            }}
          >
            {selectedVersion === 'saas' && (
              <div style={{
                position: 'absolute',
                top: '16px',
                right: '16px',
                background: 'linear-gradient(135deg, #f59e0b, #d97706)',
                color: 'white',
                padding: '4px 8px',
                borderRadius: '8px',
                fontSize: '12px',
                fontWeight: '600',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}>
                <Star size={12} />
                ÏÑ†ÌÉùÎê®
              </div>
            )}

            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '16px',
              marginBottom: '16px'
            }}>
              <div style={{
                width: '64px',
                height: '64px',
                background: selectedVersion === 'saas' 
                  ? 'rgba(255, 255, 255, 0.2)' 
                  : 'linear-gradient(135deg, #06b6d4, #0891b2)',
                borderRadius: '18px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 8px 20px rgba(0, 0, 0, 0.2)'
              }}>
                <Cloud size={32} color="white" />
              </div>
              <div>
                <h5 style={{
                  fontSize: '20px',
                  fontWeight: '700',
                  margin: '0 0 4px 0',
                  color: selectedVersion === 'saas' ? 'white' : theme.text
                }}>
                  ‚òÅÔ∏è {t('step1.saasTitle')}
                </h5>
                <p style={{
                  fontSize: '14px',
                  color: selectedVersion === 'saas' ? 'rgba(255,255,255,0.8)' : theme.textSecondary,
                  margin: 0
                }}>
{t('step1.saasSubtitle')}
                </p>
              </div>
            </div>
            <div style={{
              display: 'flex',
              gap: '8px',
              flexWrap: 'wrap'
            }}>
              {['Îπ†Î•∏ ÏÑ§Ï†ï', 'ÏûêÎèô ÏóÖÎç∞Ïù¥Ìä∏', 'Í∞ÑÌé∏Ìïú Í¥ÄÎ¶¨'].map((feature, index) => (
                <span key={index} style={{
                  fontSize: '12px',
                  padding: '4px 8px',
                  background: selectedVersion === 'saas' 
                    ? 'rgba(255, 255, 255, 0.2)' 
                    : 'rgba(139, 92, 246, 0.1)',
                  borderRadius: '8px',
                  color: selectedVersion === 'saas' ? 'white' : theme.textSecondary
                }}>
                  {feature}
                </span>
              ))}
            </div>
          </div>

          <div
            onClick={() => onVersionSelect('private')}
            style={{
              padding: '24px',
              border: selectedVersion === 'private' 
                ? '2px solid #10b981' 
                : `1px solid ${theme.cardBorder}`,
              borderRadius: '20px',
              cursor: 'pointer',
              background: selectedVersion === 'private' 
                ? 'linear-gradient(135deg, #10b981, #059669)' 
                : theme.cardBg,
              backdropFilter: 'blur(20px)',
              color: selectedVersion === 'private' ? 'white' : theme.text,
              boxShadow: selectedVersion === 'private' 
                ? '0 8px 30px rgba(16, 185, 129, 0.4)' 
                : '0 8px 24px rgba(0, 0, 0, 0.2)',
              transition: 'all 0.3s ease',
              transform: selectedVersion === 'private' ? 'translateY(-8px) scale(1.02)' : 'none',
              position: 'relative',
              overflow: 'hidden'
            }}
            onMouseEnter={(e) => {
              if (selectedVersion !== 'private') {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(-4px)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 12px 30px rgba(16, 185, 129, 0.2)';
              }
            }}
            onMouseLeave={(e) => {
              if (selectedVersion !== 'private') {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(0)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 8px 24px rgba(0, 0, 0, 0.2)';
              }
            }}
          >
            {selectedVersion === 'private' && (
              <div style={{
                position: 'absolute',
                top: '16px',
                right: '16px',
                background: 'linear-gradient(135deg, #f59e0b, #d97706)',
                color: 'white',
                padding: '4px 8px',
                borderRadius: '8px',
                fontSize: '12px',
                fontWeight: '600',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}>
                <Star size={12} />
                ÏÑ†ÌÉùÎê®
              </div>
            )}

            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '16px',
              marginBottom: '16px'
            }}>
              <div style={{
                width: '64px',
                height: '64px',
                background: selectedVersion === 'private' 
                  ? 'rgba(255, 255, 255, 0.2)' 
                  : 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
                borderRadius: '18px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 8px 20px rgba(0, 0, 0, 0.2)'
              }}>
                <Server size={32} color="white" />
              </div>
              <div>
                <h5 style={{
                  fontSize: '20px',
                  fontWeight: '700',
                  margin: '0 0 4px 0',
                  color: selectedVersion === 'private' ? 'white' : theme.text
                }}>
                  üè∞ {t('step1.privateTitle')}
                </h5>
                <p style={{
                  fontSize: '14px',
                  color: selectedVersion === 'private' ? 'rgba(255,255,255,0.8)' : theme.textSecondary,
                  margin: 0
                }}>
{t('step1.privateSubtitle')}
                </p>
              </div>
            </div>
            <div style={{
              display: 'flex',
              gap: '8px',
              flexWrap: 'wrap'
            }}>
              {['ÏôÑÏ†ÑÌïú Ï†úÏñ¥', 'Ïª§Ïä§ÌÖÄ Í∞ÄÎä•', 'ÏµúÍ≥† Î≥¥Ïïà'].map((feature, index) => (
                <span key={index} style={{
                  fontSize: '12px',
                  padding: '4px 8px',
                  background: selectedVersion === 'private' 
                    ? 'rgba(255, 255, 255, 0.2)' 
                    : 'rgba(16, 185, 129, 0.1)',
                  borderRadius: '8px',
                  color: selectedVersion === 'private' ? 'white' : theme.textSecondary
                }}>
                  {feature}
                </span>
              ))}
            </div>
          </div>
        </div>

        {/* Dynamic Content Based on Selection */}
        {selectedVersion && (
          <div style={{
            padding: '24px',
            background: theme.cardBg,
            border: `1px solid ${theme.cardBorder}`,
            borderRadius: '20px',
            marginBottom: '24px',
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
            backdropFilter: 'blur(20px)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            {/* Î∞∞Í≤Ω Ïû•Ïãù */}
            <div style={{
              position: 'absolute',
              bottom: '-30%',
              left: '-10%',
              width: '150px',
              height: '150px',
              background: selectedVersion === 'saas' 
                ? 'linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1))'
                : 'linear-gradient(135deg, rgba(16, 185, 129, 0.1), rgba(5, 150, 105, 0.1))',
              borderRadius: '50%',
              filter: 'blur(40px)'
            }} />

            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '16px',
              marginBottom: '20px',
              position: 'relative',
              zIndex: 1
            }}>
              <div style={{
                width: '40px',
                height: '40px',
                background: selectedVersion === 'saas' 
                  ? 'linear-gradient(135deg, #8b5cf6, #3b82f6)'
                  : 'linear-gradient(135deg, #10b981, #059669)',
                borderRadius: '12px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)'
              }}>
                {selectedVersion === 'saas' ? (
                  <Cloud size={20} color="white" />
                ) : (
                  <Server size={20} color="white" />
                )}
              </div>
              <h5 style={{
                fontSize: '20px',
                fontWeight: '700',
                margin: 0,
                color: theme.text
              }}>
                {selectedVersion === 'saas' ? '‚òÅÔ∏è SaaS Î≤ÑÏ†Ñ ÏÉÅÏÑ∏ Ï†ïÎ≥¥' : 'üè∞ ÌîÑÎùºÏù¥Îπó Î≤ÑÏ†Ñ ÏÉÅÏÑ∏ Ï†ïÎ≥¥'}
              </h5>
            </div>

            <div style={{ marginBottom: '24px', position: 'relative', zIndex: 1 }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
                marginBottom: '12px'
              }}>
                <span style={{
                  fontSize: '14px',
                  fontWeight: '700',
                  color: '#10b981',
                  padding: '4px 8px',
                  background: 'rgba(16, 185, 129, 0.1)',
                  borderRadius: '8px'
                }}>
                  ‚úÖ Ïû•Ï†ê
                </span>
              </div>
              <ul style={{
                margin: 0,
                paddingLeft: '20px',
                fontSize: '14px',
                color: theme.textSecondary,
                lineHeight: '1.6'
              }}>
                {selectedVersion === 'saas' ? (
                  <>
                    <li style={{ marginBottom: '8px' }}>üí∞ <strong>Ï†ÄÎ†¥Ìïú Ï¥àÍ∏∞ ÎπÑÏö©:</strong> Î≥ÑÎèÑÏùò ÏÑúÎ≤Ñ Íµ¨Ï∂ï ÏóÜÏù¥ Ïõî/Ïó∞Í∞Ñ Íµ¨ÎèÖÎ£åÎ°ú Ï¶âÏãú ÏÇ¨Ïö© ÏãúÏûë</li>
                    <li style={{ marginBottom: '8px' }}>üõ†Ô∏è <strong>Í∞ÑÌé∏Ìïú Ïú†ÏßÄÎ≥¥Ïàò:</strong> ÏÜåÌîÑÌä∏Ïõ®Ïñ¥ ÏóÖÎç∞Ïù¥Ìä∏, ÏÑúÎ≤Ñ Í¥ÄÎ¶¨, Î≥¥ÏïàÏùÑ Î≤§ÎçîÍ∞Ä Îã¥Îãπ</li>
                    <li style={{ marginBottom: '8px' }}>‚ö° <strong>Ïã†ÏÜçÌïú ÎèÑÏûÖ:</strong> Í≥ÑÏ†ï Ï†ÑÎã¨ ÌõÑ Î∞îÎ°ú ÏÇ¨Ïö© Í∞ÄÎä•Ìïú Îπ†Î•∏ ÎèÑÏûÖ ÏÜçÎèÑ</li>
                    <li style={{ marginBottom: '8px' }}>üîÑ <strong>ÏûêÎèô ÏóÖÎç∞Ïù¥Ìä∏:</strong> ÏÉàÎ°úÏö¥ Í∏∞Îä•Í≥º Î≥¥Ïïà Ìå®ÏπòÍ∞Ä ÏûêÎèôÏúºÎ°ú Ï†ÅÏö©</li>
                  </>
                ) : (
                  <>
                    <li style={{ marginBottom: '8px' }}>üîê <strong>ÏôÑÎ≤ΩÌïú Îç∞Ïù¥ÌÑ∞ Ï£ºÍ∂å:</strong> Î™®Îì† Îç∞Ïù¥ÌÑ∞Î•º Í∏∞ÏóÖ ÎÇ¥Î∂ÄÏóêÏÑú ÏôÑÏ†ÑÌûà ÌÜµÏ†úÌïòÍ≥† ÏÜåÏú†</li>
