            </div>
          </div>
        </div>

        {/* Step Progress */}
        <StepProgress
          steps={steps}
          currentStep={currentStep}
          onStepChange={handleStepChange}
        />

        {/* 게이밍 스타일 Step Content */}
        <div
          style={{
            background: theme.cardBg,
            backdropFilter: "blur(20px)",
            borderRadius: "24px",
            border: `1px solid ${theme.cardBorder}`,
            boxShadow: "0 20px 40px rgba(0, 0, 0, 0.3)",
            padding: "40px",
            minHeight: "600px",
            position: "relative",
            overflow: "hidden",
            zIndex: 20,
            marginTop: "24px",
          }}
        >
          {/* 배경 장식 */}
          <div
            style={{
              position: "absolute",
              top: "-20%",
              right: "-10%",
              width: "300px",
              height: "300px",
              background:
                "linear-gradient(135deg, rgba(139, 92, 246, 0.05), rgba(59, 130, 246, 0.05))",
              borderRadius: "50%",
              filter: "blur(60px)",
              zIndex: 0,
            }}
          />

          <div style={{ position: "relative", zIndex: 1 }}>
            {renderCurrentStep()}
          </div>
        </div>

        {/* 게이밍 스타일 Step Navigation Buttons */}
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            gap: "20px",
            padding: "24px",
            background: theme.cardBg,
            borderRadius: "20px",
            backdropFilter: "blur(20px)",
            border: `1px solid ${theme.cardBorder}`,
            marginTop: "24px",
            boxShadow: "0 10px 30px rgba(0, 0, 0, 0.2)",
          }}
        >
          <button
            onClick={handlePrev}
            disabled={!canGoPrev}
            style={{
              display: "flex",
              alignItems: "center",
              gap: "8px",
              padding: "12px 24px",
              background: canGoPrev
                ? "rgba(139, 92, 246, 0.1)"
                : "rgba(139, 92, 246, 0.05)",
              border: `1px solid ${
                canGoPrev ? theme.cardBorder : "rgba(139, 92, 246, 0.1)"
              }`,
              borderRadius: "12px",
              color: canGoPrev ? theme.text : theme.textSecondary,
              fontSize: "14px",
              fontWeight: "600",
              cursor: canGoPrev ? "pointer" : "not-allowed",
              transition: "all 0.3s ease",
              minWidth: "120px",
            }}
          >
            ← 이전
          </button>

          {/* 진행률 표시 */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: "8px",
            }}
          >
            {steps.map((step) => (
              <div
                key={step.id}
                style={{
                  width: "12px",
                  height: "12px",
                  borderRadius: "50%",
                  background: step.completed
                    ? "linear-gradient(135deg, #10b981, #059669)"
                    : currentStep === step.id
                    ? "linear-gradient(135deg, #8b5cf6, #3b82f6)"
                    : "rgba(139, 92, 246, 0.2)",
                  boxShadow:
                    step.completed || currentStep === step.id
                      ? "0 0 10px rgba(139, 92, 246, 0.5)"
                      : "none",
                  transition: "all 0.3s ease",
                }}
              />
            ))}
          </div>

          {currentStep === 5 ? (
            <button
              onClick={() => (window.location.hash = "")}
              style={{
                display: "flex",
                alignItems: "center",
                gap: "8px",
                padding: "12px 24px",
                background: "linear-gradient(135deg, #f59e0b, #d97706)",
                border: "none",
                borderRadius: "12px",
                color: "white",
                fontSize: "14px",
                fontWeight: "600",
                cursor: "pointer",
                transition: "all 0.3s ease",
                minWidth: "120px",
                boxShadow: "0 4px 15px rgba(245, 158, 11, 0.4)",
              }}
            >
              🏠 홈으로
            </button>
          ) : (
            <button
              onClick={handleNext}
              disabled={!canGoNext}
              style={{
                display: "flex",
                alignItems: "center",
                gap: "8px",
                padding: "12px 24px",
                background: canGoNext
                  ? "linear-gradient(135deg, #8b5cf6, #3b82f6)"
                  : "rgba(139, 92, 246, 0.1)",
                border: "none",
                borderRadius: "12px",
                color: canGoNext ? "white" : theme.textSecondary,
                fontSize: "14px",
                fontWeight: "600",
                cursor: canGoNext ? "pointer" : "not-allowed",
                transition: "all 0.3s ease",
                minWidth: "120px",
                boxShadow: canGoNext
                  ? "0 4px 15px rgba(139, 92, 246, 0.4)"
                  : "none",
              }}
            >
              다음 →
            </button>
          )}
        </div>
      </div>
    </Layout>
  );
};
