import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  clearedFloors: Set<number>; // Track which floors have been cleared
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  damage: number;
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  description: string;
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet' | 'consumable';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // 물약용 속성들
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // 버프 지속시간 (밀리초)
    buffType?: 'attack' | 'defense' | 'speed'; // 버프 타입
    buffAmount?: number; // 버프 수치
  };
  quantity?: number; // 소비 아이템 수량
}



interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const { colors } = useTheme();
  const { t } = useLanguage();

  // CSS 애니메이션 추가
  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes progress {
        0% {
          width: 0%;
        }
        100% {
          width: 100%;
        }
      }
      @keyframes pulse {
        0%, 100% {
          opacity: 1;
        }
        50% {
          opacity: 0.7;
        }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
