    const itemNames = {
      weapon: ['검', '도끼', '창', '활', '지팡이'],
      armor: ['갑옷', '로브', '사슬갑옷', '가죽갑옷'],
      helmet: ['투구', '모자', '관', '헬름'],
      boots: ['신발', '부츠', '장화', '샌들'],
      ring: ['반지', '링', '밴드'],
      amulet: ['목걸이', '부적', '펜던트']
    };

    const prefix = prefixes[rarity as keyof typeof prefixes][Math.floor(Math.random() * 3)];
    const itemTypeNames = itemNames[itemType as keyof typeof itemNames] || ['아이템'];
    const item = itemTypeNames[Math.floor(Math.random() * itemTypeNames.length)];

    return `${prefix} ${item}`;
  };

  const generateItemStats = (rarity: string) => {
    const multipliers = {
      common: 1,
      rare: 1.5,
      epic: 2.5,
      legendary: 4,
      mythic: 7
    };

    const base = multipliers[rarity as keyof typeof multipliers];
    return {
      attack: Math.floor(Math.random() * 20 * base),
      defense: Math.floor(Math.random() * 15 * base),
      hp: Math.floor(Math.random() * 50 * base),
      mp: Math.floor(Math.random() * 30 * base),
      speed: Math.floor(Math.random() * 10 * base),
      critRate: Math.floor(Math.random() * 10 * base),
      critDamage: Math.floor(Math.random() * 20 * base)
