import React, { useState, useEffect } from 'react';
import { CodeBlock } from './CodeBlock';

interface CodeTabItem {
  id: string;
  label: string;
  language: string;
  code: string;
}

interface CodeTabsProps {
  tabs: CodeTabItem[];
  title?: string;
}

export const CodeTabs: React.FC<CodeTabsProps> = ({ tabs, title }) => {
  const [activeTab, setActiveTab] = useState(() => tabs[0]?.id || '');
  const [hoveredTab, setHoveredTab] = useState<string | null>(null);

  // Update activeTab if tabs change and current activeTab doesn't exist
  useEffect(() => {
    if (tabs && tabs.length > 0) {
      const currentTabExists = tabs.find(tab => tab.id === activeTab);
      if (!currentTabExists) {
        console.log('Setting active tab to:', tabs[0].id);
        setActiveTab(tabs[0].id);
      }
    }
  }, [tabs, activeTab]);

  const activeTabData = tabs.find(tab => tab.id === activeTab);

  console.log('CodeTabs render - activeTab:', activeTab, 'tabs:', tabs.map(t => t.id), 'activeTabData:', activeTabData);

  return (
    <div style={{ marginBottom: 'var(--space-4)' }} className="animate-fade-in">
      {title && (
        <h6 style={{ 
          fontSize: 'var(--font-size-sm)', 
          fontWeight: '600', 
          marginBottom: 'var(--space-2)', 
          color: 'var(--gray-900)',
          background: 'var(--gradient-text)'
        }} className="gradient-text">
          {title}
        </h6>
      )}

      <div style={{
        display: 'flex',
        borderBottom: '2px solid var(--gray-200)',
        marginBottom: 'var(--space-3)',
        background: 'var(--glass-bg)',
        borderRadius: 'var(--radius-lg) var(--radius-lg) 0 0',
        backdropFilter: 'var(--glass-backdrop)',
        padding: 'var(--space-1)'
      }} className="glass-card">
        {tabs.map(tab => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            onMouseEnter={() => setHoveredTab(tab.id)}
            onMouseLeave={() => setHoveredTab(null)}
            style={{
              padding: 'var(--space-2) var(--space-4)',
              border: 'none',
              background: activeTab === tab.id 
                ? 'var(--gradient-primary)' 
                : hoveredTab === tab.id 
                  ? 'var(--primary-50)' 
                  : 'transparent',
              color: activeTab === tab.id ? 'white' : 'var(--gray-600)',
              fontSize: 'var(--font-size-sm)',
              fontWeight: activeTab === tab.id ? '600' : '500',
              cursor: 'pointer',
              borderRadius: 'var(--radius-md)',
              transition: 'var(--transition-fast)',
              boxShadow: activeTab === tab.id ? 'var(--shadow-md)' : 'none',
              transform: activeTab === tab.id ? 'translateY(-1px)' : 'none'
            }}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {(activeTabData || tabs[0]) && (
        <CodeBlock
          language={(activeTabData || tabs[0]).language}
          code={(activeTabData || tabs[0]).code}
        />
      )}
    </div>
  );
};
