import React, { useState } from 'react';
import { Zap, Database, BarChart, Users, Shield, Rocket, Star, Trophy, Activity, TrendingUp, Settings, Play } from 'lucide-react';
import { Layout } from '../components/Layout';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';

type TabType = 'overview' | 'system' | 'data-collection' | 'data-models';

export const IntroPage: React.FC = () => {
  const { t } = useLanguage();
  const { colors } = useTheme();
  const [activeTab, setActiveTab] = useState<TabType>('overview');

  // 데이터 수집 시뮬레이터 상태
  const [isSimulating, setIsSimulating] = useState(false);
  const [userLogs, setUserLogs] = useState<any[]>([]);
  const [eventLogs, setEventLogs] = useState<any[]>([]);
  const [currentAction, setCurrentAction] = useState<string>('');
  const [logCount, setLogCount] = useState(0);

  // 테마 색상 가져오기
  const theme = colors;

  const tabs = [
    {
      id: 'overview' as TabType,
      title: '기본 소개',
      icon: <Shield size={20} />,
      color: '#8b5cf6'
    },
    {
      id: 'system' as TabType,
      title: '시스템 구조',
      icon: <Settings size={20} />,
      color: '#3b82f6'
    },
    {
      id: 'data-collection' as TabType,
      title: '데이터 수집의 이해',
      icon: <Activity size={20} />,
      color: '#10b981'
    },
    {
      id: 'data-models' as TabType,
      title: '데이터 모델의 활용',
      icon: <TrendingUp size={20} />,
      color: '#f59e0b'
    }
  ];

  const renderOverviewTab = () => (
    <div>
      {/* 게이밍 스타일 헤더 */}
      <div style={{ 
        background: theme.cardBg,
        backdropFilter: 'blur(20px)',
        borderRadius: '24px',
        padding: '48px',
        marginBottom: '32px',
        border: `1px solid ${theme.cardBorder}`,
        boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)',
        position: 'relative',
        overflow: 'hidden',
        textAlign: 'center'
      }}>
        {/* 배경 장식 */}
        <div style={{
          position: 'absolute',
          top: '-30%',
          left: '-10%',
          width: '150px',
          height: '150px',
          background: 'linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1))',
          borderRadius: '50%',
          filter: 'blur(40px)'
        }} />

        <div style={{ position: 'relative', zIndex: 1 }}>
          <div style={{
            width: '80px',
            height: '80px',
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            borderRadius: '20px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            margin: '0 auto 24px',
            boxShadow: '0 12px 30px rgba(139, 92, 246, 0.4)'
          }}>
            <Zap size={40} color="white" />
          </div>

          <h1 style={{ 
            fontSize: '42px', 
            fontWeight: '800', 
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            margin: '0 0 16px 0',
            lineHeight: '1.2'
          }}>
            Thinking Engine
          </h1>

          <p style={{ 
            fontSize: '20px', 
            color: theme.textSecondary,
            lineHeight: '1.6',
            maxWidth: '800px',
            margin: '0 auto 32px'
          }}>
            씽킹데이터(ThinkingData)가 개발한 '씽킹 엔진(Thinking Engine)'은 사용자 행동 데이터를 기반으로 심층적인 분석과 실행 가능한 인사이트를 제공하는 원스톱 데이터 플랫폼입니다.
          </p>

          {/* 주요 통계 */}
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '48px',
            marginTop: '32px',
            flexWrap: 'wrap'
          }}>
            {[
              { label: '고객사', value: '1,500+', color: '#10b981', icon: <Users size={20} /> },
              { label: '연결된 프로덕트', value: '8,000+', color: '#8b5cf6', icon: <Database size={20} /> },
              { label: '고객 만족도', value: '98%', color: '#f59e0b', icon: <Star size={20} /> }
            ].map((stat, index) => (
              <div key={index} style={{ textAlign: 'center' }}>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '8px',
                  marginBottom: '8px',
                  color: stat.color
                }}>
                  {stat.icon}
                  <div style={{
                    fontSize: '28px',
                    fontWeight: '800',
                    color: stat.color,
                    textShadow: `0 0 10px ${stat.color}40`
                  }}>
                    {stat.value}
                  </div>
                </div>
                <div style={{
                  fontSize: '14px',
                  color: theme.textSecondary,
                  fontWeight: '500'
                }}>
                  {stat.label}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* 핵심 기능 */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
        gap: '24px',
        marginBottom: '32px'
      }}>
        {[
          {
            icon: <BarChart size={32} />,
            title: '다각적인 데이터 분석',
            description: '퍼널 분석, 리텐션 분석, 경로 분석, 코호트 분석, LTV 분석 등 13가지 이상의 다채로운 분석 모델을 제공합니다.',
            color: '#3b82f6',
            gradient: 'linear-gradient(135deg, #3b82f6, #1d4ed8)'
          },
          {
            icon: <Activity size={32} />,
            title: '실시간 데이터 처리',
            description: '사용자의 행동 데이터를 실시간으로 수집하고 분석하여 급변하는 시장 상황과 사용자 반응에 신속하게 대응할 수 있습니다.',
            color: '#10b981',
            gradient: 'linear-gradient(135deg, #10b981, #059669)'
          },
          {
            icon: <Users size={32} />,
            title: '노코드 분석 환경',
            description: '개발자나 데이터 분석가의 도움 없이도 현업 담당자가 직접 데이터를 분석하고 인사이트를 도출할 수 있습니다.',
            color: '#f59e0b',
            gradient: 'linear-gradient(135deg, #f59e0b, #d97706)'
          }
        ].map((feature, index) => (
          <div key={index} style={{
            background: theme.cardBg,
            borderRadius: '20px',
            padding: '32px',
            border: `1px solid ${theme.cardBorder}`,
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
            backdropFilter: 'blur(20px)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            <div style={{
              width: '64px',
              height: '64px',
              background: feature.gradient,
              borderRadius: '16px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              marginBottom: '20px',
              boxShadow: `0 8px 20px ${feature.color}40`
            }}>
              <div style={{ color: 'white' }}>
                {feature.icon}
              </div>
            </div>

            <h3 style={{
              fontSize: '20px',
              fontWeight: '700',
              color: theme.text,
              margin: '0 0 12px 0'
            }}>
              {feature.title}
            </h3>

            <p style={{
              fontSize: '14px',
              color: theme.textSecondary,
              lineHeight: '1.6',
              margin: 0
            }}>
              {feature.description}
            </p>
          </div>
        ))}
      </div>

      {/* 활용 분야 */}
      <div style={{
        background: theme.cardBg,
        borderRadius: '24px',
        padding: '48px',
        border: `1px solid ${theme.cardBorder}`,
        boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)',
        backdropFilter: 'blur(20px)'
      }}>
        <h2 style={{
          fontSize: '32px',
          fontWeight: '700',
          color: theme.text,
          margin: '0 0 32px 0',
          textAlign: 'center'
        }}>
          주요 활용 분야
        </h2>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '24px'
        }}>
          {[
            {
              title: '🎮 게임',
              description: '사용자 이탈 예측 및 방지, 핵심 유료화 모델 강화, 콘텐츠 및 밸런스 업데이트 방향성 수립을 통해 게임의 라이프 사이클을 연장하고 수익성을 극대화합니다.',
              example: '궁수의 전설 - 리텐션 33% 향상, LTV 90% 증가'
            },
            {
              title: '🛒 이커머스',
              description: '구매 전환율이 높은 사용자의 행동 패턴을 분석하여 구매를 망설이는 고객에게 개인화된 추천 및 프로모션을 제공함으로써 매출 증대를 꾀할 수 있습니다.',
              example: '개인화 추천을 통한 전환율 향상'
            },
            {
              title: '📺 미디어',
              description: '사용자의 콘텐츠 소비 패턴, 시청 시간, 이탈 지점 등을 분석하여 개인화된 콘텐츠를 추천하고 사용자의 몰입도와 만족도를 높일 수 있습니다.',
              example: '맞춤형 콘텐츠 추천 시스템'
            }
          ].map((field, index) => (
            <div key={index} style={{
              padding: '24px',
              background: `${theme.accent}10`,
              borderRadius: '16px',
              border: `1px solid ${theme.accent}30`
            }}>
