    switch (activeTab) {
      case 'overview':
        return renderOverviewTab();
      case 'system':
        return renderSystemTab();
      case 'data-collection':
        return renderDataCollectionTab();
      case 'data-models':
        return renderDataModelsTab();
      default:
        return renderOverviewTab();
    }
  };

