import React, { useState, useEffect, useRef } from 'react';

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface User {
  id: string;
  name: string;
  avatar: string;
  level: number;
  points: number;
  streak: number;
  badges: string[];
  joinDate: string;
}

interface Notification {
  id: string;
  title: string;
  message: string;
  type: 'success' | 'info' | 'warning' | 'error';
  timestamp: number;
  read: boolean;
}

interface Achievement {
  id: string;
  title: string;
  description: string;
  icon: string;
  progress: number;
  maxProgress: number;
  completed: boolean;
  reward: number;
}

interface Restaurant {
  id: string;
  name: string;
  category: string;
  image: string;
  rating: number;
  deliveryTime: string;
  deliveryFee: number;
  minOrder: number;
  isOpen: boolean;
  tags: string[];
  distance: string;
  discount?: string;
}

interface CartItem {
  id: string;
  restaurantId: string;
  name: string;
  price: number;
  quantity: number;
  options: string[];
}

interface Order {
  id: string;
  restaurantName: string;
  items: CartItem[];
  totalPrice: number;
  status: 'preparing' | 'cooking' | 'delivering' | 'delivered' | 'cancelled';
  orderTime: string;
  deliveryTime?: string;
  address: string;
}

interface AppState {
  currentTab: 'home' | 'search' | 'favorites' | 'orders' | 'my';
  user: User;
  notifications: Notification[];
  restaurants: Restaurant[];
  favorites: string[];
  cart: CartItem[];
  orders: Order[];
  currentLocation: string;
  isLoading: boolean;
  showNotification: Notification | null;
  pullRefresh: { isRefreshing: boolean; pullDistance: number };
  searchQuery: string;
  selectedCategory: string;
}

export const MobileApp: React.FC<AppProps> = ({
  onAction,
  playerStats,
  setPlayerStats,
  currentUser
}) => {
  const [appState, setAppState] = useState<AppState>({
    currentTab: 'home',
    user: {
      id: currentUser.user_id,
      name: 'Î∞∞Îã¨Í≥†Ïàò',
      avatar: 'üçî',
      level: 5,
      points: 2450,
      streak: 7,
      badges: ['üî•', '‚≠ê', 'üéØ', 'üíé'],
      joinDate: '2024-01-15'
    },
    notifications: [
      {
        id: '1',
        title: 'Î∞∞Îã¨ ÏôÑÎ£å!',
        message: 'ÏπòÌÇ®Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î∞∞Îã¨ÎêòÏóàÏäµÎãàÎã§.',
        type: 'success',
        timestamp: Date.now() - 1800000,
        read: false
      },
      {
        id: '2',
        title: 'Ìï†Ïù∏ Ïø†Ìè∞ ÎèÑÏ∞©!',
        message: '30% Ìï†Ïù∏ Ïø†Ìè∞Ïù¥ Î∞úÍ∏âÎêòÏóàÏäµÎãàÎã§.',
        type: 'info',
        timestamp: Date.now() - 3600000,
        read: false
      }
    ],
    restaurants: [
      {
        id: 'rest1',
        name: 'ÎßõÏûàÎäî ÏπòÌÇ®Ïßë',
        category: 'ÏπòÌÇ®',
        image: 'üçó',
        rating: 4.8,
        deliveryTime: '25-35Î∂Ñ',
        deliveryFee: 2500,
        minOrder: 15000,
        isOpen: true,
        tags: ['Ïù∏Í∏∞', 'Îπ†Î•∏Î∞∞Îã¨'],
        distance: '0.8km',
        discount: '30% Ìï†Ïù∏'
      },
      {
        id: 'rest2',
        name: 'ÌîºÏûêÏôïÍµ≠',
        category: 'ÌîºÏûê',
        image: 'üçï',
        rating: 4.6,
        deliveryTime: '30-40Î∂Ñ',
        deliveryFee: 3000,
        minOrder: 20000,
        isOpen: true,
        tags: ['Ïã†Î©îÎâ¥'],
        distance: '1.2km'
      },
      {
        id: 'rest3',
        name: 'ÌïúÏãùÎãπ',
        category: 'ÌïúÏãù',
        image: 'üçö',
        rating: 4.7,
        deliveryTime: '20-30Î∂Ñ',
        deliveryFee: 2000,
        minOrder: 12000,
        isOpen: true,
        tags: ['Í±¥Í∞ïÌïú'],
        distance: '0.5km'
      },
      {
        id: 'rest4',
        name: 'Î≤ÑÍ±∞ÌÇπ',
        category: 'Ìå®Ïä§Ìä∏Ìë∏Îìú',
        image: 'üçî',
        rating: 4.5,
        deliveryTime: '15-25Î∂Ñ',
        deliveryFee: 1500,
        minOrder: 10000,
        isOpen: false,
        tags: ['24ÏãúÍ∞Ñ'],
        distance: '1.5km'
      }
    ],
    favorites: ['rest1', 'rest3'],
    cart: [],
    orders: [
      {
        id: 'order1',
        restaurantName: 'ÎßõÏûàÎäî ÏπòÌÇ®Ïßë',
        items: [
          { id: 'item1', restaurantId: 'rest1', name: 'ÌõÑÎùºÏù¥Îìú ÏπòÌÇ®', price: 18000, quantity: 1, options: ['ÏàúÏÇ¥'] }
        ],
        totalPrice: 20500,
        status: 'delivered',
        orderTime: '2024-01-15 19:30',
        deliveryTime: '2024-01-15 20:15',
        address: 'ÏÑúÏö∏Ïãú Í∞ïÎÇ®Íµ¨ ÌÖåÌó§ÎûÄÎ°ú 123'
      }
    ],
    currentLocation: 'ÏÑúÏö∏Ïãú Í∞ïÎÇ®Íµ¨ ÌÖåÌó§ÎûÄÎ°ú',
    isLoading: false,
    showNotification: null,
    pullRefresh: { isRefreshing: false, pullDistance: 0 },
    searchQuery: '',
    selectedCategory: 'Ï†ÑÏ≤¥'
  });

  const scrollRef = useRef<HTMLDivElement>(null);
  const touchStartY = useRef<number>(0);
  const [frameCount, setFrameCount] = useState(0);

  // Animation loop for smooth effects
  useEffect(() => {
    const animate = () => {
      setFrameCount(prev => prev + 1);
      requestAnimationFrame(animate);
    };
    animate();
  }, []);

  // Simulate real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      // Random point gain simulation
      if (Math.random() < 0.3) {
        const pointGain = Math.floor(Math.random() * 50) + 10;
        setAppState(prev => ({
          ...prev,
          user: { ...prev.user, points: prev.user.points + pointGain }
        }));

        onAction('points_gained', {
          user_id: currentUser.user_id,
          points_gained: pointGain,
          total_points: appState.user.points + pointGain,
          timestamp: Date.now()
        });
      }

      // Random notifications
      if (Math.random() < 0.1) {
        const messages = [
          { title: 'ÏÉàÎ°úÏö¥ Ï±åÎ¶∞ÏßÄ!', message: 'Ïò§ÎäòÏùò ÌäπÎ≥Ñ ÎØ∏ÏÖòÏù¥ ÎèÑÏ∞©ÌñàÏäµÎãàÎã§!', type: 'info' as const },
          { title: 'ÏπúÍµ¨ ÏöîÏ≤≠', message: 'ThinkingMasterÎãòÏù¥ ÏπúÍµ¨ ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉàÏäµÎãàÎã§.', type: 'info' as const },
          { title: 'Î†àÎ≤®ÏóÖ!', message: 'Ï∂ïÌïòÌï©ÎãàÎã§! ÏÉàÎ°úÏö¥ Î†àÎ≤®Ïóê ÎèÑÎã¨ÌñàÏäµÎãàÎã§!', type: 'success' as const }
        ];

        const randomMessage = messages[Math.floor(Math.random() * messages.length)];
        const newNotification: Notification = {
          id: `notif_${Date.now()}`,
          ...randomMessage,
          timestamp: Date.now(),
          read: false
        };

        setAppState(prev => ({
          ...prev,
          notifications: [newNotification, ...prev.notifications.slice(0, 9)],
          showNotification: newNotification
        }));

        // Hide notification after 3 seconds
        setTimeout(() => {
          setAppState(prev => ({ ...prev, showNotification: null }));
        }, 3000);
      }
    }, 5000);

    return () => clearInterval(interval);
  }, [currentUser.user_id, appState.user.points, onAction]);

  // Pull to refresh
  const handleTouchStart = (e: React.TouchEvent) => {
    if (scrollRef.current?.scrollTop === 0) {
      touchStartY.current = e.touches[0].clientY;
    }
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (scrollRef.current?.scrollTop === 0) {
      const currentY = e.touches[0].clientY;
      const pullDistance = Math.max(0, Math.min(100, currentY - touchStartY.current));

      setAppState(prev => ({
        ...prev,
        pullRefresh: { ...prev.pullRefresh, pullDistance }
      }));
    }
  };

  const handleTouchEnd = () => {
    if (appState.pullRefresh.pullDistance > 50) {
      setAppState(prev => ({
        ...prev,
        pullRefresh: { isRefreshing: true, pullDistance: 0 },
        isLoading: true
      }));

      // Simulate refresh
      setTimeout(() => {
        setAppState(prev => ({
          ...prev,
          pullRefresh: { isRefreshing: false, pullDistance: 0 },
          isLoading: false,
          user: { ...prev.user, points: prev.user.points + Math.floor(Math.random() * 100) }
        }));
      }, 1500);
    } else {
      setAppState(prev => ({
        ...prev,
        pullRefresh: { ...prev.pullRefresh, pullDistance: 0 }
      }));
    }
  };

  // Tab switching
  const switchTab = (tab: AppState['currentTab']) => {
    setAppState(prev => ({ ...prev, currentTab: tab }));

    onAction('tab_switched', {
      user_id: currentUser.user_id,
      from_tab: appState.currentTab,
      to_tab: tab,
      timestamp: Date.now()
    });
  };

  // Home Screen
  const renderHomeScreen = () => (
    <div style={{ padding: '20px', paddingBottom: '20px' }}>
      {/* Pull to refresh indicator */}
      {appState.pullRefresh.pullDistance > 0 && (
        <div style={{
          position: 'absolute',
          top: `-${20 - appState.pullRefresh.pullDistance / 5}px`,
          left: '50%',
          transform: 'translateX(-50%)',
          fontSize: '24px',
          transition: 'all 0.2s ease'
        }}>
          {appState.pullRefresh.pullDistance > 50 ? 'üîÑ' : '‚¨áÔ∏è'}
        </div>
      )}

      {/* Stats Cards */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: '1fr 1fr',
        gap: '15px',
        marginBottom: '25px'
      }}>
        <div style={{
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          borderRadius: '15px',
          padding: '20px',
          color: 'white',
          textAlign: 'center',
          boxShadow: '0 8px 20px rgba(0,0,0,0.1)',
          transform: `scale(${1 + Math.sin(frameCount * 0.01) * 0.02})`
        }}>
          <div style={{ fontSize: '28px', marginBottom: '5px' }}>‚≠ê</div>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{appState.user.points.toLocaleString()}</div>
          <div style={{ fontSize: '12px', opacity: 0.9 }}>Ìè¨Ïù∏Ìä∏</div>
        </div>

        <div style={{
          background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
          borderRadius: '15px',
          padding: '20px',
          color: 'white',
          textAlign: 'center',
          boxShadow: '0 8px 20px rgba(0,0,0,0.1)',
          transform: `scale(${1 + Math.sin(frameCount * 0.01 + 1) * 0.02})`
        }}>
          <div style={{ fontSize: '28px', marginBottom: '5px' }}>üî•</div>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{appState.user.streak}</div>
          <div style={{ fontSize: '12px', opacity: 0.9 }}>Ïó∞ÏÜç ÏùºÏàò</div>
        </div>
      </div>

      {/* Quick Actions */}
      <div style={{ marginBottom: '25px' }}>
        <h3 style={{ marginBottom: '15px', color: '#333' }}>Îπ†Î•∏ Ïã§Ìñâ</h3>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(2, 1fr)',
          gap: '15px'
        }}>
          {[
            { icon: 'üéØ', title: 'Îç∞Ïù¥ÌÑ∞ Î∂ÑÏÑù', color: '#4facfe' },
            { icon: 'üìä', title: 'Î¶¨Ìè¨Ìä∏ ÏÉùÏÑ±', color: '#43e97b' },
            { icon: 'üß†', title: 'ÏÇ¨Í≥† ÌõàÎ†®', color: '#fa709a' },
            { icon: 'üéÆ', title: 'ÎØ∏ÎãàÍ≤åÏûÑ', color: '#ffecd2' }
          ].map((action, index) => (
            <div
              key={action.title}
              onClick={() => onAction('quick_action', { action: action.title, user_id: currentUser.user_id })}
              style={{
                background: `linear-gradient(135deg, ${action.color}40, ${action.color}80)`,
                borderRadius: '12px',
                padding: '20px',
                textAlign: 'center',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                border: 'none',
                boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
                transform: `translateY(${Math.sin(frameCount * 0.02 + index) * 2}px)`
              }}
              onMouseDown={(e) => {
                e.currentTarget.style.transform = 'scale(0.95)';
              }}
              onMouseUp={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
              }}
            >
              <div style={{ fontSize: '32px', marginBottom: '8px' }}>{action.icon}</div>
              <div style={{ fontWeight: 'bold', color: '#333' }}>{action.title}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Recent Activity */}
      <div>
        <h3 style={{ marginBottom: '15px', color: '#333' }}>ÏµúÍ∑º ÌôúÎèô</h3>
        <div style={{
          background: 'white',
          borderRadius: '12px',
          boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
          overflow: 'hidden'
        }}>
          {[
            { icon: 'üìà', text: 'Îç∞Ïù¥ÌÑ∞ Î∂ÑÏÑù ÏôÑÎ£å', time: '2Î∂Ñ Ï†Ñ', points: '+50' },
            { icon: 'üèÜ', text: 'Ï£ºÍ∞Ñ Ï±åÎ¶∞ÏßÄ Îã¨ÏÑ±', time: '1ÏãúÍ∞Ñ Ï†Ñ', points: '+200' },
            { icon: 'üí°', text: 'ÏÉàÎ°úÏö¥ Ïù∏ÏÇ¨Ïù¥Ìä∏ Î∞úÍ≤¨', time: '3ÏãúÍ∞Ñ Ï†Ñ', points: '+75' }
          ].map((activity, index) => (
            <div
              key={index}
              style={{
                padding: '15px 20px',
                borderBottom: index < 2 ? '1px solid #f0f0f0' : 'none',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between'
              }}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <div style={{ fontSize: '20px' }}>{activity.icon}</div>
                <div>
                  <div style={{ fontWeight: '500', color: '#333' }}>{activity.text}</div>
                  <div style={{ fontSize: '12px', color: '#999' }}>{activity.time}</div>
                </div>
              </div>
              <div style={{
                background: 'linear-gradient(135deg, #667eea, #764ba2)',
                color: 'white',
                padding: '4px 8px',
                borderRadius: '12px',
                fontSize: '12px',
                fontWeight: 'bold'
              }}>
                {activity.points}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Profile Screen
  const renderProfileScreen = () => (
    <div style={{ padding: '20px', paddingBottom: '20px' }}>
      {/* Profile Header */}
      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: '20px',
        padding: '30px 20px',
        color: 'white',
        textAlign: 'center',
        marginBottom: '25px',
        boxShadow: '0 8px 25px rgba(0,0,0,0.15)'
      }}>
        <div style={{
          fontSize: '60px',
          marginBottom: '10px',
          transform: `rotate(${Math.sin(frameCount * 0.02) * 5}deg)`
        }}>
          {appState.user.avatar}
        </div>
        <h2 style={{ margin: '0 0 5px 0' }}>{appState.user.name}</h2>
        <div style={{ opacity: 0.9 }}>Level {appState.user.level}</div>
        <div style={{ opacity: 0.8, fontSize: '14px', marginTop: '5px' }}>
          Í∞ÄÏûÖÏùº: {new Date(appState.user.joinDate).toLocaleDateString()}
        </div>
      </div>

      {/* Stats Grid */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(2, 1fr)',
        gap: '15px',
        marginBottom: '25px'
      }}>
        <div style={{
          background: 'white',
          borderRadius: '15px',
          padding: '20px',
          textAlign: 'center',
          boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
        }}>
          <div style={{ fontSize: '24px', color: '#667eea', fontWeight: 'bold' }}>
            {appState.user.points.toLocaleString()}
          </div>
          <div style={{ color: '#666', fontSize: '14px' }}>Ï¥ù Ìè¨Ïù∏Ìä∏</div>
        </div>

        <div style={{
          background: 'white',
          borderRadius: '15px',
          padding: '20px',
          textAlign: 'center',
          boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
        }}>
          <div style={{ fontSize: '24px', color: '#f5576c', fontWeight: 'bold' }}>
            {appState.user.streak}
          </div>
          <div style={{ color: '#666', fontSize: '14px' }}>Ïó∞ÏÜç Í∏∞Î°ù</div>
        </div>
      </div>

      {/* Badges */}
      <div style={{ marginBottom: '25px' }}>
        <h3 style={{ marginBottom: '15px', color: '#333' }}>ÌöçÎìùÌïú Î∞∞ÏßÄ</h3>
        <div style={{
          display: 'flex',
          gap: '10px',
          flexWrap: 'wrap'
        }}>
          {appState.user.badges.map((badge, index) => (
            <div
              key={index}
              style={{
                background: 'white',
                borderRadius: '50%',
                width: '60px',
                height: '60px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '24px',
                boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
                transform: `scale(${1 + Math.sin(frameCount * 0.03 + index) * 0.1})`
              }}
            >
              {badge}
            </div>
          ))}
        </div>
      </div>

      {/* Settings Options */}
      <div>
        <h3 style={{ marginBottom: '15px', color: '#333' }}>ÏÑ§Ï†ï</h3>
        <div style={{
          background: 'white',
          borderRadius: '12px',
          boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
          overflow: 'hidden'
        }}>
          {[
            { icon: 'üîî', title: 'ÏïåÎ¶º ÏÑ§Ï†ï', subtitle: 'Ìë∏Ïãú ÏïåÎ¶º Í¥ÄÎ¶¨' },
            { icon: 'üé®', title: 'ÌÖåÎßà ÏÑ§Ï†ï', subtitle: 'Ïï± ÎîîÏûêÏù∏ Î≥ÄÍ≤Ω' },
            { icon: 'üîí', title: 'Í∞úÏù∏Ï†ïÎ≥¥ Î≥¥Ìò∏', subtitle: 'Î≥¥Ïïà Î∞è ÌîÑÎùºÏù¥Î≤ÑÏãú' },
            { icon: '‚ùì', title: 'ÎèÑÏõÄÎßê', subtitle: 'ÏûêÏ£º Î¨ªÎäî ÏßàÎ¨∏' }
          ].map((setting, index) => (
            <div
              key={setting.title}
              onClick={() => onAction('setting_clicked', { setting: setting.title, user_id: currentUser.user_id })}
              style={{
                padding: '15px 20px',
                borderBottom: index < 3 ? '1px solid #f0f0f0' : 'none',
                display: 'flex',
                alignItems: 'center',
                gap: '15px',
                cursor: 'pointer',
                transition: 'background 0.2s ease'
              }}
              onMouseDown={(e) => {
                e.currentTarget.style.background = '#f8f8f8';
              }}
              onMouseUp={(e) => {
                e.currentTarget.style.background = 'white';
              }}
            >
              <div style={{ fontSize: '20px' }}>{setting.icon}</div>
              <div style={{ flex: 1 }}>
                <div style={{ fontWeight: '500', color: '#333' }}>{setting.title}</div>
                <div style={{ fontSize: '12px', color: '#999' }}>{setting.subtitle}</div>
              </div>
              <div style={{ color: '#ccc' }}>‚Ä∫</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Achievements Screen
  const renderAchievementsScreen = () => (
    <div style={{ padding: '20px', paddingBottom: '20px' }}>
      <h2 style={{ marginBottom: '20px', color: '#333' }}>ÏóÖÏ†Å</h2>

      {appState.achievements.map((achievement) => (
        <div
          key={achievement.id}
          style={{
            background: 'white',
            borderRadius: '15px',
            padding: '20px',
            marginBottom: '15px',
            boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
            border: achievement.completed ? '2px solid #4CAF50' : '1px solid #e0e0e0'
          }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '15px' }}>
            <div style={{
