import React, { useState, useEffect } from 'react';
import { Users, UserPlus, Shield, Eye, EyeOff } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { Tabs } from '../../components/Tabs';

interface Step2Props {
  onComplete: () => void;
}

export const Step2AccountSetup: React.FC<Step2Props> = ({ onComplete }) => {
  const [understood, setUnderstood] = useState(false);

  useEffect(() => {
    if (understood) {
      onComplete();
    }
  }, [understood, onComplete]);

  const roleTypes = [
    {
      name: '프로젝트 관리자',
      badge: { text: '최고 권한', variant: 'danger' as const },
      permissions: [
        '모든 프로젝트 설정 관리',
        '구성원 및 역할 관리',
        '데이터 연동 설정',
        '보고서 생성 및 공유',
        '시스템 설정'
      ],
      description: '프로젝트의 모든 권한을 가지며 전체적인 관리를 담당합니다.'
    },
    {
      name: '분석 관리자',
      badge: { text: '관리 권한', variant: 'warning' as const },
      permissions: [
        '대시보드 생성 및 편집',
        '보고서 관리',
        '데이터 분석',
        '일반 구성원 관리',
        '분석 도구 설정'
      ],
      description: '분석과 관련된 모든 업무를 관리할 수 있습니다.'
    },
    {
      name: '분석가',
      badge: { text: '분석 권한', variant: 'info' as const },
      permissions: [
        '대시보드 보기 및 생성',
        '데이터 분석',
        '개인 보고서 생성',
        '차트 및 위젯 생성',
        '데이터 내보내기'
      ],
      description: '데이터 분석 업무를 수행할 수 있습니다.'
