import React, { useState, useEffect } from 'react';
import { Database, Smartphone, Monitor, Gamepad2, Wrench, Globe } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { Tabs } from '../../components/Tabs';
import { Input } from '../../components/Input';
import { CodeBlock } from '../../components/CodeBlock';

interface Step3Props {
  onComplete: () => void;
  appData: {
    appId: string;
    dataUrl: string;
    selectedVersion: 'saas' | 'private' | null;
  };
  onAppDataChange: (data: any) => void;
}

export const Step3DataIntegration: React.FC<Step3Props> = ({
  onComplete,
  appData,
  onAppDataChange
}) => {
  const [understood, setUnderstood] = useState(false);
  const [hasRequiredData, setHasRequiredData] = useState(false);

  useEffect(() => {
    const hasData = appData.appId.trim() !== '' && appData.dataUrl.trim() !== '';
    setHasRequiredData(hasData);

    if (understood && hasData) {
      onComplete();
    }
  }, [understood, appData, onComplete]);

  const platformTabs = [
    {
      id: 'javascript',
      label: 'JavaScript',
      icon: <Globe size={16} />,
      content: getJavaScriptCode(appData)
    },
    {
      id: 'ios',
      label: 'iOS',
      icon: <Smartphone size={16} />,
      content: getIOSCode(appData)
    },
    {
      id: 'android',
      label: 'Android',
      icon: <Smartphone size={16} />,
      content: getAndroidCode(appData)
    },
    {
      id: 'unity',
      label: 'Unity',
      icon: <Gamepad2 size={16} />,
      content: getUnityCode(appData)
    },
    {
      id: 'unreal',
      label: 'Unreal',
      icon: <Gamepad2 size={16} />,
      content: getUnrealCode(appData)
    },
    {
      id: 'api',
      label: 'RESTful API',
      icon: <Wrench size={16} />,
      content: getAPICode(appData)
    }
  ];

  const tabs = platformTabs.map(platform => ({
    id: platform.id,
    label: (
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        {platform.icon}
        {platform.label}
      </div>
    ),
    content: platform.content
  }));

  return (
    <div>
      {/* Step Title */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '12px',
          marginBottom: '16px' 
        }}>
          <Database size={24} color="var(--brand-primary)" />
          <h3 style={{ 
            fontSize: '24px', 
            fontWeight: '600', 
            color: 'var(--gray-900)',
            margin: 0
          }}>
            3단계: 데이터 연동
          </h3>
        </div>
        <p style={{ 
          fontSize: '16px', 
          color: 'var(--gray-600)',
          lineHeight: '1.6',
          margin: 0
        }}>
          앱에서 발생하는 데이터를 수집하기 위해 SDK를 설치하고 초기화하세요.
          플랫폼별 가이드를 참고하여 연동을 완료할 수 있습니다.
        </p>
      </div>

      {/* App Configuration */}
      <div style={{ marginBottom: '32px' }}>
        <Card>
          <h4 style={{ 
            fontSize: '18px', 
            fontWeight: '600', 
            marginBottom: '16px',
            color: 'var(--gray-900)'
          }}>
            설정 정보 입력
          </h4>

          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
            gap: '16px',
            marginBottom: '16px'
          }}>
            <Input
              label="App ID"
              value={appData.appId}
              onChange={(value) => onAppDataChange({ ...appData, appId: value })}
              placeholder="your-app-id"
              required
            />
            <Input
              label="Data URL"
              value={appData.dataUrl}
              onChange={(value) => onAppDataChange({ ...appData, dataUrl: value })}
              placeholder="https://your-server-url/sync_js"
              required
              type="url"
            />
          </div>

          <InfoBox type="info" title="설정 정보 안내">
            <p style={{ margin: 0 }}>
              App ID와 Data URL은 프로젝트 생성 시 제공되는 고유 식별자입니다. 
              위 정보를 입력하면 아래 코드 예제에 자동으로 반영됩니다.
            </p>
          </InfoBox>
        </Card>
      </div>

      {/* Quick Start Guide */}
      <div style={{ marginBottom: '32px' }}>
        <Card>
          <h4 style={{ 
            fontSize: '18px', 
            fontWeight: '600', 
            marginBottom: '16px',
            color: 'var(--gray-900)'
          }}>
            빠른 시작 안내
          </h4>

          <div style={{ marginBottom: '16px' }}>
            <p style={{ 
              fontSize: '14px', 
              color: 'var(--gray-600)',
              lineHeight: '1.6',
              margin: '0 0 12px 0'
            }}>
              대부분의 기본 기능은 1-2단계만으로 시작할 수 있습니다:
            </p>

            <div style={{ 
              display: 'flex', 
              gap: '16px',
              flexWrap: 'wrap'
            }}>
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '8px',
                padding: '8px 16px',
                backgroundColor: 'var(--success)',
                color: 'white',
                borderRadius: 'var(--border-radius)',
                fontSize: '14px'
              }}>
                <Badge variant="success" size="sm">필수</Badge>
                <span>SDK 설치</span>
              </div>
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '8px',
                padding: '8px 16px',
                backgroundColor: 'var(--success)',
                color: 'white',
                borderRadius: 'var(--border-radius)',
                fontSize: '14px'
              }}>
                <Badge variant="success" size="sm">필수</Badge>
                <span>SDK 초기화 및 자동 이벤트 추적</span>
              </div>
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '8px',
                padding: '8px 16px',
                backgroundColor: 'var(--gray-400)',
                color: 'white',
                borderRadius: 'var(--border-radius)',
                fontSize: '14px'
              }}>
                <Badge variant="secondary" size="sm">선택</Badge>
                <span>추가 SDK 가이드</span>
              </div>
            </div>
          </div>

          <InfoBox type="success" title="빠른 시작 팁">
            <p style={{ margin: 0 }}>
              필수 단계만 완료해도 기본적인 사용자 행동 데이터(페이지 뷰, 클릭 등)를 
              자동으로 수집하기 시작합니다. 추가 설정은 나중에 필요에 따라 진행하세요.
            </p>
          </InfoBox>
        </Card>
      </div>

      {/* Platform SDK Guides */}
      <div style={{ marginBottom: '32px' }}>
        <h4 style={{ 
          fontSize: '18px', 
          fontWeight: '600', 
          marginBottom: '16px',
          color: 'var(--gray-900)'
        }}>
          플랫폼별 SDK 가이드
        </h4>

        <Tabs tabs={tabs} defaultTab="javascript" />
      </div>

      {/* Additional Resources */}
      <div style={{ marginBottom: '32px' }}>
        <InfoBox type="info" title="추가 리소스">
          <div style={{ 
            display: 'flex', 
            flexDirection: 'column', 
            gap: '8px',
            fontSize: '14px'
          }}>
            <a href="#" style={{ color: 'var(--brand-primary)', textDecoration: 'none' }}>
              📚 전체 SDK 문서 보기
            </a>
            <a href="#" style={{ color: 'var(--brand-primary)', textDecoration: 'none' }}>
              🔧 고급 설정 가이드
            </a>
            <a href="#" style={{ color: 'var(--brand-primary)', textDecoration: 'none' }}>
              💡 자주 묻는 질문 (FAQ)
            </a>
            <a href="#" style={{ color: 'var(--brand-primary)', textDecoration: 'none' }}>
              🎯 이벤트 트래킹 예제
            </a>
          </div>
        </InfoBox>
      </div>

      {/* Completion Checkbox */}
      <div style={{ 
        padding: '20px', 
        backgroundColor: 'var(--gray-50)', 
        borderRadius: 'var(--border-radius)',
        border: '1px solid var(--gray-200)'
      }}>
        <Checkbox
          checked={understood}
          onChange={setUnderstood}
          label="데이터 연동 과정을 이해했으며 SDK 설치를 완료했습니다. 다음 단계로 진행할 준비가 되었습니다."
          disabled={!hasRequiredData}
        />
        {!hasRequiredData && (
          <p style={{ 
            fontSize: '12px', 
            color: 'var(--danger)', 
            margin: '8px 0 0 28px' 
          }}>
            App ID와 Data URL을 먼저 입력해주세요.
          </p>
        )}
      </div>
    </div>
  );
};

// Platform-specific code generators
function getJavaScriptCode(appData: any) {
  const { appId, dataUrl } = appData;
  const jsServerUrl = dataUrl ? (dataUrl.endsWith('/sync_js') ? dataUrl : `${dataUrl}/sync_js`) : 'https://YOUR_SERVER_URL/sync_js';

  return (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          1. SDK 설치
        </h5>
        <CodeBlock
          language="bash"
          title="NPM 설치"
          code="npm install thinkingdata-browser --save"
        />
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          2. SDK 초기화 및 자동 이벤트 추적
        </h5>
        <CodeBlock
          language="javascript"
          title="자동 통합 방식"
          code={`import thinkingdata from "thinkingdata-browser";

// SDK 구성 객체 생성
var config = {
  appId: "${appId || 'YOUR_APP_ID'}",
  serverUrl: "${jsServerUrl}",
  batch: true, // 데이터를 먼저 로컬에 캐시한 후 일괄 전송합니다. 기본값은 false입니다.
  autoTrack: {
    pageShow: true, // 페이지 표시 이벤트를 활성화합니다. 이벤트 이름: ta_page_show
    pageHide: true, // 페이지 숨김 이벤트를 활성화합니다. 이벤트 이름: ta_page_hide
  }
};

// SDK 초기화
thinkingdata.init(config);`}
        />
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="secondary" size="sm" style={{ marginRight: '8px' }}>선택</Badge>
          3. 계정 ID 설정
        </h5>
        <CodeBlock
          language="javascript"
          title="로그인 사용자 설정"
          code={`// 유저가 로그인할 때 계정 ID 설정
ta.login("user_account_id");`}
        />
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="secondary" size="sm" style={{ marginRight: '8px' }}>선택</Badge>
          4. 이벤트 전송 및 유저 속성
        </h5>
        <CodeBlock
          language="javascript"
          title="커스텀 이벤트 및 속성"
          code={`// 공통 이벤트 속성 설정
var superProperties = {};
superProperties["channel"] = "web"; // String
superProperties["user_type"] = "premium"; // String
ta.setSuperProperties(superProperties);

// 이벤트 전송
ta.track("product_buy", { 
  product_name: "상품명",
  price: 29900 
});

// 유저 속성 설정
ta.userSet({ username: "사용자명" });`}
        />
      </div>

      <InfoBox type="success" title="설치 완료!">
        <p style={{ margin: 0 }}>
          위 1-2단계만 완료해도 기본적인 페이지 뷰 이벤트가 자동으로 수집됩니다. 
          3-4단계는 필요에 따라 추가로 구현하세요.
        </p>
      </InfoBox>
    </div>
  );
}

function getIOSCode(appData: any) {
  const { appId, dataUrl } = appData;

  return (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          1. SDK 설치
        </h5>
        <CodeBlock
          language="ruby"
          title="Podfile"
          code={`# iOS 8.0 이상 필요
platform :ios, '8.0'

target 'YourApp' do
  pod 'ThinkingSDK'
end`}
        />
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          2. SDK 초기화
        </h5>
        <CodeBlock
          language="swift"
          title="AppDelegate.swift"
          code={`import ThinkingSDK

@UIApplicationMain
class AppDelegate: UIResponder, UIApplicationDelegate {

    func application(_ application: UIApplication, didFinishLaunchingWithOptions launchOptions: [UIApplication.LaunchOptionsKey: Any]?) -> Bool {

        // SDK 초기화
        let config = TDConfig.init()
        config.appId = "${appId || 'YOUR_APP_ID'}"
        config.serverUrl = "${dataUrl || 'https://YOUR_SERVER_URL/sync_js'}"
        config.autoTrackEventType = [.appStart, .appEnd, .appViewScreen]

        ThinkingAnalyticsSDK.start(with: config)

        return true
    }
}`}
        />
      </div>
    </div>
  );
}

function getAndroidCode(appData: any) {
  const { appId, dataUrl } = appData;

  return (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          1. SDK 설치
        </h5>
        <CodeBlock
          language="gradle"
          title="app/build.gradle"
          code={`dependencies {
    implementation 'cn.thinkingdata.android:ThinkingAnalyticsSDK:2.8.+'
}`}
        />
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          2. SDK 초기화
        </h5>
        <CodeBlock
          language="java"
          title="MainApplication.java"
          code={`import cn.thinkingdata.analytics.ThinkingAnalyticsSDK;
import cn.thinkingdata.analytics.TDConfig;

public class MainApplication extends Application {
    @Override
    public void onCreate() {
        super.onCreate();

        TDConfig config = TDConfig.getInstance(this, "${appId || 'YOUR_APP_ID'}", "${dataUrl || 'https://YOUR_SERVER_URL'}");
        TDAnalytics.init(config);
    }
}`}
        />
      </div>
    </div>
  );
}

function getUnityCode(appData: any) {
  const { appId, dataUrl } = appData;

  return (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          1. SDK 설치
        </h5>
        <p style={{ fontSize: '14px', color: 'var(--gray-600)', marginBottom: '12px' }}>
          Unity Package Manager를 통해 ThinkingData SDK를 가져옵니다.
        </p>
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          2. SDK 초기화
        </h5>
        <CodeBlock
          language="csharp"
          title="GameManager.cs"
          code={`using ThinkingAnalytics;

public class GameManager : MonoBehaviour 
{
    void Start() 
    {
        TDConfig config = new TDConfig();
        config.appId = "${appId || 'YOUR_APP_ID'}";
        config.serverUrl = "${dataUrl || 'https://YOUR_SERVER_URL/sync_js'}";
        config.autoTrack = TDAutoTrack.START | TDAutoTrack.END | TDAutoTrack.SCENE;

        ThinkingAnalyticsAPI.StartThinkingAnalytics(config);
    }
}`}
        />
      </div>
    </div>
  );
}

function getUnrealCode(appData: any) {
  const { appId, dataUrl } = appData;

  return (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          1. SDK 설치
        </h5>
        <p style={{ fontSize: '14px', color: 'var(--gray-600)', marginBottom: '12px' }}>
          ThinkingData Unreal Engine 플러그인을 프로젝트에 추가합니다.
        </p>
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          2. SDK 초기화
        </h5>
        <CodeBlock
          language="cpp"
          title="GameMode.cpp"
          code={`#include "ThinkingAnalytics.h"

void AMyGameMode::BeginPlay()
{
    Super::BeginPlay();

    FTDConfig Config;
    Config.AppId = TEXT("${appId || 'YOUR_APP_ID'}");
    Config.ServerUrl = TEXT("${dataUrl || 'https://YOUR_SERVER_URL/sync_js'}");
    Config.AutoTrackType = ETDAutoTrack::GameStart | ETDAutoTrack::GameEnd;

    UThinkingAnalytics::StartWithConfig(Config);
}`}
        />
      </div>
    </div>
  );
}

function getAPICode(appData: any) {
  const { appId, dataUrl } = appData;
  const syncDataUrl = dataUrl ? `${dataUrl}/sync_data` : 'https://YOUR_SERVER_URL/sync_data';
  const syncJsonUrl = dataUrl ? `${dataUrl}/sync_json` : 'https://YOUR_SERVER_URL/sync_json';

  return (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="success" size="sm" style={{ marginRight: '8px' }}>필수</Badge>
          1. 데이터 수신 API (form-data 전송)
        </h5>
        <CodeBlock
          language="bash"
          title="curl 예제"
          code={`curl "${syncDataUrl}" \\
