import React, { useState, useEffect } from 'react';
import { Heart, MessageCircle, Share, Bookmark, MoreHorizontal, Users, Code, Settings, ChevronRight, Mail, Phone, Video } from 'lucide-react';
import { Button } from '../components/Button';

export const HomePage: React.FC = () => {
  const [isFollowing, setIsFollowing] = useState(false);
  const [notifications, setNotifications] = useState(false);
  const [screenSize, setScreenSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    const handleResize = () => {
      setScreenSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleFollow = () => {
    setIsFollowing(!isFollowing);
    if (!isFollowing) {
      // 팔로우 시 알림 활성화 여부 물어보기
      setNotifications(true);
    }
  };

  const navigateToQuickStart = () => {
    // 빠른 시작 가이드로 이동
    window.location.href = '/quickstart';
  };

  // 반응형 설정
  const isMobile = screenSize.width < 768;
  const isTablet = screenSize.width >= 768 && screenSize.width < 1024;
  const isDesktop = screenSize.width >= 1024;

  // 컨테이너 스타일 동적 계산
  const getContainerStyles = () => {
    if (isMobile) {
      return {
        maxWidth: '100%',
        margin: '0',
        borderRadius: '0',
        minHeight: '100vh'
      };
    } else if (isTablet) {
      return {
        maxWidth: '600px',
        margin: '0 auto',
        borderRadius: '24px',
        minHeight: 'auto'
      };
    } else {
      return {
        maxWidth: '420px',
        margin: '0 auto',
        borderRadius: '24px',
        minHeight: 'auto'
      };
    }
  };

  // 그리드 컬럼 수 동적 계산
  const getGridColumns = (defaultCols: number) => {
    if (isMobile) return Math.min(defaultCols, 2);
    if (isTablet) return Math.min(defaultCols, 4);
    return defaultCols;
  };

  // 패딩 동적 계산
  const getPadding = (base: string) => {
    if (isMobile) return base;
    if (isTablet) return '32px 24px';
    return base;
  };

  const containerStyles = getContainerStyles();

  return (
    <div style={{
      minHeight: '100vh',
      background: isDesktop 
        ? 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
        : isMobile 
          ? 'linear-gradient(180deg, #667eea 0%, #764ba2 100%)'
          : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: isMobile ? '0' : '20px 0'
    }}>
      <div style={{
        ...containerStyles,
        background: '#ffffff',
        boxShadow: isMobile ? 'none' : '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
        overflow: 'hidden'
      }}>
        {/* Header */}
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          padding: getPadding('16px 20px'),
          borderBottom: '1px solid #e5e7eb'
        }}>
          <h1 style={{
            fontSize: '24px',
            fontWeight: '700',
            margin: 0,
            background: 'linear-gradient(45deg, #667eea, #764ba2)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            ThinkingData
          </h1>
          <MoreHorizontal size={24} style={{ color: '#6b7280', cursor: 'pointer' }} />
        </div>

        {/* Profile Section */}
        <div style={{ padding: getPadding('24px 20px') }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '20px',
            marginBottom: '20px'
          }}>
            {/* Profile Image */}
            <div style={{
              width: isMobile ? '80px' : isTablet ? '100px' : '88px',
              height: isMobile ? '80px' : isTablet ? '100px' : '88px',
              borderRadius: '50%',
              background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: isMobile ? '28px' : isTablet ? '36px' : '32px',
              color: 'white',
              fontWeight: '700',
              boxShadow: '0 8px 24px rgba(102, 126, 234, 0.3)'
            }}>
              📊
            </div>

            {/* Stats */}
            <div style={{
              display: 'flex',
              gap: isMobile ? '16px' : isTablet ? '32px' : '24px',
              flex: 1
            }}>
              <div style={{ textAlign: 'center' }}>
                <div style={{
                  fontSize: isMobile ? '16px' : '18px',
                  fontWeight: '700',
                  color: '#1f2937'
                }}>
                  1.2K
                </div>
                <div style={{
                  fontSize: isMobile ? '12px' : '14px',
                  color: '#6b7280'
                }}>
                  가이드
                </div>
              </div>
              <div style={{ textAlign: 'center' }}>
                <div style={{
                  fontSize: isMobile ? '16px' : '18px',
                  fontWeight: '700',
                  color: '#1f2937'
                }}>
                  45.8K
                </div>
                <div style={{
                  fontSize: isMobile ? '12px' : '14px',
                  color: '#6b7280'
                }}>
                  팔로워
                </div>
              </div>
              <div style={{ textAlign: 'center' }}>
                <div style={{
                  fontSize: isMobile ? '16px' : '18px',
                  fontWeight: '700',
                  color: '#1f2937'
                }}>
                  892
                </div>
                <div style={{
                  fontSize: isMobile ? '12px' : '14px',
                  color: '#6b7280'
                }}>
                  팔로잉
                </div>
              </div>
            </div>
          </div>

          {/* Bio */}
          <div style={{ marginBottom: '20px' }}>
            <h2 style={{
              fontSize: '16px',
              fontWeight: '600',
              margin: '0 0 8px 0',
              color: '#1f2937'
            }}>
              @thinkingdata_official
            </h2>
            <p style={{
              fontSize: '14px',
              color: '#374151',
              margin: '0 0 8px 0',
              lineHeight: '1.5'
            }}>
              🚀 데이터 분석 플랫폼 | 실시간 고객 행동 분석
            </p>
            <p style={{
              fontSize: '14px',
              color: '#374151',
              margin: 0,
              lineHeight: '1.5'
            }}>
              📈 비즈니스 성장을 위한 인사이트 제공
            </p>
          </div>

          {/* Follow Button */}
          <div style={{
            display: 'flex',
            gap: '12px',
            marginBottom: '20px'
          }}>
            <Button
              onClick={handleFollow}
              style={{
                flex: 1,
                background: isFollowing 
                  ? 'linear-gradient(135deg, #10b981 0%, #059669 100%)' 
                  : 'linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%)',
                border: 'none',
                color: 'white',
                fontWeight: '600',
                padding: '12px 20px',
                borderRadius: '12px',
                transition: 'all 0.3s ease'
              }}
            >
              {isFollowing ? '✓ 팔로잉' : '+ 팔로우'}
            </Button>
            <Button
              style={{
                background: '#f3f4f6',
                border: '1px solid #d1d5db',
                color: '#374151',
                fontWeight: '600',
                padding: '12px 20px',
                borderRadius: '12px',
                minWidth: '48px'
              }}
            >
              💬
            </Button>
          </div>

          {/* Notification Alert */}
          {isFollowing && notifications && (
            <div style={{
              background: 'linear-gradient(135deg, #fef3c7 0%, #fde68a 100%)',
              border: '1px solid #f59e0b',
              borderRadius: '12px',
              padding: '12px 16px',
              marginBottom: '20px',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <span style={{ fontSize: '16px' }}>🔔</span>
              <span style={{
                fontSize: '13px',
                color: '#92400e',
                fontWeight: '500'
              }}>
                새로운 가이드와 업데이트 소식을 받아보세요!
              </span>
            </div>
          )}
        </div>

        {/* Content Sections */}
        <div style={{
          borderTop: '1px solid #e5e7eb'
        }}>
          {/* ThinkingData Guide */}
          <div style={{ padding: '24px 20px', borderBottom: '1px solid #f3f4f6' }}>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '700',
              margin: '0 0 16px 0',
              color: '#1f2937',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              📚 씽킹데이터 가이드
            </h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                padding: '16px',
                background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%)',
                borderRadius: '12px',
                border: '1px solid #e2e8f0',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
              onMouseEnter={(e) => {
                const element = e.currentTarget as HTMLElement;
                element.style.transform = 'translateY(-2px)';
                element.style.boxShadow = '0 8px 24px rgba(0, 0, 0, 0.1)';
              }}
              onMouseLeave={(e) => {
                const element = e.currentTarget as HTMLElement;
                element.style.transform = 'translateY(0)';
                element.style.boxShadow = 'none';
              }}>
                <div>
                  <div style={{
                    fontSize: '16px',
                    fontWeight: '600',
                    color: '#1f2937',
                    marginBottom: '4px'
                  }}>
                    씽킹데이터는 어떤 툴인가요?
                  </div>
                  <div style={{
                    fontSize: '14px',
                    color: '#6b7280'
                  }}>
                    플랫폼 소개 및 핵심 기능 안내
                  </div>
                </div>
                <ChevronRight size={20} style={{ color: '#9ca3af' }} />
              </div>

              <div style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                padding: '16px',
                background: 'linear-gradient(135deg, #eff6ff 0%, #dbeafe 100%)',
                borderRadius: '12px',
                border: '1px solid #3b82f6',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
              onClick={navigateToQuickStart}
              onMouseEnter={(e) => {
                const element = e.currentTarget as HTMLElement;
                element.style.transform = 'translateY(-2px)';
                element.style.boxShadow = '0 8px 24px rgba(59, 130, 246, 0.2)';
              }}
              onMouseLeave={(e) => {
                const element = e.currentTarget as HTMLElement;
                element.style.transform = 'translateY(0)';
                element.style.boxShadow = 'none';
              }}>
                <div>
                  <div style={{
                    fontSize: '16px',
                    fontWeight: '600',
                    color: '#1f2937',
                    marginBottom: '4px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px'
                  }}>
                    🚀 씽킹데이터 시작하기
                    <span style={{
                      fontSize: '11px',
                      background: '#3b82f6',
                      color: 'white',
                      padding: '2px 6px',
                      borderRadius: '6px',
                      fontWeight: '600'
                    }}>
                      HOT
                    </span>
                  </div>
                  <div style={{
                    fontSize: '14px',
                    color: '#6b7280'
                  }}>
                    4단계로 완성하는 빠른 시작 가이드
                  </div>
                </div>
                <ChevronRight size={20} style={{ color: '#3b82f6' }} />
              </div>
            </div>
          </div>

          {/* User Guide */}
          <div style={{ padding: '24px 20px', borderBottom: '1px solid #f3f4f6' }}>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '700',
              margin: '0 0 16px 0',
              color: '#1f2937',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              👥 사용자별 가이드
            </h3>
            <div style={{
              display: 'grid',
              gridTemplateColumns: `repeat(${getGridColumns(3)}, 1fr)`,
              gap: isMobile ? '8px' : '12px'
            }}>
              {[
                { icon: <Users size={isMobile ? 20 : 24} />, title: '사용자', color: '#10b981', bg: '#ecfdf5' },
                { icon: <Code size={isMobile ? 20 : 24} />, title: '개발자', color: '#3b82f6', bg: '#eff6ff' },
                { icon: <Settings size={isMobile ? 20 : 24} />, title: '관리자', color: '#f59e0b', bg: '#fffbeb' }
              ].map((item, index) => (
                <div key={index} style={{
                  padding: isMobile ? '16px 12px' : '20px 16px',
                  background: item.bg,
                  borderRadius: '12px',
                  textAlign: 'center',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  border: `1px solid ${item.color}20`
                }}
                onMouseEnter={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.transform = 'translateY(-4px)';
                  element.style.boxShadow = `0 12px 28px ${item.color}25`;
                }}
                onMouseLeave={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.transform = 'translateY(0)';
                  element.style.boxShadow = 'none';
                }}>
                  <div style={{
                    color: item.color,
                    marginBottom: '8px',
                    display: 'flex',
                    justifyContent: 'center'
                  }}>
                    {item.icon}
                  </div>
                  <div style={{
                    fontSize: isMobile ? '13px' : '14px',
                    fontWeight: '600',
                    color: '#1f2937'
                  }}>
                    {item.title}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* FAQ */}
          <div style={{ padding: getPadding('24px 20px'), borderBottom: '1px solid #f3f4f6' }}>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '700',
              margin: '0 0 16px 0',
              color: '#1f2937',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              ❓ 자주 묻는 질문
            </h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
              {[
                'SDK 설치는 어떻게 하나요?',
                '데이터 수집이 안 될 때는?',
                '실시간 분석 설정 방법은?',
                '권한 관리는 어떻게 하나요?'
              ].map((question, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  padding: '12px 16px',
                  background: '#f9fafb',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease'
                }}
                onMouseEnter={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.background = '#f3f4f6';
                }}
                onMouseLeave={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.background = '#f9fafb';
                }}>
                  <span style={{
                    fontSize: '14px',
                    color: '#374151',
                    fontWeight: '500'
                  }}>
                    Q{index + 1}. {question}
                  </span>
                  <ChevronRight size={16} style={{ color: '#9ca3af' }} />
                </div>
              ))}
            </div>
          </div>

          {/* Contact Us */}
          <div style={{ padding: getPadding('24px 20px') }}>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '700',
              margin: '0 0 16px 0',
              color: '#1f2937',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              📞 Contact us
            </h3>
            <div style={{
              display: 'grid',
              gridTemplateColumns: isTablet ? 'repeat(2, 1fr)' : '1fr',
              gap: isMobile ? '8px' : '12px'
            }}>
              {[
                { icon: <Video size={20} />, title: '데모 신청하기', color: '#ec4899', bg: '#fdf2f8' },
                { icon: <Mail size={20} />, title: '서포트팀에게 연락하기', color: '#10b981', bg: '#ecfdf5' },
                { icon: <Phone size={20} />, title: '영업팀에게 연락하기', color: '#f59e0b', bg: '#fffbeb' }
              ].map((item, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  padding: '16px',
                  background: item.bg,
                  borderRadius: '12px',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  border: `1px solid ${item.color}20`
                }}
                onMouseEnter={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.transform = 'translateX(4px)';
                  element.style.boxShadow = `0 8px 24px ${item.color}20`;
                }}
                onMouseLeave={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.transform = 'translateX(0)';
                  element.style.boxShadow = 'none';
                }}>
                  <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '12px'
                  }}>
                    <div style={{ color: item.color }}>
                      {item.icon}
                    </div>
                    <span style={{
                      fontSize: '16px',
                      fontWeight: '600',
                      color: '#1f2937'
                    }}>
                      {item.title}
                    </span>
                  </div>
                  <ChevronRight size={20} style={{ color: item.color }} />
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
