import React, { useState, useEffect, useRef } from 'react';

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface User {
  id: string;
  name: string;
  avatar: string;
  level: number;
  points: number;
  streak: number;
  badges: string[];
  joinDate: string;
}

interface Notification {
  id: string;
  title: string;
  message: string;
  type: 'success' | 'info' | 'warning' | 'error';
  timestamp: number;
  read: boolean;
}

interface Achievement {
  id: string;
  title: string;
  description: string;
  icon: string;
  progress: number;
  maxProgress: number;
  completed: boolean;
  reward: number;
}

interface Restaurant {
  id: string;
  name: string;
  category: string;
  image: string;
  rating: number;
  deliveryTime: string;
  deliveryFee: number;
  minOrder: number;
  isOpen: boolean;
  tags: string[];
  distance: string;
  discount?: string;
}

interface CartItem {
  id: string;
  restaurantId: string;
  name: string;
  price: number;
  quantity: number;
  options: string[];
}

interface Order {
  id: string;
  restaurantName: string;
  items: CartItem[];
  totalPrice: number;
  status: 'preparing' | 'cooking' | 'delivering' | 'delivered' | 'cancelled';
  orderTime: string;
  deliveryTime?: string;
  address: string;
}

interface AppState {
  currentTab: 'home' | 'search' | 'favorites' | 'orders' | 'my';
  user: User;
  notifications: Notification[];
  restaurants: Restaurant[];
  favorites: string[];
  cart: CartItem[];
  orders: Order[];
  currentLocation: string;
  isLoading: boolean;
  showNotification: Notification | null;
  pullRefresh: { isRefreshing: boolean; pullDistance: number };
  searchQuery: string;
  selectedCategory: string;
}

export const MobileApp: React.FC<AppProps> = ({
  onAction,
  playerStats,
  setPlayerStats,
  currentUser
}) => {
  const [appState, setAppState] = useState<AppState>({
    currentTab: 'home',
    user: {
      id: currentUser.user_id,
      name: 'ë°°ë‹¬ê³ ìˆ˜',
      avatar: 'ðŸ”',
      level: 5,
      points: 2450,
      streak: 7,
      badges: ['ðŸ”¥', 'â­', 'ðŸŽ¯', 'ðŸ’Ž'],
      joinDate: '2024-01-15'
    },
    notifications: [
      {
        id: '1',
        title: 'ë°°ë‹¬ ì™„ë£Œ!',
        message: 'ì¹˜í‚¨ì´ ì„±ê³µì ìœ¼ë¡œ ë°°ë‹¬ë˜ì—ˆìŠµë‹ˆë‹¤.',
        type: 'success',
        timestamp: Date.now() - 1800000,
        read: false
      },
      {
        id: '2',
        title: 'í• ì¸ ì¿ í° ë„ì°©!',
        message: '30% í• ì¸ ì¿ í°ì´ ë°œê¸‰ë˜ì—ˆìŠµë‹ˆë‹¤.',
        type: 'info',
        timestamp: Date.now() - 3600000,
        read: false
      }
    ],
    restaurants: [
      {
        id: 'rest1',
        name: 'ë§›ìžˆëŠ” ì¹˜í‚¨ì§‘',
        category: 'ì¹˜í‚¨',
        image: 'ðŸ—',
        rating: 4.8,
        deliveryTime: '25-35ë¶„',
        deliveryFee: 2500,
        minOrder: 15000,
        isOpen: true,
        tags: ['ì¸ê¸°', 'ë¹ ë¥¸ë°°ë‹¬'],
        distance: '0.8km',
        discount: '30% í• ì¸'
      },
      {
        id: 'rest2',
        name: 'í”¼ìžì™•êµ­',
        category: 'í”¼ìž',
        image: 'ðŸ•',
        rating: 4.6,
        deliveryTime: '30-40ë¶„',
        deliveryFee: 3000,
        minOrder: 20000,
        isOpen: true,
        tags: ['ì‹ ë©”ë‰´'],
        distance: '1.2km'
      },
      {
        id: 'rest3',
        name: 'í•œì‹ë‹¹',
        category: 'í•œì‹',
        image: 'ðŸš',
        rating: 4.7,
        deliveryTime: '20-30ë¶„',
        deliveryFee: 2000,
        minOrder: 12000,
        isOpen: true,
        tags: ['ê±´ê°•í•œ'],
        distance: '0.5km'
      },
      {
        id: 'rest4',
        name: 'ë²„ê±°í‚¹',
        category: 'íŒ¨ìŠ¤íŠ¸í‘¸ë“œ',
        image: 'ðŸ”',
        rating: 4.5,
        deliveryTime: '15-25ë¶„',
        deliveryFee: 1500,
        minOrder: 10000,
        isOpen: false,
        tags: ['24ì‹œê°„'],
        distance: '1.5km'
      }
    ],
    favorites: ['rest1', 'rest3'],
    cart: [],
    orders: [
      {
        id: 'order1',
        restaurantName: 'ë§›ìžˆëŠ” ì¹˜í‚¨ì§‘',
        items: [
          { id: 'item1', restaurantId: 'rest1', name: 'í›„ë¼ì´ë“œ ì¹˜í‚¨', price: 18000, quantity: 1, options: ['ìˆœì‚´'] }
        ],
        totalPrice: 20500,
        status: 'delivered',
        orderTime: '2024-01-15 19:30',
        deliveryTime: '2024-01-15 20:15',
        address: 'ì„œìš¸ì‹œ ê°•ë‚¨êµ¬ í…Œí—¤ëž€ë¡œ 123'
      }
    ],
    currentLocation: 'ì„œìš¸ì‹œ ê°•ë‚¨êµ¬ í…Œí—¤ëž€ë¡œ',
    isLoading: false,
    showNotification: null,
    pullRefresh: { isRefreshing: false, pullDistance: 0 },
    searchQuery: '',
    selectedCategory: 'ì „ì²´'
  });

  const scrollRef = useRef<HTMLDivElement>(null);
  const touchStartY = useRef<number>(0);
  const [frameCount, setFrameCount] = useState(0);

  // Animation loop for smooth effects
  useEffect(() => {
    const animate = () => {
      setFrameCount(prev => prev + 1);
      requestAnimationFrame(animate);
    };
    animate();
  }, []);

  // Simulate real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      // Random point gain simulation
      if (Math.random() < 0.3) {
        const pointGain = Math.floor(Math.random() * 50) + 10;
        setAppState(prev => ({
          ...prev,
          user: { ...prev.user, points: prev.user.points + pointGain }
        }));

        onAction('points_gained', {
          user_id: currentUser.user_id,
          points_gained: pointGain,
          total_points: appState.user.points + pointGain,
          timestamp: Date.now()
        });
      }

      // Random notifications
      if (Math.random() < 0.1) {
        const messages = [
          { title: 'ìƒˆë¡œìš´ ì±Œë¦°ì§€!', message: 'ì˜¤ëŠ˜ì˜ íŠ¹ë³„ ë¯¸ì…˜ì´ ë„ì°©í–ˆìŠµë‹ˆë‹¤!', type: 'info' as const },
          { title: 'ì¹œêµ¬ ìš”ì²­', message: 'ThinkingMasterë‹˜ì´ ì¹œêµ¬ ìš”ì²­ì„ ë³´ëƒˆìŠµë‹ˆë‹¤.', type: 'info' as const },
          { title: 'ë ˆë²¨ì—…!', message: 'ì¶•í•˜í•©ë‹ˆë‹¤! ìƒˆë¡œìš´ ë ˆë²¨ì— ë„ë‹¬í–ˆìŠµë‹ˆë‹¤!', type: 'success' as const }
        ];

        const randomMessage = messages[Math.floor(Math.random() * messages.length)];
        const newNotification: Notification = {
          id: `notif_${Date.now()}`,
          ...randomMessage,
          timestamp: Date.now(),
          read: false
        };

        setAppState(prev => ({
          ...prev,
          notifications: [newNotification, ...prev.notifications.slice(0, 9)],
          showNotification: newNotification
        }));

        // Hide notification after 3 seconds
        setTimeout(() => {
          setAppState(prev => ({ ...prev, showNotification: null }));
        }, 3000);
      }
    }, 5000);

    return () => clearInterval(interval);
  }, [currentUser.user_id, appState.user.points, onAction]);

  // Pull to refresh
  const handleTouchStart = (e: React.TouchEvent) => {
    if (scrollRef.current?.scrollTop === 0) {
      touchStartY.current = e.touches[0].clientY;
    }
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    if (scrollRef.current?.scrollTop === 0) {
      const currentY = e.touches[0].clientY;
      const pullDistance = Math.max(0, Math.min(100, currentY - touchStartY.current));

      setAppState(prev => ({
        ...prev,
        pullRefresh: { ...prev.pullRefresh, pullDistance }
      }));
    }
  };

  const handleTouchEnd = () => {
    if (appState.pullRefresh.pullDistance > 50) {
      setAppState(prev => ({
        ...prev,
        pullRefresh: { isRefreshing: true, pullDistance: 0 },
        isLoading: true
      }));

      // Simulate refresh
      setTimeout(() => {
        setAppState(prev => ({
          ...prev,
          pullRefresh: { isRefreshing: false, pullDistance: 0 },
          isLoading: false,
          user: { ...prev.user, points: prev.user.points + Math.floor(Math.random() * 100) }
        }));
      }, 1500);
    } else {
      setAppState(prev => ({
        ...prev,
        pullRefresh: { ...prev.pullRefresh, pullDistance: 0 }
      }));
    }
  };

  // Tab switching
  const switchTab = (tab: AppState['currentTab']) => {
    setAppState(prev => ({ ...prev, currentTab: tab }));

    onAction('tab_switched', {
      user_id: currentUser.user_id,
      from_tab: appState.currentTab,
      to_tab: tab,
      timestamp: Date.now()
    });
  };

  // Home Screen
  const renderHomeScreen = () => (
    <div style={{ paddingBottom: '20px' }}>
      {/* Pull to refresh indicator */}
      {appState.pullRefresh.pullDistance > 0 && (
        <div style={{
          position: 'absolute',
          top: `-${20 - appState.pullRefresh.pullDistance / 5}px`,
          left: '50%',
          transform: 'translateX(-50%)',
          fontSize: '24px',
          transition: 'all 0.2s ease'
        }}>
          {appState.pullRefresh.pullDistance > 50 ? 'ðŸ”„' : 'â¬‡ï¸'}
        </div>
      )}

      {/* Header with Location and Search */}
      <div style={{
        padding: '20px 20px 15px 20px',
        background: 'white',
        borderBottom: '1px solid #f0f0f0'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '15px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <span style={{ fontSize: '20px' }}>ðŸ“</span>
            <div>
              <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#333' }}>{appState.currentLocation}</div>
              <div style={{ fontSize: '12px', color: '#999' }}>ë°°ë‹¬ì£¼ì†Œ</div>
            </div>
          </div>
          <div style={{ display: 'flex', gap: '10px' }}>
            <div style={{ fontSize: '24px', cursor: 'pointer' }}>ðŸ””</div>
            <div style={{ fontSize: '24px', cursor: 'pointer' }}>ðŸ›’</div>
          </div>
        </div>

        {/* Search Bar */}
        <div style={{
          background: '#f8f8f8',
          borderRadius: '25px',
          padding: '12px 20px',
          display: 'flex',
          alignItems: 'center',
          gap: '10px',
          cursor: 'pointer'
        }} onClick={() => setAppState(prev => ({ ...prev, currentTab: 'search' }))}>
          <span style={{ fontSize: '18px' }}>ðŸ”</span>
          <span style={{ color: '#999', fontSize: '16px' }}>ìŒì‹ì ì´ë‚˜ ë©”ë‰´ë¥¼ ê²€ìƒ‰í•´ë³´ì„¸ìš”</span>
        </div>
      </div>

      {/* Banner */}
      <div style={{ padding: '20px' }}>
        <div style={{
          background: 'linear-gradient(135deg, #ff6b6b 0%, #ffa500 100%)',
          borderRadius: '15px',
          padding: '25px',
          color: 'white',
          position: 'relative',
          overflow: 'hidden'
        }}>
          <div style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '8px' }}>ðŸŽ‰ íŠ¹ë³„ í• ì¸!</div>
          <div style={{ fontSize: '16px', marginBottom: '8px' }}>ì²« ì£¼ë¬¸ì‹œ ìµœëŒ€ 5,000ì› í• ì¸</div>
          <div style={{ fontSize: '14px', opacity: 0.9 }}>ì§€ê¸ˆ ë°”ë¡œ ì£¼ë¬¸í•˜ê³  í˜œíƒ ë°›ìœ¼ì„¸ìš”!</div>
          <div style={{
            position: 'absolute',
            right: '20px',
            top: '50%',
            transform: 'translateY(-50%)',
            fontSize: '40px',
            opacity: 0.3
          }}>ðŸ•</div>
        </div>
      </div>

      {/* Categories */}
      <div style={{ marginBottom: '25px' }}>
        <div style={{ padding: '0 20px 15px 20px' }}>
          <h3 style={{ margin: 0, color: '#333', fontSize: '18px', fontWeight: 'bold' }}>ì¹´í…Œê³ ë¦¬</h3>
        </div>
        <div style={{
          display: 'flex',
          gap: '15px',
          padding: '0 20px',
          overflowX: 'auto',
          scrollbarWidth: 'none',
          msOverflowStyle: 'none'
        }}>
          {[
            { icon: 'ðŸ—', name: 'ì¹˜í‚¨', color: '#ffeb3b' },
            { icon: 'ðŸ•', name: 'í”¼ìž', color: '#ff5722' },
            { icon: 'ðŸš', name: 'í•œì‹', color: '#4caf50' },
            { icon: 'ðŸ”', name: 'ë²„ê±°', color: '#ff9800' },
            { icon: 'ðŸœ', name: 'ì¤‘ì‹', color: '#e91e63' },
            { icon: 'ðŸ±', name: 'ì¼ì‹', color: '#9c27b0' }
          ].map((category, index) => (
            <div
              key={category.name}
              style={{
                minWidth: '70px',
                textAlign: 'center',
                cursor: 'pointer',
                transform: `scale(${1 + Math.sin(frameCount * 0.02 + index) * 0.05})`
              }}
              onClick={() => {
                setAppState(prev => ({ ...prev, selectedCategory: category.name, currentTab: 'search' }));
                onAction('category_selected', { category: category.name, user_id: currentUser.user_id });
              }}
            >
              <div style={{
                width: '60px',
                height: '60px',
                borderRadius: '50%',
                background: category.color,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '28px',
                marginBottom: '8px',
                boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
              }}>
                {category.icon}
              </div>
              <div style={{ fontSize: '12px', fontWeight: '500', color: '#333' }}>{category.name}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Recommended Restaurants */}
      <div style={{ padding: '0 20px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
          <h3 style={{ margin: 0, color: '#333', fontSize: '18px', fontWeight: 'bold' }}>ì¶”ì²œ ë§›ì§‘</h3>
          <span style={{ fontSize: '14px', color: '#999', cursor: 'pointer' }}>ì „ì²´ë³´ê¸° â€º</span>
        </div>

