import React, { useState, useEffect } from 'react';
import { Database, Smartphone, Gamepad2, Wrench, Globe } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { Tabs } from '../../components/Tabs';
import { Input } from '../../components/Input';
import { CodeBlock } from '../../components/CodeBlock';
import { CodeTabs } from '../../components/CodeTabs';

interface Step3Props {
  onComplete: () => void;
  appData: {
    appId: string;
    dataUrl: string;
    selectedVersion: 'saas' | 'private' | null;
  };
  onAppDataChange: (data: any) => void;
}

export const Step3DataIntegration: React.FC<Step3Props> = ({
  onComplete,
  appData,
  onAppDataChange
}) => {
  const [understood, setUnderstood] = useState(false);
  const [hasRequiredData, setHasRequiredData] = useState(false);

  useEffect(() => {
    const hasData = appData.appId.trim() !== '' && appData.dataUrl.trim() !== '';
    setHasRequiredData(hasData);

    if (understood && hasData) {
      onComplete();
    }
  }, [understood, appData, onComplete]);

  const platformTabs = [
    {
      id: 'javascript',
      label: 'JavaScript',
      icon: <Globe size={16} />,
      content: getJavaScriptCode(appData)
    },
    {
      id: 'ios',
      label: 'iOS',
      icon: <Smartphone size={16} />,
      content: getIOSCode(appData)
    },
    {
      id: 'android',
      label: 'Android',
      icon: <Smartphone size={16} />,
      content: getAndroidCode(appData)
    },
    {
      id: 'unity',
      label: 'Unity',
      icon: <Gamepad2 size={16} />,
      content: getUnityCode(appData)
    },
    {
      id: 'unreal',
      label: 'Unreal',
      icon: <Gamepad2 size={16} />,
      content: getUnrealCode(appData)
    },
    {
      id: 'api',
      label: 'RESTful API',
      icon: <Wrench size={16} />,
      content: getAPICode(appData)
    }
  ];

  const tabs = platformTabs.map(platform => ({
    id: platform.id,
    label: (
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        {platform.icon}
        {platform.label}
      </div>
    ),
    content: platform.content
  }));

  return (
    <div>
      {/* Step Title */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '12px',
          marginBottom: '16px' 
        }}>
          <Database size={24} color="#334155" />
          <h3 style={{ 
            fontSize: '24px', 
            fontWeight: '600', 
            color: 'var(--gray-900)',
            margin: 0
          }}>
            3단계: 데이터 연동
          </h3>
        </div>
        <p style={{ 
          fontSize: '16px', 
          color: 'var(--gray-600)',
          lineHeight: '1.6',
          margin: 0
        }}>
          앱에서 발생하는 데이터를 수집하기 위해 SDK를 설치하고 초기화하세요.
          플랫폼별 가이드를 참고하여 연동을 완료할 수 있습니다.
        </p>
      </div>

      {/* App Configuration */}
      <div style={{ marginBottom: '32px' }}>
        <Card>
          <h4 style={{ 
            fontSize: '18px', 
            fontWeight: '600', 
            marginBottom: '16px',
            color: 'var(--gray-900)'
          }}>
            설정 정보 입력
          </h4>

          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
            gap: '16px',
            marginBottom: '16px'
          }}>
            <Input
              label="App ID"
              value={appData.appId}
              onChange={(value) => onAppDataChange({ ...appData, appId: value })}
              placeholder="your-app-id"
              required
            />
            <Input
              label="Data URL"
              value={appData.dataUrl}
              onChange={(value) => onAppDataChange({ ...appData, dataUrl: value })}
              placeholder="https://your-server-url/sync_js"
              required
              type="url"
            />
          </div>

          <InfoBox type="info" title="설정 정보 안내">
            <p style={{ margin: 0 }}>
              App ID와 Data URL은 프로젝트 생성 시 제공되는 고유 식별자입니다. 
              위 정보를 입력하면 아래 코드 예제에 자동으로 반영됩니다.
            </p>
          </InfoBox>
        </Card>
      </div>

      {/* Platform SDK Guides */}
      <div style={{ marginBottom: '32px' }}>
        <h4 style={{ 
          fontSize: '18px', 
          fontWeight: '600', 
          marginBottom: '16px',
          color: 'var(--gray-900)'
        }}>
          플랫폼별 SDK 가이드
        </h4>

        <Tabs tabs={tabs} defaultTab="javascript" />
      </div>

      {/* Additional Resources */}
      <div style={{ marginBottom: '32px' }}>
        <h4 style={{ 
          fontSize: '18px', 
          fontWeight: '700', 
          marginBottom: '24px',
          color: '#2d3748',
          display: 'flex',
          alignItems: 'center',
          gap: '12px'
        }}>
          📚 추가 리소스 및 가이드
        </h4>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '24px',
          marginBottom: '32px'
        }}>
          {/* 핵심 문서 */}
          <div style={{
            background: '#ffffff',
            borderRadius: '20px',
            padding: '28px',
            border: '2px solid #e5e7eb',
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.08), 0 4px 8px rgba(0, 0, 0, 0.04)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            {/* 상단 그라디언트 바 */}
            <div style={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '4px',
              background: 'linear-gradient(90deg, #3b82f6, #1d4ed8)',
              borderRadius: '20px 20px 0 0'
            }} />

            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '16px',
              marginBottom: '20px'
            }}>
              <div style={{
                width: '56px',
                height: '56px',
                background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
                borderRadius: '16px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '24px',
                boxShadow: '0 6px 20px rgba(59, 130, 246, 0.25)',
                flexShrink: 0
              }}>
                📖
              </div>
              <div>
                <h5 style={{ 
                  fontSize: '18px', 
                  fontWeight: '700', 
                  margin: 0,
                  color: '#1f2937',
                  letterSpacing: '-0.025em'
                }}>
                  핵심 문서
                </h5>
                <p style={{ 
                  fontSize: '14px', 
                  color: '#6b7280', 
                  margin: 0,
                  marginTop: '4px',
                  fontWeight: '500'
                }}>
                  필수 설치 가이드 및 FAQ
                </p>
              </div>
            </div>

            <div style={{
              background: 'rgba(59, 130, 246, 0.05)',
              borderRadius: '12px',
              padding: '20px',
              border: '1px solid rgba(59, 130, 246, 0.15)'
            }}>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
                <a 
                  href="https://docs-v2.thinkingdata.kr/?version=latest&lan=ko-KR&code=installation_menu-YfSvwiWwYiX1DtkCMoTcnbOsnsb&anchorId=" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  style={{ 
                    color: '#374151', 
                    textDecoration: 'none', 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '10px',
                    fontSize: '14px',
                    fontWeight: '600',
                    padding: '8px 12px',
                    borderRadius: '8px',
                    background: '#ffffff',
                    border: '1px solid rgba(59, 130, 246, 0.2)',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = 'rgba(59, 130, 246, 0.05)';
                    element.style.transform = 'translateX(4px)';
                  }}
                  onMouseLeave={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = '#ffffff';
                    element.style.transform = 'translateX(0)';
                  }}
                >
                  <span style={{ fontSize: '16px' }}>📋</span>
                  전체 SDK 설치 가이드
                </a>
                <a 
                  href="https://docs-v2.thinkingdata.kr/?version=latest&lan=ko-KR&code=client_sdk_faq&anchorId=" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  style={{ 
                    color: '#374151', 
                    textDecoration: 'none', 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '10px',
                    fontSize: '14px',
                    fontWeight: '600',
                    padding: '8px 12px',
                    borderRadius: '8px',
                    background: '#ffffff',
                    border: '1px solid rgba(59, 130, 246, 0.2)',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = 'rgba(59, 130, 246, 0.05)';
                    element.style.transform = 'translateX(4px)';
                  }}
                  onMouseLeave={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = '#ffffff';
                    element.style.transform = 'translateX(0)';
                  }}
                >
                  <span style={{ fontSize: '16px' }}>❓</span>
                  클라이언트 SDK FAQ
                </a>
              </div>
              <div style={{
                marginTop: '16px',
                padding: '12px',
                background: 'rgba(251, 191, 36, 0.1)',
                borderRadius: '8px',
                border: '1px solid rgba(251, 191, 36, 0.2)'
              }}>
                <span style={{ 
                  color: '#92400e', 
                  fontSize: '13px',
                  fontWeight: '600',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}>
                  💡 고급 설정은 각 플랫폼 문서의 Advanced 섹션을 확인하세요
                </span>
              </div>
            </div>
          </div>

          {/* 데이터 정책 */}
          <div style={{
            background: '#ffffff',
            borderRadius: '20px',
            padding: '28px',
            border: '2px solid #e5e7eb',
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.08), 0 4px 8px rgba(0, 0, 0, 0.04)',
            position: 'relative',
            overflow: 'hidden'
          }}>
            {/* 상단 그라디언트 바 */}
            <div style={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '4px',
              background: 'linear-gradient(90deg, #ec4899, #be185d)',
              borderRadius: '20px 20px 0 0'
            }} />

            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '16px',
              marginBottom: '20px'
            }}>
              <div style={{
                width: '56px',
                height: '56px',
                background: 'linear-gradient(135deg, #ec4899, #be185d)',
                borderRadius: '16px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '24px',
                boxShadow: '0 6px 20px rgba(236, 72, 153, 0.25)',
                flexShrink: 0
              }}>
                🎯
              </div>
              <div>
                <h5 style={{ 
                  fontSize: '18px', 
                  fontWeight: '700', 
                  margin: 0,
                  color: '#1f2937',
                  letterSpacing: '-0.025em'
                }}>
                  데이터 정책
                </h5>
                <p style={{ 
                  fontSize: '14px', 
                  color: '#6b7280', 
                  margin: 0,
                  marginTop: '4px',
                  fontWeight: '500'
                }}>
                  이벤트 트래킹 및 데이터 형식
                </p>
              </div>
            </div>

            <div style={{
              background: 'rgba(236, 72, 153, 0.05)',
              borderRadius: '12px',
              padding: '20px',
              border: '1px solid rgba(236, 72, 153, 0.15)'
            }}>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
                <a 
                  href="https://docs-v2.thinkingdata.kr/?version=latest&lan=ko-KR&code=set_properties&anchorId=" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  style={{ 
                    color: '#374151', 
                    textDecoration: 'none', 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '10px',
                    fontSize: '14px',
                    fontWeight: '600',
                    padding: '8px 12px',
                    borderRadius: '8px',
                    background: '#ffffff',
                    border: '1px solid rgba(236, 72, 153, 0.2)',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = 'rgba(236, 72, 153, 0.05)';
                    element.style.transform = 'translateX(4px)';
                  }}
                  onMouseLeave={(e) => {
                    e.stopPropagation();
                    const element = e.currentTarget as HTMLElement;
                    element.style.background = '#ffffff';
                    element.style.transform = 'translateX(0)';
                  }}
                >
                  <span style={{ fontSize: '16px' }}>📊</span>
                  이벤트 트래킹 정책
                </a>
                <a 
                  href="https://docs-v2.thinkingdata.kr/?version=latest&lan=ko-KR&code=data_format&anchorId=" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  style={{ 
                    color: '#374151', 
                    textDecoration: 'none', 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '10px',
                    fontSize: '14px',
                    fontWeight: '600',
                    padding: '8px 12px',
                    borderRadius: '8px',
                    background: '#ffffff',
                    border: '1px solid rgba(236, 72, 153, 0.2)',
