function getAPICode(appData: any) {
  const { appId, dataUrl } = appData;
  const syncDataUrl = dataUrl ? `${dataUrl}/sync_data` : 'https://YOUR_SERVER_URL/sync_data';
  const syncJsonUrl = dataUrl ? `${dataUrl}/sync_json` : 'https://YOUR_SERVER_URL/sync_json';

  return (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="primary" size="sm" style={{ marginRight: 'var(--space-2)' }}>필수</Badge>
          1. 데이터 수신 API (form-data 전송)
        </h5>
        <CodeBlock
          language="bash"
          title="curl 예제"
          code={`curl "${syncDataUrl}" \\
  --data "appid=${appId || 'YOUR_APP_ID'}&data=%7B%22%23account_id%22%3A%22testing%22%2C%22%23time%22%3A%222019-01-01%2010%3A00%3A00.000%22%2C%22%23type%22%3A%22track%22%2C%22%23event_name%22%3A%22testing%22%2C%22properties%22%3A%7B%22test%22%3A%22test%22%7D%7D"`}
        />
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="primary" size="sm" style={{ marginRight: 'var(--space-2)' }}>필수</Badge>
          2. 데이터 수집 API (raw JSON 전송)
        </h5>
        <CodeBlock
          language="http"
          title="JSON API 방식"
          code={`POST ${syncJsonUrl}
Content-Type: application/json

{
  "appid": "${appId || 'YOUR_APP_ID'}",
  "debug": 0,
  "data": {
    "#type": "track",
    "#event_name": "product_buy",
    "#time": "2019-11-15 11:35:53.648",
    "properties": { 
      "product_name": "상품명",
      "price": 29900 
    },
    "#distinct_id": "user123"
  }
}`}
        />
      </div>

      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px' }}>
          <Badge variant="warning" size="sm" style={{ marginRight: 'var(--space-2)' }}>선택</Badge>
          3. 다중 데이터 전송
        </h5>
        <CodeBlock
          language="json"
          title="배열 형태로 여러 이벤트 전송"
          code={`[
  {
    "appid": "${appId || 'YOUR_APP_ID'}",
    "data": {
      "#type": "track",
      "#event_name": "page_view",
      "#time": "2019-11-15 11:35:53.648",
      "properties": { "page": "/home" },
      "#distinct_id": "user123"
    }
  },
  {
    "appid": "${appId || 'YOUR_APP_ID'}",
    "data": {
      "#type": "track", 
      "#event_name": "button_click",
      "#time": "2019-11-15 11:36:00.000",
      "properties": { "button_name": "구매하기" },
      "#distinct_id": "user123"
    }
  }
]`}
        />
      </div>

      <InfoBox type="info" title="API 사용 안내">
        <p style={{ margin: 0 }}>
          • <strong>sync_data</strong>: form-data 방식으로 데이터를 urlencode하여 전송<br/>
          • <strong>sync_json</strong>: raw JSON 방식으로 데이터를 직접 전송 (권장)<br/>
          • 모든 인터페이스는 UTF-8 인코딩을 사용합니다.
        </p>
      </InfoBox>
    </div>
  );
}
