import React, { useState, useEffect } from 'react';
import { Play, Star, Trophy, Zap, Users, Code, Settings, ChevronRight, Mail, Phone, Video, Shield, BookOpen, HelpCircle, Target, Sparkles, Crown, Sword, Search, User, Home, Gamepad2, BarChart3 } from 'lucide-react';
import { Button } from '../components/Button';

export const HomePage: React.FC = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [focusedCard, setFocusedCard] = useState(0);
  const [screenSize, setScreenSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    const handleResize = () => {
      setScreenSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => {
      clearInterval(timer);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navigateToQuickStart = () => {
    window.location.href = '/quickstart';
  };

  // 반응형 설정
  const isMobile = screenSize.width < 768;
  const isTablet = screenSize.width >= 768 && screenSize.width < 1200;
  const isDesktop = screenSize.width >= 1200;

  return (
    <div style={{
      minHeight: '100vh',
      background: `
        radial-gradient(circle at 20% 50%, #667eea 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, #764ba2 0%, transparent 50%),
        radial-gradient(circle at 40% 80%, #f093fb 0%, transparent 50%),
        linear-gradient(135deg, #0f0c29 0%, #24243e 50%, #313862 100%)
      `,
      padding: isMobile ? '16px' : '24px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    }}>
      {/* Main Container */}
      <div style={{
        maxWidth: isDesktop ? '1400px' : isTablet ? '900px' : '100%',
        margin: '0 auto',
        display: 'grid',
        gridTemplateColumns: isDesktop ? '1fr 2fr 1fr' : isTablet ? '1fr 2fr' : '1fr',
        gridTemplateRows: 'auto auto auto',
        gap: isMobile ? '16px' : '24px',
        gridTemplateAreas: isDesktop 
          ? '"header header header" "sidebar main aside" "footer footer footer"'
          : isTablet 
            ? '"header header" "main sidebar" "footer footer"'
            : '"header" "main" "sidebar" "footer"'
      }}>

        {/* Header */}
        <div style={{
          gridArea: 'header',
          background: 'linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(255,255,255,0.1)',
          borderRadius: '24px',
          padding: isMobile ? '20px' : '32px',
          boxShadow: '0 8px 32px rgba(0,0,0,0.3)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* Animated Background Elements */}
          <div style={{
            position: 'absolute',
            top: '-50%',
            right: '-10%',
            width: '200px',
            height: '200px',
            background: 'linear-gradient(45deg, #667eea, #764ba2)',
            borderRadius: '50%',
            opacity: 0.1,
            animation: 'float 6s ease-in-out infinite'
          }} />

          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            flexWrap: 'wrap',
            gap: '16px'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
              <div style={{
                width: isMobile ? '60px' : '80px',
                height: isMobile ? '60px' : '80px',
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                borderRadius: '20px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: isMobile ? '24px' : '32px',
                boxShadow: '0 8px 24px rgba(102, 126, 234, 0.4)',
                position: 'relative'
              }}>
                📊
                <div style={{
                  position: 'absolute',
                  top: '-8px',
                  right: '-8px',
                  width: '24px',
                  height: '24px',
                  background: 'linear-gradient(135deg, #f093fb, #f5576c)',
                  borderRadius: '50%',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '12px',
                  color: 'white',
                  fontWeight: '700',
                  boxShadow: '0 4px 12px rgba(240, 147, 251, 0.4)'
                }}>
                  ✨
                </div>
              </div>
              <div>
                <h1 style={{
                  fontSize: isMobile ? '24px' : '32px',
                  fontWeight: '800',
                  margin: 0,
                  background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  textShadow: '0 2px 8px rgba(0,0,0,0.3)'
                }}>
                  ThinkingData
                </h1>
                <p style={{
                  fontSize: isMobile ? '14px' : '16px',
                  color: 'rgba(255,255,255,0.8)',
                  margin: 0,
                  fontWeight: '500'
                }}>
                  Master Data Analytics Platform
                </p>
              </div>
            </div>

            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              background: 'rgba(255,255,255,0.1)',
              padding: '12px 16px',
              borderRadius: '16px',
              border: '1px solid rgba(255,255,255,0.2)'
            }}>
              <Crown size={20} style={{ color: '#fbbf24' }} />
              <div>
                <div style={{
                  fontSize: '14px',
                  color: 'rgba(255,255,255,0.9)',
                  fontWeight: '700'
                }}>
                  Level {playerLevel}
                </div>
                <div style={{
                  fontSize: '12px',
                  color: 'rgba(255,255,255,0.7)'
                }}>
                  {experience.toLocaleString()} XP
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div style={{
          gridArea: 'main',
          display: 'flex',
          flexDirection: 'column',
          gap: isMobile ? '16px' : '24px'
        }}>

          {/* Quick Start Hero */}
          <div style={{
            background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.9) 0%, rgba(118, 75, 162, 0.9) 100%)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255,255,255,0.2)',
            borderRadius: '24px',
            padding: isMobile ? '24px' : '40px',
            position: 'relative',
            overflow: 'hidden',
            cursor: 'pointer',
            transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
          }}
          onClick={navigateToQuickStart}
          onMouseEnter={(e) => {
            const element = e.currentTarget as HTMLElement;
            element.style.transform = 'translateY(-4px) scale(1.02)';
            element.style.boxShadow = '0 20px 40px rgba(102, 126, 234, 0.4)';
          }}
          onMouseLeave={(e) => {
            const element = e.currentTarget as HTMLElement;
            element.style.transform = 'translateY(0) scale(1)';
            element.style.boxShadow = '0 8px 32px rgba(0,0,0,0.3)';
          }}>

            {/* Epic Glow Effect */}
            <div style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              width: '200px',
              height: '200px',
              background: 'radial-gradient(circle, rgba(255,255,255,0.2) 0%, transparent 70%)',
              animation: 'pulse 3s ease-in-out infinite'
            }} />

            <div style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              position: 'relative',
              zIndex: 1
            }}>
              <div style={{ flex: 1 }}>
