import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  damage: number;
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  description: string;
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet' | 'consumable';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // Î¨ºÏïΩÏö© ÏÜçÏÑ±Îì§
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // Î≤ÑÌîÑ ÏßÄÏÜçÏãúÍ∞Ñ (Î∞ÄÎ¶¨Ï¥à)
    buffType?: 'attack' | 'defense' | 'speed'; // Î≤ÑÌîÑ ÌÉÄÏûÖ
    buffAmount?: number; // Î≤ÑÌîÑ ÏàòÏπò
  };
  quantity?: number; // ÏÜåÎπÑ ÏïÑÏù¥ÌÖú ÏàòÎüâ
}



interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const { colors } = useTheme();
  const { t } = useLanguage();
  const [character, setCharacter] = useState<Character>({
    id: '1',
    name: 'Ïö©ÏÇ¨',
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: 'Ï†ÑÏÇ¨',
    skills: [],
    equipment: {},
    stats: {
      strength: 10,
      agility: 8,
      intelligence: 5,
      vitality: 12,
      luck: 7,
      availablePoints: 0
    },
    prestige: 0,
    rebirth: 0,
    buffs: {
      attack: { amount: 0, duration: 0 },
      defense: { amount: 0, duration: 0 },
      speed: { amount: 0, duration: 0 }
    }
  });

  const [currentTab, setCurrentTab] = useState<'lobby' | 'dungeon' | 'character' | 'inventory' | 'skills' | 'shop'>('lobby');
  const [gameMode, setGameMode] = useState<'lobby' | 'dungeon' | 'combat'>('lobby');
  const [isAutoFighting, setIsAutoFighting] = useState(false);
  const [currentMonster, setCurrentMonster] = useState<Monster | null>(null);
  const [combatLog, setCombatLog] = useState<string[]>([]);
  const [selectedDungeon, setSelectedDungeon] = useState<Dungeon | null>(null);
  const [inventory, setInventory] = useState<Item[]>([]);
  const [availableDungeons, setAvailableDungeons] = useState<Dungeon[]>([]);
  const [currentDungeon, setCurrentDungeon] = useState<Dungeon | null>(null);
  const [inDungeon, setInDungeon] = useState(false);
  const [guild, setGuild] = useState<Guild | null>(null);
  const [shop, setShop] = useState<Item[]>([]);
  const [achievements, setAchievements] = useState<string[]>([]);
  const [monstersDefeated, setMonstersDefeated] = useState(0);
  const [totalGoldEarned, setTotalGoldEarned] = useState(0);

  const logRef = useRef<HTMLDivElement>(null);

  // Í≤åÏûÑ Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    initializeGame();
  }, []);

  // Ï∫êÎ¶≠ÌÑ∞ ÏÉÅÌÉúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ userStats ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    setUserStats({
      level: character.level,
      gold: character.gold,
      attack: character.attack,
      defense: character.defense,
      hp: character.hp,
      maxHp: character.maxHp,
      exp: character.exp,
      maxExp: character.maxExp,
      monsters_defeated: monstersDefeated,
      character_class: character.class,
      total_gold_earned: totalGoldEarned,
      inventory_items: inventory.map(item => ({
        id: item.id,
        name: item.name,
        type: item.type,
        rarity: item.rarity,
        quantity: item.quantity || 1,
        stats: item.stats
      })),
      skills: character.skills.map(skill => ({
        id: skill.id,
        name: skill.name,
        level: skill.level,
        type: skill.type
      })),
      current_buffs: Object.entries(character.buffs)
        .filter(([_, buff]) => buff.duration > 0)
        .map(([type, buff]) => ({
          type,
          amount: buff.amount,
          duration: Math.ceil(buff.duration / 1000)
        }))
    });
  }, [character, inventory, monstersDefeated, totalGoldEarned, setUserStats]);

  const initializeGame = () => {
    // Ï¥àÍ∏∞ Ïä§ÌÇ¨ ÏÑ§Ï†ï
    const initialSkills: Skill[] = [
      {
        id: 'slash',
        name: 'Í∞ïÎ†•Ìïú Î≤†Í∏∞',
        level: 1,
        maxLevel: 10,
        damage: 30,
        manaCost: 10,
        cooldown: 3000,
        currentCooldown: 0,
        type: 'active',
        description: 'Í∞ïÎ†•Ìïú ÏùºÍ≤©ÏùÑ Í∞ÄÌï©ÎãàÎã§.'
      },
      {
        id: 'heal',
        name: 'ÏπòÏú†',
        level: 1,
        maxLevel: 10,
        damage: 0,
        manaCost: 15,
        cooldown: 5000,
        currentCooldown: 0,
        type: 'active',
        description: 'HPÎ•º ÌöåÎ≥µÌï©ÎãàÎã§.'
      },
      {
        id: 'critical',
        name: 'ÏπòÎ™ÖÌÉÄ ÏàôÎ†®',
        level: 1,
        maxLevel: 10,
        damage: 0,
        manaCost: 0,
        cooldown: 0,
        currentCooldown: 0,
        type: 'passive',
        description: 'ÏπòÎ™ÖÌÉÄ ÌôïÎ•†ÏùÑ Ï¶ùÍ∞ÄÏãúÌÇµÎãàÎã§.'
      }
    ];

    // ÎçòÏ†Ñ Ï¥àÍ∏∞Ìôî
    const initialDungeons: Dungeon[] = [
      {
        id: 'forest',
        name: 'Ïñ¥Îë†Ïùò Ïà≤',
        level: 1,
        description: 'Ï¥àÎ≥¥ÏûêÎ•º ÏúÑÌïú ÎçòÏ†ÑÏûÖÎãàÎã§.',
        floors: 10,
        currentFloor: 1,
        monsters: [],
        rewards: {
          goldRange: [10, 25],
          expRange: [15, 30],
          itemDropChance: 0.1
        },
        requiredLevel: 1,
        isUnlocked: true
      },
      {
        id: 'cave',
        name: 'Í≥†Î∏îÎ¶∞ ÎèôÍµ¥',
        level: 5,
        description: 'Í≥†Î∏îÎ¶∞Îì§Ïù¥ ÏÑúÏãùÌïòÎäî ÏúÑÌóòÌïú ÎèôÍµ¥ÏûÖÎãàÎã§.',
        floors: 15,
        currentFloor: 1,
        monsters: [],
        rewards: {
          goldRange: [25, 50],
          expRange: [40, 75],
          itemDropChance: 0.15
        },
        requiredLevel: 5,
        isUnlocked: false
      },
      {
        id: 'tower',
        name: 'ÎßàÎ≤ïÏÇ¨Ïùò ÌÉë',
        level: 10,
        description: 'Í∞ïÎ†•Ìïú ÎßàÎ≤ï Î™¨Ïä§ÌÑ∞Îì§Ïù¥ ÏûàÎäî ÌÉëÏûÖÎãàÎã§.',
        floors: 20,
        currentFloor: 1,
        monsters: [],
        rewards: {
          goldRange: [50, 100],
          expRange: [80, 150],
          itemDropChance: 0.2
        },
        requiredLevel: 10,
        isUnlocked: false
      }
    ];

    // ÏÉÅÏ†ê ÏïÑÏù¥ÌÖú Ï¥àÍ∏∞Ìôî
    const shopItems: Item[] = [
      {
        id: 'sword1',
        name: 'Í∞ïÏ≤† Í≤Ä',
        type: 'weapon',
        rarity: 'common',
        level: 1,
        stats: { attack: 15 },
        price: 200,
        enhancement: 0
      },
      {
        id: 'armor1',
        name: 'Í∞ÄÏ£Ω Í∞ëÏò∑',
        type: 'armor',
        rarity: 'common',
        level: 1,
        stats: { defense: 10, hp: 20 },
        price: 150,
        enhancement: 0
      },
      {
        id: 'helmet1',
        name: 'Ï≤†Ï†ú Ìà¨Íµ¨',
        type: 'helmet',
        rarity: 'common',
        level: 1,
        stats: { defense: 5, hp: 15 },
        price: 100,
        enhancement: 0
      },
      // Î¨ºÏïΩÎì§
      {
        id: 'health_potion',
        name: 'Ï≤¥Î†• Î¨ºÏïΩ',
        type: 'consumable',
        rarity: 'common',
        level: 1,
        stats: {},
        price: 50,
        enhancement: 0,
        consumableEffect: {
          healHp: 50
        },
        quantity: 1
      },
      {
        id: 'mana_potion',
        name: 'ÎßàÎÇò Î¨ºÏïΩ',
        type: 'consumable',
        rarity: 'common',
        level: 1,
        stats: {},
        price: 40,
        enhancement: 0,
        consumableEffect: {
          healMp: 30
        },
        quantity: 1
      },
      {
        id: 'greater_health_potion',
        name: 'Í≥†Í∏â Ï≤¥Î†• Î¨ºÏïΩ',
        type: 'consumable',
        rarity: 'rare',
        level: 2,
        stats: {},
        price: 120,
        enhancement: 0,
        consumableEffect: {
          healHp: 120
        },
        quantity: 1
      },
      {
        id: 'strength_potion',
        name: 'ÌûòÏùò Î¨ºÏïΩ',
        type: 'consumable',
        rarity: 'rare',
        level: 2,
        stats: {},
        price: 100,
        enhancement: 0,
        consumableEffect: {
          buffType: 'attack',
          buffAmount: 10,
          buffDuration: 30000
        },
        quantity: 1
      },
      {
        id: 'defense_potion',
        name: 'Î∞©Ïñ¥Ïùò Î¨ºÏïΩ',
        type: 'consumable',
        rarity: 'rare',
        level: 2,
        stats: {},
        price: 100,
        enhancement: 0,
        consumableEffect: {
          buffType: 'defense',
          buffAmount: 8,
          buffDuration: 30000
        },
        quantity: 1
      }
    ];

    setCharacter(prev => ({ ...prev, skills: initialSkills }));
    setAvailableDungeons(initialDungeons);
    setShop(shopItems);
    spawnMonster();
  };

  // Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ±
  const spawnMonster = () => {
    // Ïù¥ÎØ∏ Î™¨Ïä§ÌÑ∞Í∞Ä ÏûàÍ±∞ÎÇò ÎçòÏ†ÑÏóê ÏûàÏúºÎ©¥ ÏÉàÎ°ú ÏÉùÏÑ±ÌïòÏßÄ ÏïäÏùå
    if (currentMonster || inDungeon) return;
    const monsters = [
      { name: 'Ïä¨ÎùºÏûÑ', baseHp: 40, baseAttack: 8, baseDefense: 2, exp: 15, gold: 10 },
      { name: 'Í≥†Î∏îÎ¶∞', baseHp: 60, baseAttack: 12, baseDefense: 4, exp: 25, gold: 18 },
      { name: 'Ïò§ÌÅ¨', baseHp: 100, baseAttack: 18, baseDefense: 8, exp: 40, gold: 30 },
      { name: 'Ìä∏Î°§', baseHp: 150, baseAttack: 25, baseDefense: 12, exp: 60, gold: 45 },
      { name: 'ÎìúÎûòÍ≥§', baseHp: 300, baseAttack: 40, baseDefense: 20, exp: 120, gold: 100 }
    ];

    const randomMonster = monsters[Math.floor(Math.random() * monsters.length)];
    const levelMultiplier = 1 + (character.level - 1) * 0.3;

    const monster: Monster = {
      id: `monster_${Date.now()}`,
      name: randomMonster.name,
      level: Math.max(1, character.level + Math.floor(Math.random() * 3 - 1)),
      hp: Math.floor(randomMonster.baseHp * levelMultiplier),
      maxHp: Math.floor(randomMonster.baseHp * levelMultiplier),
      attack: Math.floor(randomMonster.baseAttack * levelMultiplier),
      defense: Math.floor(randomMonster.baseDefense * levelMultiplier),
      speed: 10,
      exp: Math.floor(randomMonster.exp * levelMultiplier),
      gold: Math.floor(randomMonster.gold * levelMultiplier),
      dropItems: generateDropItems(),
      isBoss: Math.random() < 0.1 // 10% Î≥¥Ïä§ ÌôïÎ•†
    };

    if (monster.isBoss) {
      monster.name = `Î≥¥Ïä§ ${monster.name}`;
      monster.hp *= 3;
      monster.maxHp *= 3;
      monster.attack *= 2;
      monster.defense *= 2;
      monster.exp *= 5;
      monster.gold *= 3;
    }

    setCurrentMonster(monster);
    addToCombatLog(`${monster.name} Lv.${monster.level}Ïù¥ ÎÇòÌÉÄÎÇ¨Îã§!`);
  };

  // ÎìúÎ°≠ ÏïÑÏù¥ÌÖú ÏÉùÏÑ±
  const generateDropItems = (): Item[] => {
    const dropChance = Math.random();
    if (dropChance < 0.1) { // 10% ÌôïÎ•†Î°ú ÏïÑÏù¥ÌÖú ÎìúÎ°≠
      const rarities: Array<'common' | 'rare' | 'epic' | 'legendary' | 'mythic'> = ['common', 'rare', 'epic', 'legendary', 'mythic'];
      const rarity = rarities[Math.floor(Math.random() * rarities.length)];

      return [{
        id: `drop_${Date.now()}`,
        name: generateItemName(rarity),
        type: ['weapon', 'armor', 'helmet', 'boots', 'ring', 'amulet'][Math.floor(Math.random() * 6)] as any,
        rarity,
        level: character.level,
        stats: generateItemStats(rarity),
        price: calculateItemPrice(rarity, character.level),
        enhancement: 0
      }];
    }
    return [];
  };

  const generateItemName = (rarity: string): string => {
    const prefixes = {
      common: ['ÎÇ°ÏùÄ', 'ÏùºÎ∞òÏ†ÅÏù∏', 'Í∏∞Î≥∏'],
      rare: ['ÎÇ†Ïπ¥Î°úÏö¥', 'ÌäºÌäºÌïú', 'ÎßàÎ≤ïÏùò'],
      epic: ['Ï†ÑÏÑ§Ï†ÅÏù∏', 'Í≥†ÎåÄÏùò', 'Ïã†ÏÑ±Ìïú'],
      legendary: ['Ï†ÑÏÑ§Ïùò', 'ÏòÅÏõÖÏùò', 'Î∂àÎ©∏Ïùò'],
      mythic: ['Ïã†ÌôîÏùò', 'Ï∞ΩÏ°∞Ïùò', 'Ï†àÎåÄÏùò']
    };

    const items = ['Í≤Ä', 'Í∞ëÏò∑', 'Ìà¨Íµ¨', 'Ïã†Î∞ú', 'Î∞òÏßÄ', 'Î™©Í±∏Ïù¥'];
    const prefix = prefixes[rarity as keyof typeof prefixes][Math.floor(Math.random() * 3)];
    const item = items[Math.floor(Math.random() * items.length)];

    return `${prefix} ${item}`;
  };

  const generateItemStats = (rarity: string) => {
    const multipliers = {
      common: 1,
      rare: 1.5,
      epic: 2.5,
      legendary: 4,
      mythic: 7
    };

    const base = multipliers[rarity as keyof typeof multipliers];
    return {
      attack: Math.floor(Math.random() * 20 * base),
      defense: Math.floor(Math.random() * 15 * base),
      hp: Math.floor(Math.random() * 50 * base),
      mp: Math.floor(Math.random() * 30 * base),
      speed: Math.floor(Math.random() * 10 * base),
      critRate: Math.floor(Math.random() * 10 * base),
      critDamage: Math.floor(Math.random() * 20 * base)
    };
  };

  const calculateItemPrice = (rarity: string, level: number): number => {
    const basePrice = {
      common: 50,
      rare: 150,
      epic: 500,
      legendary: 2000,
      mythic: 10000
    };

    return Math.floor(basePrice[rarity as keyof typeof basePrice] * (1 + level * 0.2));
  };

  // Ï†ÑÌà¨ ÏãúÏä§ÌÖú
  const attack = () => {
    if (!currentMonster) return;

    const buffedAttack = character.attack + character.buffs.attack.amount;
    const damage = calculateDamage(buffedAttack, currentMonster.defense);
    const isCritical = Math.random() < 0.1 + (character.stats.luck * 0.01);
    const finalDamage = isCritical ? Math.floor(damage * 1.5) : damage;

    const newMonsterHp = Math.max(0, currentMonster.hp - finalDamage);
    setCurrentMonster(prev => prev ? { ...prev, hp: newMonsterHp } : null);

    addToCombatLog(`${character.name}Ïùò Í≥µÍ≤©! ${finalDamage}${isCritical ? ' (ÏπòÎ™ÖÌÉÄ!)' : ''} Îç∞ÎØ∏ÏßÄ!`);

    onAction('combat_attack', {
      user_id: currentUser.user_id,
      damage: finalDamage,
      is_critical: isCritical,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level,
      character_level: character.level,
      character_attack: character.attack,
      monster_hp_remaining: newMonsterHp,
      dungeon_name: currentDungeon?.name || null,
      dungeon_floor: currentDungeon?.currentFloor || null
    });

    if (newMonsterHp <= 0) {
      defeatMonster();
    } else {
      // Î™¨Ïä§ÌÑ∞ Î∞òÍ≤©
      setTimeout(() => monsterAttack(), 1000);
    }
  };

  const monsterAttack = () => {
    if (!currentMonster || currentMonster.hp <= 0) return;

    const buffedDefense = character.defense + character.buffs.defense.amount;
    const damage = calculateDamage(currentMonster.attack, buffedDefense);
    const newHp = Math.max(0, character.hp - damage);

    setCharacter(prev => ({ ...prev, hp: newHp }));
    addToCombatLog(`${currentMonster.name}Ïùò Í≥µÍ≤©! ${damage} Îç∞ÎØ∏ÏßÄ!`);

    if (newHp <= 0) {
      addToCombatLog('Ï†ÑÌà¨ÏóêÏÑú Ìå®Î∞∞ÌñàÏäµÎãàÎã§...');
      setIsAutoFighting(false);

      // Ï†ÑÌà¨ Ïã§Ìå® Ïù¥Î≤§Ìä∏ Î°úÍπÖ
      onAction('combat_defeat', {
        monster_name: currentMonster.name,
        monster_level: currentMonster.level,
        character_level: character.level,
        dungeon_id: currentDungeon?.id || 'field',
        dungeon_name: currentDungeon?.name || 'field',
        damage_taken: currentMonster.attack,
        character_defense: character.defense
      });

      // ÎçòÏ†ÑÏóêÏÑú ÎÇòÍ∞ÄÍ≥† Ï≤¥Î†•/ÎßàÎÇò ÏôÑÏ†Ñ ÌöåÎ≥µ
      setTimeout(() => {
        setCharacter(prev => ({
          ...prev,
          hp: prev.maxHp,
          mp: prev.maxMp
        }));

        if (inDungeon && currentDungeon) {
          // ÎçòÏ†ÑÏóêÏÑú ÎÇòÍ∞ÄÍ∏∞ (Ï£ΩÏùåÏúºÎ°ú Ïù∏Ìïú ÏûêÎèô Ìá¥Ïû•)
          onAction('dungeon_exited', {
            dungeon_id: currentDungeon.id,
            dungeon_name: currentDungeon.name,
            current_floor: currentDungeon.currentFloor,
            total_floors: currentDungeon.floors,
            character_level: character.level,
            exit_reason: 'death',
            time_spent: new Date().getTime()
          });
          setInDungeon(false);
          setCurrentDungeon(null);
          setCurrentTab('lobby');
          setGameMode('lobby');
          addToCombatLog('ÎçòÏ†ÑÏóêÏÑú ÎÇòÏôîÏäµÎãàÎã§. Ï≤¥Î†•Í≥º ÎßàÎÇòÍ∞Ä ÏôÑÏ†ÑÌûà ÌöåÎ≥µÎêòÏóàÏäµÎãàÎã§!');
        } else {
          // ÏùºÎ∞ò ÌïÑÎìúÏóêÏÑúÎäî ÏÉà Î™¨Ïä§ÌÑ∞ Ïä§Ìè∞
          addToCombatLog('Ï≤¥Î†•Í≥º ÎßàÎÇòÍ∞Ä ÏôÑÏ†ÑÌûà ÌöåÎ≥µÎêòÏóàÏäµÎãàÎã§!');
          spawnMonster();
        }
      }, 2000);
    }
  };

  const calculateDamage = (attack: number, defense: number): number => {
    const baseDamage = Math.max(1, attack - Math.floor(defense * 0.5));
    return Math.floor(baseDamage * (0.8 + Math.random() * 0.4));
  };

  const defeatMonster = () => {
    if (!currentMonster) return;

    const expGained = currentMonster.exp;
    const goldGained = currentMonster.gold;

    setCharacter(prev => {
      const newExp = prev.exp + expGained;
      const newGold = prev.gold + goldGained;
      let newLevel = prev.level;
      let newMaxExp = prev.maxExp;
      let newHp = prev.hp;
      let newMaxHp = prev.maxHp;
      let availablePoints = prev.stats.availablePoints;

      // Î†àÎ≤®ÏóÖ Ï≤¥ÌÅ¨
      if (newExp >= prev.maxExp) {
        newLevel += 1;
        newMaxExp = Math.floor(prev.maxExp * 1.5);
        newMaxHp += 20;
        newHp = newMaxHp; // Î†àÎ≤®ÏóÖ Ïãú Ï≤¥Î†• ÏôÑÏ†Ñ ÌöåÎ≥µ
        availablePoints += 5;

        addToCombatLog(`üéâ Î†àÎ≤®ÏóÖ! Lv.${newLevel}Ïù¥ ÎêòÏóàÏäµÎãàÎã§!`);

        onAction('level_up', {
          user_id: currentUser.user_id,
          new_level: newLevel,
          character_class: prev.class,
