          setCurrentDungeon(updatedDungeon);
          spawnDungeonMonster(updatedDungeon);
          addToCombatLog(`${updatedDungeon.currentFloor}Ï∏µÏúºÎ°ú ÏßÑÌñâÌï©ÎãàÎã§!`);
        } else {
