                opacity: character.hp > 0 ? 1 : 0.5
              }}
            >
              ‚öîÔ∏è Í≥µÍ≤©
            </button>

            <button
              onClick={() => setIsAutoFighting(!isAutoFighting)}
              disabled={character.hp <= 0}
              style={{
                background: isAutoFighting 
                  ? 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)'
                  : 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '10px',
                padding: '12px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: character.hp > 0 ? 'pointer' : 'not-allowed',
                opacity: character.hp > 0 ? 1 : 0.5
              }}
            >
              {isAutoFighting ? '‚èπÔ∏è ÏûêÎèô Ï§ëÏßÄ' : '‚ñ∂Ô∏è ÏûêÎèô Ï†ÑÌà¨'}
            </button>

            <div style={{ marginTop: '10px' }}>
              <h4 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>Ïä§ÌÇ¨</h4>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                {character.skills.map(skill => (
                  <button
                    key={skill.id}
                    onClick={() => {
                      if (character.mp < skill.manaCost || skill.currentCooldown > 0) return;

                      setCharacter(prev => ({ ...prev, mp: prev.mp - skill.manaCost }));

                      if (skill.id === 'heal') {
                        const healAmount = Math.floor(character.maxHp * 0.3);
                        setCharacter(prev => ({ 
                          ...prev, 
                          hp: Math.min(prev.maxHp, prev.hp + healAmount) 
                        }));
                        addToCombatLog(`ÏπòÏú† Ïä§ÌÇ¨ ÏÇ¨Ïö©! HP +${healAmount}`);
                      } else {
                        // Í≥µÍ≤© Ïä§ÌÇ¨
                        if (currentMonster) {
                          const damage = skill.damage + Math.floor(character.attack * 0.5);
                          const newMonsterHp = Math.max(0, currentMonster.hp - damage);
                          setCurrentMonster(prev => prev ? { ...prev, hp: newMonsterHp } : null);
                          addToCombatLog(`${skill.name} ÏÇ¨Ïö©! ${damage} Îç∞ÎØ∏ÏßÄ!`);

                          if (newMonsterHp <= 0) {
                            defeatMonster();
                          }
                        }
                      }

                      // Ïø®Îã§Ïö¥ ÏÑ§Ï†ï
                      setCharacter(prev => ({
                        ...prev,
                        skills: prev.skills.map(s => 
                          s.id === skill.id ? { ...s, currentCooldown: skill.cooldown } : s
                        )
                      }));
                    }}
                    disabled={character.mp < skill.manaCost || skill.currentCooldown > 0}
                    style={{
                      background: skill.currentCooldown > 0 || character.mp < skill.manaCost 
                        ? '#6b7280' 
                        : 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)',
                      color: 'white',
                      border: 'none',
                      borderRadius: '8px',
                      padding: '8px 12px',
                      fontSize: '14px',
                      cursor: (character.mp >= skill.manaCost && skill.currentCooldown === 0) ? 'pointer' : 'not-allowed',
                      opacity: (character.mp >= skill.manaCost && skill.currentCooldown === 0) ? 1 : 0.6
                    }}
                  >
                    {skill.name} (MP: {skill.manaCost})
                    {skill.currentCooldown > 0 && ` (${Math.ceil(skill.currentCooldown / 1000)}s)`}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Ï†ÑÌà¨ Î°úÍ∑∏ */}
      <div style={{
        background: '#374151',
        borderRadius: '15px',
        padding: '20px'
      }}>
        <h3 style={{ color: 'white', margin: '0 0 15px 0' }}>Ï†ÑÌà¨ Î°úÍ∑∏</h3>
