sourcesContent:
import React, { useState, useEffect, useRef } from 'react';

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  damage: number;
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  description: string;
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet' | 'consumable';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // 물약용 속성들
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // 버프 지속시간 (밀리초)
    buffType?: 'attack' | 'defense' | 'speed'; // 버프 타입
    buffAmount?: number; // 버프 수치
  };
  quantity?: number; // 소비 아이템 수량
}



interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const [character, setCharacter] = useState<Character>({
    id: '1',
    name: '용사',
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: '전사',
    skills: [],
    equipment: {},
    stats: {
      strength: 10,
      agility: 8,
      intelligence: 5,
      vitality: 12,
      luck: 7,
      availablePoints: 0
    },
    prestige: 0,
    rebirth: 0,
    buffs: {
      attack: { amount: 0, duration: 0 },
      defense: { amount: 0, duration: 0 },
      speed: { amount: 0, duration: 0 }
    }
  });

  const [currentTab, setCurrentTab] = useState<'lobby' | 'dungeon' | 'character' | 'inventory' | 'skills' | 'shop'>('lobby');
  const [gameMode, setGameMode] = useState<'lobby' | 'dungeon' | 'combat'>('lobby');
  const [isAutoFighting, setIsAutoFighting] = useState(false);
  const [currentMonster, setCurrentMonster] = useState<Monster | null>(null);
  const [combatLog, setCombatLog] = useState<string[]>([]);
  const [selectedDungeon, setSelectedDungeon] = useState<Dungeon | null>(null);
  const [inventory, setInventory] = useState<Item[]>([]);
  const [availableDungeons, setAvailableDungeons] = useState<Dungeon[]>([]);
  const [currentDungeon, setCurrentDungeon] = useState<Dungeon | null>(null);
  const [inDungeon, setInDungeon] = useState(false);
  const [guild, setGuild] = useState<Guild | null>(null);
  const [shop, setShop] = useState<Item[]>([]);
  const [achievements, setAchievements] = useState<string[]>([]);
  const [monstersDefeated, setMonstersDefeated] = useState(0);
  const [totalGoldEarned, setTotalGoldEarned] = useState(0);

  const logRef = useRef<HTMLDivElement>(null);

  // 게임 초기화
  useEffect(() => {
    initializeGame();
  }, []);

  // 캐릭터 상태가 변경될 때마다 userStats 업데이트
  useEffect(() => {
    setUserStats({
      level: character.level,
      gold: character.gold,
      attack: character.attack,
      defense: character.defense,
      hp: character.hp,
      maxHp: character.maxHp,
      exp: character.exp,
      maxExp: character.maxExp,
      monsters_defeated: monstersDefeated,
      character_class: character.class,
      total_gold_earned: totalGoldEarned,
      inventory_items: inventory.map(item => ({
        id: item.id,
        name: item.name,
        type: item.type,
        rarity: item.rarity,
        quantity: item.quantity || 1,
        stats: item.stats
      })),
      skills: character.skills.map(skill => ({
        id: skill.id,
        name: skill.name,
        level: skill.level,
        type: skill.type
      })),
      current_buffs: Object.entries(character.buffs)
        .filter(([_, buff]) => buff.duration > 0)
        .map(([type, buff]) => ({
          type,
          amount: buff.amount,
          duration: Math.ceil(buff.duration / 1000)
        }))
    });
  }, [character, inventory, monstersDefeated, totalGoldEarned, setUserStats]);

  const initializeGame = () => {
    // 초기 스킬 설정
    const initialSkills: Skill[] = [
      {
        id: 'slash',
        name: '강력한 베기',
        level: 1,
        maxLevel: 10,
        damage: 30,
        manaCost: 10,
        cooldown: 3000,
        currentCooldown: 0,
        type: 'active',
        description: '강력한 일격을 가합니다.'
      },
      {
        id: 'heal',
        name: '치유',
        level: 1,
        maxLevel: 10,
        damage: 0,
        manaCost: 15,
        cooldown: 5000,
        currentCooldown: 0,
        type: 'active',
        description: 'HP를 회복합니다.'
      },
      {
        id: 'critical',
        name: '치명타 숙련',
        level: 1,
        maxLevel: 10,
        damage: 0,
        manaCost: 0,
        cooldown: 0,
        currentCooldown: 0,
        type: 'passive',
        description: '치명타 확률을 증가시킵니다.'
      }
    ];

    // 던전 초기화
    const initialDungeons: Dungeon[] = [
      {
        id: 'forest',
        name: '어둠의 숲',
        level: 1,
        description: '초보자를 위한 던전입니다.',
        floors: 10,
        currentFloor: 1,
        monsters: [],
        rewards: {
          goldRange: [10, 25],
          expRange: [15, 30],
          itemDropChance: 0.1
        },
        requiredLevel: 1,
        isUnlocked: true
      },
      {
        id: 'cave',
        name: '고블린 동굴',
        level: 5,
        description: '고블린들이 서식하는 위험한 동굴입니다.',
        floors: 15,
        currentFloor: 1,
        monsters: [],
        rewards: {
          goldRange: [25, 50],
          expRange: [40, 75],
          itemDropChance: 0.15
        },
        requiredLevel: 5,
        isUnlocked: false
      },
      {
        id: 'tower',
        name: '마법사의 탑',
        level: 10,
        description: '강력한 마법 몬스터들이 있는 탑입니다.',
        floors: 20,
        currentFloor: 1,
        monsters: [],
        rewards: {
          goldRange: [50, 100],
          expRange: [80, 150],
          itemDropChance: 0.2
        },
        requiredLevel: 10,
        isUnlocked: false
      }
    ];

    // 상점 아이템 초기화
    const shopItems: Item[] = [
      {
        id: 'sword1',
        name: '강철 검',
        type: 'weapon',
        rarity: 'common',
        level: 1,
        stats: { attack: 15 },
        price: 200,
        enhancement: 0
      },
      {
        id: 'armor1',
        name: '가죽 갑옷',
        type: 'armor',
        rarity: 'common',
        level: 1,
        stats: { defense: 10, hp: 20 },
        price: 150,
        enhancement: 0
      },
      {
        id: 'helmet1',
        name: '철제 투구',
        type: 'helmet',
        rarity: 'common',
        level: 1,
        stats: { defense: 5, hp: 15 },
        price: 100,
        enhancement: 0
      },
      // 물약들
      {
        id: 'health_potion',
        name: '체력 물약',
        type: 'consumable',
        rarity: 'common',
        level: 1,
        stats: {},
        price: 50,
        enhancement: 0,
        consumableEffect: {
          healHp: 50
        },
        quantity: 1
      },
      {
        id: 'mana_potion',
        name: '마나 물약',
        type: 'consumable',
        rarity: 'common',
        level: 1,
        stats: {},
        price: 40,
        enhancement: 0,
        consumableEffect: {
          healMp: 30
        },
        quantity: 1
      },
      {
        id: 'greater_health_potion',
        name: '고급 체력 물약',
        type: 'consumable',
        rarity: 'rare',
        level: 2,
        stats: {},
        price: 120,
        enhancement: 0,
        consumableEffect: {
          healHp: 120
        },
        quantity: 1
      },
      {
        id: 'strength_potion',
        name: '힘의 물약',
        type: 'consumable',
        rarity: 'rare',
        level: 2,
        stats: {},
        price: 100,
        enhancement: 0,
        consumableEffect: {
          buffType: 'attack',
          buffAmount: 10,
          buffDuration: 30000
        },
        quantity: 1
      },
      {
        id: 'defense_potion',
        name: '방어의 물약',
        type: 'consumable',
        rarity: 'rare',
        level: 2,
        stats: {},
        price: 100,
        enhancement: 0,
        consumableEffect: {
          buffType: 'defense',
          buffAmount: 8,
          buffDuration: 30000
        },
        quantity: 1
      }
    ];

    setCharacter(prev => ({ ...prev, skills: initialSkills }));
    setAvailableDungeons(initialDungeons);
    setShop(shopItems);
    spawnMonster();
  };

  // 몬스터 생성
  const spawnMonster = () => {
    const monsters = [
      { name: '슬라임', baseHp: 40, baseAttack: 8, baseDefense: 2, exp: 15, gold: 10 },
      { name: '고블린', baseHp: 60, baseAttack: 12, baseDefense: 4, exp: 25, gold: 18 },
      { name: '오크', baseHp: 100, baseAttack: 18, baseDefense: 8, exp: 40, gold: 30 },
      { name: '트롤', baseHp: 150, baseAttack: 25, baseDefense: 12, exp: 60, gold: 45 },
      { name: '드래곤', baseHp: 300, baseAttack: 40, baseDefense: 20, exp: 120, gold: 100 }
    ];

    const randomMonster = monsters[Math.floor(Math.random() * monsters.length)];
    const levelMultiplier = 1 + (character.level - 1) * 0.3;

    const monster: Monster = {
      id: `monster_${Date.now()}`,
      name: randomMonster.name,
      level: Math.max(1, character.level + Math.floor(Math.random() * 3 - 1)),
      hp: Math.floor(randomMonster.baseHp * levelMultiplier),
      maxHp: Math.floor(randomMonster.baseHp * levelMultiplier),
      attack: Math.floor(randomMonster.baseAttack * levelMultiplier),
      defense: Math.floor(randomMonster.baseDefense * levelMultiplier),
      speed: 10,
      exp: Math.floor(randomMonster.exp * levelMultiplier),
      gold: Math.floor(randomMonster.gold * levelMultiplier),
      dropItems: generateDropItems(),
      isBoss: Math.random() < 0.1 // 10% 보스 확률
    };

    if (monster.isBoss) {
      monster.name = `보스 ${monster.name}`;
      monster.hp *= 3;
      monster.maxHp *= 3;
      monster.attack *= 2;
      monster.defense *= 2;
      monster.exp *= 5;
      monster.gold *= 3;
    }

    setCurrentMonster(monster);
    addToCombatLog(`${monster.name} Lv.${monster.level}이 나타났다!`);
  };

  // 드롭 아이템 생성
  const generateDropItems = (): Item[] => {
    const dropChance = Math.random();
    if (dropChance < 0.1) { // 10% 확률로 아이템 드롭
      const rarities: Array<'common' | 'rare' | 'epic' | 'legendary' | 'mythic'> = ['common', 'rare', 'epic', 'legendary', 'mythic'];
      const rarity = rarities[Math.floor(Math.random() * rarities.length)];

      return [{
        id: `drop_${Date.now()}`,
        name: generateItemName(rarity),
        type: ['weapon', 'armor', 'helmet', 'boots', 'ring', 'amulet'][Math.floor(Math.random() * 6)] as any,
        rarity,
        level: character.level,
        stats: generateItemStats(rarity),
        price: calculateItemPrice(rarity, character.level),
        enhancement: 0
      }];
    }
    return [];
  };

  const generateItemName = (rarity: string): string => {
    const prefixes = {
      common: ['낡은', '일반적인', '기본'],
      rare: ['날카로운', '튼튼한', '마법의'],
      epic: ['전설적인', '고대의', '신성한'],
      legendary: ['전설의', '영웅의', '불멸의'],
      mythic: ['신화의', '창조의', '절대의']
    };

    const items = ['검', '갑옷', '투구', '신발', '반지', '목걸이'];
    const prefix = prefixes[rarity as keyof typeof prefixes][Math.floor(Math.random() * 3)];
    const item = items[Math.floor(Math.random() * items.length)];

    return `${prefix} ${item}`;
  };

  const generateItemStats = (rarity: string) => {
    const multipliers = {
      common: 1,
      rare: 1.5,
      epic: 2.5,
      legendary: 4,
      mythic: 7
    };

    const base = multipliers[rarity as keyof typeof multipliers];
    return {
      attack: Math.floor(Math.random() * 20 * base),
      defense: Math.floor(Math.random() * 15 * base),
      hp: Math.floor(Math.random() * 50 * base),
      mp: Math.floor(Math.random() * 30 * base),
      speed: Math.floor(Math.random() * 10 * base),
      critRate: Math.floor(Math.random() * 10 * base),
      critDamage: Math.floor(Math.random() * 20 * base)
    };
  };

  const calculateItemPrice = (rarity: string, level: number): number => {
    const basePrice = {
      common: 50,
      rare: 150,
      epic: 500,
      legendary: 2000,
      mythic: 10000
    };

    return Math.floor(basePrice[rarity as keyof typeof basePrice] * (1 + level * 0.2));
  };

  // 전투 시스템
  const attack = () => {
    if (!currentMonster) return;

    const buffedAttack = character.attack + character.buffs.attack.amount;
    const damage = calculateDamage(buffedAttack, currentMonster.defense);
    const isCritical = Math.random() < 0.1 + (character.stats.luck * 0.01);
    const finalDamage = isCritical ? Math.floor(damage * 1.5) : damage;

    const newMonsterHp = Math.max(0, currentMonster.hp - finalDamage);
    setCurrentMonster(prev => prev ? { ...prev, hp: newMonsterHp } : null);

    addToCombatLog(`${character.name}의 공격! ${finalDamage}${isCritical ? ' (치명타!)' : ''} 데미지!`);

    onAction('combat_attack', {
      user_id: currentUser.user_id,
      damage: finalDamage,
      is_critical: isCritical,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level
    });

    if (newMonsterHp <= 0) {
      defeatMonster();
    } else {
      // 몬스터 반격
      setTimeout(() => monsterAttack(), 1000);
    }
  };

  const monsterAttack = () => {
    if (!currentMonster || currentMonster.hp <= 0) return;

    const buffedDefense = character.defense + character.buffs.defense.amount;
    const damage = calculateDamage(currentMonster.attack, buffedDefense);
    const newHp = Math.max(0, character.hp - damage);

    setCharacter(prev => ({ ...prev, hp: newHp }));
    addToCombatLog(`${currentMonster.name}의 공격! ${damage} 데미지!`);

    if (newHp <= 0) {
      addToCombatLog('전투에서 패배했습니다...');
      setIsAutoFighting(false);

      // 전투 실패 이벤트 로깅
      onAction('combat_defeat', {
        monster_name: currentMonster.name,
        monster_level: currentMonster.level,
        character_level: character.level,
        dungeon_id: currentDungeon?.id || 'field'
      });

      // 던전에서 나가고 체력/마나 완전 회복
      setTimeout(() => {
        setCharacter(prev => ({
          ...prev,
          hp: prev.maxHp,
          mp: prev.maxMp
        }));

        if (inDungeon && currentDungeon) {
          // 던전에서 나가기
          setInDungeon(false);
          setCurrentDungeon(null);
          setCurrentTab('lobby');
          setGameMode('lobby');
          addToCombatLog('던전에서 나왔습니다. 체력과 마나가 완전히 회복되었습니다!');
        } else {
          // 일반 필드에서는 새 몬스터 스폰
          addToCombatLog('체력과 마나가 완전히 회복되었습니다!');
          spawnMonster();
        }
      }, 2000);
    }
  };

  const calculateDamage = (attack: number, defense: number): number => {
    const baseDamage = Math.max(1, attack - Math.floor(defense * 0.5));
    return Math.floor(baseDamage * (0.8 + Math.random() * 0.4));
  };

  const defeatMonster = () => {
    if (!currentMonster) return;

    const expGained = currentMonster.exp;
    const goldGained = currentMonster.gold;

    setCharacter(prev => {
      const newExp = prev.exp + expGained;
      const newGold = prev.gold + goldGained;
      let newLevel = prev.level;
      let newMaxExp = prev.maxExp;
      let newHp = prev.hp;
      let newMaxHp = prev.maxHp;
      let availablePoints = prev.stats.availablePoints;

      // 레벨업 체크
      if (newExp >= prev.maxExp) {
        newLevel += 1;
        newMaxExp = Math.floor(prev.maxExp * 1.5);
        newMaxHp += 20;
        newHp = newMaxHp; // 레벨업 시 체력 완전 회복
        availablePoints += 5;

        addToCombatLog(`🎉 레벨업! Lv.${newLevel}이 되었습니다!`);

        onAction('level_up', {
          user_id: currentUser.user_id,
          new_level: newLevel,
          character_class: prev.class,
          stat_points_gained: 5
        });

        // 던전 해금 체크
        setAvailableDungeons(prevDungeons => 
          prevDungeons.map(dungeon => ({
            ...dungeon,
            isUnlocked: dungeon.isUnlocked || newLevel >= dungeon.requiredLevel
          }))
        );
      }

      return {
        ...prev,
        exp: newExp >= prev.maxExp ? newExp - prev.maxExp : newExp,
        gold: newGold,
        level: newLevel,
        maxExp: newMaxExp,
        hp: newHp,
        maxHp: newMaxHp,
        stats: { ...prev.stats, availablePoints }
      };
    });

    // 아이템 드롭 처리
    if (currentMonster.dropItems.length > 0) {
      setInventory(prev => [...prev, ...currentMonster.dropItems]);
      addToCombatLog(`✨ ${currentMonster.dropItems[0].name}을 획득했습니다!`);
    }

    addToCombatLog(`${currentMonster.name}를 물리쳤다! EXP +${expGained}, 골드 +${goldGained}`);

    // 추적 카운터 업데이트
    setMonstersDefeated(prev => prev + 1);
    setTotalGoldEarned(prev => prev + goldGained);

    onAction('monster_defeated', {
      user_id: currentUser.user_id,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level,
      exp_gained: expGained,
      gold_gained: goldGained,
      items_dropped: currentMonster.dropItems.length
    });

    // 던전에있는 경우 다음 층 진행, 아니면 새 몬스터 생성
    setTimeout(() => {
      if (inDungeon && currentDungeon) {
        // 던전 내부에서의 다음 진행
        if (currentDungeon.currentFloor < currentDungeon.floors) {
          const updatedDungeon = { ...currentDungeon, currentFloor: currentDungeon.currentFloor + 1 };
          setCurrentDungeon(updatedDungeon);
          spawnDungeonMonster(updatedDungeon);
          addToCombatLog(`${updatedDungeon.currentFloor}층으로 진행합니다!`);
        } else {
          // 던전 클리어
          addToCombatLog(`🎉 ${currentDungeon.name} 던전을 클리어했습니다!`);
          onAction('dungeon_cleared', {
            dungeon_id: currentDungeon.id,
            dungeon_name: currentDungeon.name,
            floors_cleared: currentDungeon.floors,
            character_level: character.level
          });
          setInDungeon(false);
          setCurrentDungeon(null);
          setCurrentTab('lobby');
        }
      } else {
        // 일반 방치형 전투에서 새 몬스터 생성
        spawnMonster();
      }
    }, 2000);
  };


  // 자동 전투
  useEffect(() => {
    if (isAutoFighting && currentMonster && currentMonster.hp > 0 && character.hp > 0) {
      const autoAttackTimer = setTimeout(() => {
        attack();
      }, 2000);

      return () => clearTimeout(autoAttackTimer);
    }
  }, [isAutoFighting, currentMonster, character.hp]);

  // 스킬 쿨다운 및 MP 회복
  useEffect(() => {
    const interval = setInterval(() => {
      setCharacter(prev => ({
        ...prev,
        mp: Math.min(prev.maxMp, prev.mp + 1),
        skills: prev.skills.map(skill => ({
          ...skill,
          currentCooldown: Math.max(0, skill.currentCooldown - 100)
        })),
        buffs: {
          attack: {
            amount: prev.buffs.attack.duration > 0 ? prev.buffs.attack.amount : 0,
            duration: Math.max(0, prev.buffs.attack.duration - 100)
          },
          defense: {
            amount: prev.buffs.defense.duration > 0 ? prev.buffs.defense.amount : 0,
            duration: Math.max(0, prev.buffs.defense.duration - 100)
          },
          speed: {
            amount: prev.buffs.speed.duration > 0 ? prev.buffs.speed.amount : 0,
            duration: Math.max(0, prev.buffs.speed.duration - 100)
          }
        }
      }));
    }, 100);

    return () => clearInterval(interval);
  }, []);

  const addToCombatLog = (message: string) => {
    setCombatLog(prev => {
      const newLog = [...prev, message];
      if (newLog.length > 50) newLog.shift();
      return newLog;
    });

    setTimeout(() => {
      if (logRef.current) {
        logRef.current.scrollTop = logRef.current.scrollHeight;
      }
    }, 0);
  };

  // 스탯 분배
  const allocateStatPoint = (stat: keyof Omit<CharacterStats, 'availablePoints'>) => {
    if (character.stats.availablePoints <= 0) return;

    setCharacter(prev => ({
      ...prev,
      stats: {
        ...prev.stats,
        [stat]: prev.stats[stat] + 1,
        availablePoints: prev.stats.availablePoints - 1
      },
      // 스탯에 따른 능력치 업데이트
      attack: stat === 'strength' ? prev.attack + 2 : prev.attack,
      defense: stat === 'vitality' ? prev.defense + 1 : prev.defense,
      maxHp: stat === 'vitality' ? prev.maxHp + 10 : prev.maxHp,
      maxMp: stat === 'intelligence' ? prev.maxMp + 5 : prev.maxMp,
      speed: stat === 'agility' ? prev.speed + 1 : prev.speed
    }));
  };

  // 아이템 장착
  const equipItem = (item: Item) => {
    // 소비 아이템은 장착할 수 없음
    if (item.type === 'consumable') {
      return;
    }

    setCharacter(prev => {
      const newEquipment = { ...prev.equipment };
      newEquipment[item.type as keyof typeof newEquipment] = item;

      // 장착한 아이템의 스탯 적용
      let newAttack = prev.attack;
      let newDefense = prev.defense;
      let newMaxHp = prev.maxHp;
      let newMaxMp = prev.maxMp;
      let newSpeed = prev.speed;

      if (item.stats.attack) newAttack += item.stats.attack;
      if (item.stats.defense) newDefense += item.stats.defense;
      if (item.stats.hp) newMaxHp += item.stats.hp;
      if (item.stats.mp) newMaxMp += item.stats.mp;
      if (item.stats.speed) newSpeed += item.stats.speed;

      return {
        ...prev,
        equipment: newEquipment,
        attack: newAttack,
        defense: newDefense,
        maxHp: newMaxHp,
        maxMp: newMaxMp,
        speed: newSpeed
      };
    });

    // 인벤토리에서 아이템 제거
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}을 장착했습니다!`);
  };

  // 아이템 판매
  const sellItem = (item: Item) => {
    const sellPrice = Math.floor(item.price * 0.7);
    setCharacter(prev => ({ ...prev, gold: prev.gold + sellPrice }));
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}을 ${sellPrice} 골드에 판매했습니다!`);
  };

  // 아이템 구매
  const buyItem = (item: Item) => {
    if (character.gold < item.price) return;

    setCharacter(prev => ({ ...prev, gold: prev.gold - item.price }));
    setInventory(prev => [...prev, { ...item, id: `${item.id}_${Date.now()}` }]);
    addToCombatLog(`${item.name}을 구매했습니다!`);
  };

  const getRarityColor = (rarity: string) => {
    const colors = {
      common: '#9ca3af',
      rare: '#3b82f6',
      epic: '#8b5cf6',
      legendary: '#f59e0b',
      mythic: '#ef4444'
    };
    return colors[rarity as keyof typeof colors] || '#9ca3af';
  };

  // 던전 입장 함수
  const enterDungeon = (dungeon: Dungeon) => {
    if (character.level < dungeon.requiredLevel) {
      addToCombatLog(`레벨 ${dungeon.requiredLevel} 이상이어야 입장 가능합니다.`);
      return;
    }
    setCurrentDungeon(dungeon);
    setInDungeon(true);
    setGameMode('dungeon');
    setCurrentTab('dungeon');
    spawnDungeonMonster(dungeon);

    // 이벤트 로깅
    onAction('dungeon_entered', {
      dungeon_id: dungeon.id,
      dungeon_name: dungeon.name,
      character_level: character.level,
      floor: dungeon.currentFloor
    });
  };

  // 던전 몬스터 스폰
  const spawnDungeonMonster = (dungeon: Dungeon) => {
    const baseMonsters = [
      { name: '슬라임', baseHp: 30, baseAttack: 8, baseDefense: 2 },
      { name: '고블린', baseHp: 50, baseAttack: 12, baseDefense: 4 },
      { name: '오크', baseHp: 100, baseAttack: 18, baseDefense: 8 },
      { name: '트롤', baseHp: 150, baseAttack: 25, baseDefense: 12 },
      { name: '드래곤', baseHp: 300, baseAttack: 40, baseDefense: 20 }
    ];

    const selectedMonster = baseMonsters[Math.min(Math.floor(dungeon.level / 2), baseMonsters.length - 1)];
    const levelMultiplier = 1 + (dungeon.level + dungeon.currentFloor - 1) * 0.2;

    const monster: Monster = {
      id: `dungeon_${dungeon.id}_${Date.now()}`,
      name: `${dungeon.name} ${selectedMonster.name}`,
      level: dungeon.level + dungeon.currentFloor,
      hp: Math.floor(selectedMonster.baseHp * levelMultiplier),
      maxHp: Math.floor(selectedMonster.baseHp * levelMultiplier),
      attack: Math.floor(selectedMonster.baseAttack * levelMultiplier),
      defense: Math.floor(selectedMonster.baseDefense * levelMultiplier),
      speed: 10,
      exp: Math.floor((15 + dungeon.level * 5) * levelMultiplier),
      gold: Math.floor((10 + dungeon.level * 3) * levelMultiplier),
      dropItems: generateDropItems(),
      isBoss: dungeon.currentFloor % 5 === 0
    };

    if (monster.isBoss) {
      monster.name = `보스 ${monster.name}`;
      monster.hp *= 2;
      monster.maxHp *= 2;
      monster.attack *= 1.5;
      monster.exp *= 3;
      monster.gold *= 2;
    }

    setCurrentMonster(monster);
    addToCombatLog(`${monster.name} Lv.${monster.level}이 나타났다!`);
  };

  // 물약 사용 함수
  const consumeItem = (item: Item) => {
    if (item.type !== 'consumable' || !item.consumableEffect) return;

    const effect = item.consumableEffect;

    // 체력 회복
    if (effect.healHp) {
      setCharacter(prev => ({
        ...prev,
        hp: Math.min(prev.maxHp, prev.hp + effect.healHp!)
      }));
      addToCombatLog(`${item.name}을 사용하여 HP ${effect.healHp} 회복!`);
    }

    // 마나 회복
    if (effect.healMp) {
      setCharacter(prev => ({
        ...prev,
        mp: Math.min(prev.maxMp, prev.mp + effect.healMp!)
      }));
      addToCombatLog(`${item.name}을 사용하여 MP ${effect.healMp} 회복!`);
    }

    // 버프 효과
    if (effect.buffType && effect.buffAmount && effect.buffDuration) {
      setCharacter(prev => ({
        ...prev,
        buffs: {
          ...prev.buffs,
          [effect.buffType!]: {
            amount: effect.buffAmount!,
            duration: effect.buffDuration!
          }
        }
      }));
      addToCombatLog(`${item.name}을 사용하여 ${effect.buffType === 'attack' ? '공격력' : effect.buffType === 'defense' ? '방어력' : '속도'} ${effect.buffAmount} 증가! (${effect.buffDuration/1000}초)`);
    }

    // 인벤토리에서 물약 제거 또는 수량 감소
    setInventory(prev => {
      const itemIndex = prev.findIndex(invItem => invItem.id === item.id);
      if (itemIndex !== -1) {
        const newInventory = [...prev];
        const currentItem = newInventory[itemIndex];
        if (currentItem.quantity && currentItem.quantity > 1) {
          newInventory[itemIndex] = { ...currentItem, quantity: currentItem.quantity - 1 };
        } else {
          newInventory.splice(itemIndex, 1);
        }
        return newInventory;
      }
      return prev;
    });

    // 이벤트 로깅
    onAction('consumable_used', {
      item_name: item.name,
      item_type: item.type,
      character_level: character.level,
      heal_hp: effect.healHp || 0,
      heal_mp: effect.healMp || 0,
      buff_type: effect.buffType || 'none',
      buff_amount: effect.buffAmount || 0
    });
  };

  // 스킬 사용 함수
  const castSkill = (skill: Skill) => {
    if (skill.currentCooldown > 0 || character.mp < skill.manaCost) return;

    // 스킬 사용 로직
    if (skill.id === 'heal') {
      const healAmount = 30 + skill.level * 5;
      setCharacter(prev => ({
        ...prev,
        hp: Math.min(prev.maxHp, prev.hp + healAmount),
        mp: prev.mp - skill.manaCost
      }));
      addToCombatLog(`${healAmount}만큼 회복했다!`);
    } else {
      // 공격 스킬
      if (currentMonster) {
        const damage = calculateDamage(skill.damage + skill.level * 3, currentMonster.defense);
        const newMonsterHp = Math.max(0, currentMonster.hp - damage);
        setCurrentMonster(prev => prev ? {...prev, hp: newMonsterHp} : null);
        setCharacter(prev => ({ ...prev, mp: prev.mp - skill.manaCost }));
        addToCombatLog(`${skill.name}로 ${damage} 데미지!`);

        // 몬스터가 죽지 않았으면 반격
        if (newMonsterHp > 0) {
          setTimeout(() => {
            monsterAttack();
          }, 1000);
        } else {
          // 몬스터가 죽었으면 처치 처리
          setTimeout(() => {
            defeatMonster();
          }, 1500);
        }
      }
    }

    // 쿨다운 적용
    setCharacter(prev => ({
      ...prev,
      skills: prev.skills.map(s => 
        s.id === skill.id ? {...s, currentCooldown: s.cooldown} : s
      )
    }));

    // 이벤트 로깅
    onAction('skill_used', {
      skill_name: skill.name,
      skill_level: skill.level,
      mana_cost: skill.manaCost,
      character_level: character.level
    });
  };

  // 스탯 포인트 할당
  const allocateStat = (statName: keyof CharacterStats) => {
    if (character.stats.availablePoints <= 0) return;

    setCharacter(prev => ({
      ...prev,
      stats: {
        ...prev.stats,
        [statName]: prev.stats[statName] + 1,
        availablePoints: prev.stats.availablePoints - 1
      }
    }));

    // 스탯에 따른 능력치 업데이트
    let hpBonus = 0, mpBonus = 0, attackBonus = 0, defenseBonus = 0;
    if (statName === 'vitality') hpBonus = 5;
    if (statName === 'intelligence') mpBonus = 3;
    if (statName === 'strength') attackBonus = 2;
    if (statName === 'agility') { attackBonus = 1; defenseBonus = 1; }

    setCharacter(prev => ({
      ...prev,
      maxHp: prev.maxHp + hpBonus,
      hp: prev.hp + hpBonus,
      maxMp: prev.maxMp + mpBonus,
      mp: prev.mp + mpBonus,
      attack: prev.attack + attackBonus,
      defense: prev.defense + defenseBonus
    }));

    // 이벤트 로깅
    onAction('stat_allocated', {
      stat_name: statName,
      stat_value: character.stats[statName] + 1,
      character_level: character.level,
      available_points: character.stats.availablePoints - 1
    });
  };

  // 공통 캐릭터 정보 헤더 컴포넌트
  const renderCharacterHeader = () => (
    <div style={{
      background: 'linear-gradient(135deg, #4f46e5 0%, #7c3aed 100%)',
      borderRadius: '15px',
      padding: '15px 20px',
      marginBottom: '20px',
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
      gap: '15px',
      color: 'white'
    }}>
      <div style={{ textAlign: 'center' }}>
        <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#ffffff' }}>
          Lv.{character.level}
        </div>
        <div style={{ color: '#e0e7ff', fontSize: '12px' }}>레벨</div>
      </div>
      <div style={{ textAlign: 'center' }}>
        <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#fbbf24' }}>
          {character.gold}G
        </div>
        <div style={{ color: '#e0e7ff', fontSize: '12px' }}>골드</div>
      </div>
      <div style={{ textAlign: 'center' }}>
        <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#f87171' }}>
          {character.attack}
        </div>
        <div style={{ color: '#e0e7ff', fontSize: '12px' }}>공격력</div>
      </div>
      <div style={{ textAlign: 'center' }}>
        <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#60a5fa' }}>
          {character.defense}
        </div>
        <div style={{ color: '#e0e7ff', fontSize: '12px' }}>방어력</div>
      </div>
      <div style={{ textAlign: 'center' }}>
        <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#34d399' }}>
          {character.hp}/{character.maxHp}
        </div>
        <div style={{ color: '#e0e7ff', fontSize: '12px' }}>체력</div>
      </div>
      <div style={{ textAlign: 'center' }}>
        <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#a78bfa' }}>
          {character.mp}/{character.maxMp}
        </div>
        <div style={{ color: '#e0e7ff', fontSize: '12px' }}>마나</div>
      </div>
    </div>
  );

  // 로비 렌더링
  const renderLobbyTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      {/* 공통 캐릭터 헤더 */}
      {renderCharacterHeader()}

      <div style={{
