      {/* 공통 캐릭터 헤더 */}
      {renderCharacterHeader()}

      <div style={{
        background: 'linear-gradient(135deg, #7c3aed 0%, #a855f7 100%)',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '20px',
        color: 'white',
