                        if (character.gold >= cost) {
                          setCharacter(prev => ({
                            ...prev,
                            gold: prev.gold - cost,
                            skills: prev.skills.map(s => 
                              s.id === skill.id ? {...s, level: s.level + 1} : s
                            )
                          }));

                          // 이벤트 로깅
                          onAction('skill_upgraded', {
                            skill_name: skill.name,
                            new_level: skill.level + 1,
                            gold_cost: cost,
                            character_level: character.level
                          });

                          addToCombatLog(`${skill.name}을(를) 레벨업했다! (Lv.${skill.level + 1})`);
